        function barycentricToCartesian(xA, xB, xC) {
            return createVector(xA * PA.x + xB * PB.x + xC * PC.x, xA * PA.y + xB * PB.y + xC * PC.y);
        }

        function cartesianToBarycentric(px, py) {
            let denom = ((PB.y - PC.y) * (PA.x - PC.x) + (PC.x - PB.x) * (PA.y - PC.y));
            let xA = ((PB.y - PC.y) * (px - PC.x) + (PC.x - PB.x) * (py - PC.y)) / denom;
            let xB = ((PC.y - PA.y) * (px - PC.x) + (PA.x - PC.x) * (py - PC.y)) / denom;
            let xC = 1 - xA - xB;
            return { xA, xB, xC };
        }

        function isInsideTriangle(point) {
            return point.xA >= 0 && point.xB >= 0 && point.xC >= 0;
        }

        function isInsidePolygon(polygon, point) {
            let inside = false;
            for (let i = 0, j = polygon.length - 1; i < polygon.length; j = i++) {
                let xi = polygon[i].xA * PA.x + polygon[i].xB * PB.x + polygon[i].xC * PC.x;
                let yi = polygon[i].xA * PA.y + polygon[i].xB * PB.y + polygon[i].xC * PC.y;
                let xj = polygon[j].xA * PA.x + polygon[j].xB * PB.x + polygon[j].xC * PC.x;
                let yj = polygon[j].xA * PA.y + polygon[j].xB * PB.y + polygon[j].xC * PC.y;
                let intersect = ((yi > py) != (yj > py)) && (px < (xj - xi) * (py - yi) / (yj - yi) + xi);
                if (intersect) inside = !inside;
            }
            return inside;
        }

        function findNearestTieLine(point) {
            let minDist = Infinity;
            let nearestTieLine = null;
            for (let tieLine of tieLines) {
