                let betaPos = barycentricToCartesian(tieLine.beta.xA, tieLine.beta.xB, tieLine.beta.xC);
                let dist = pointToSegmentDistance(barycentricToCartesian(point.xA, point.xB, point.xC), alphaPos, betaPos);
                if (dist < minDist) {
                    minDist = dist;
                    nearestTieLine = tieLine;
                }
            }
            return nearestTieLine;
        }

        function pointToSegmentDistance(p, v, w) {
            let l2 = v.dist(w) ** 2;
            if (l2 == 0) return p.dist(v);
            let t = ((p.x - v.x) * (w.x - v.x) + (p.y - v.y) * (w.y - v.y)) / l2;
            t = max(0, min(1, t));
            let projection = createVector(v.x + t * (w.x - v.x), v.y + t * (w.y - v.y));
            return p.dist(projection);
        }

