    <script>
        const seedInput = document.getElementById('seed-input');
        const btnRandomSet = document.getElementById('btn-random-set');
        const btnResetSeed = document.getElementById('btn-reset-seed');
        const sliderPoints = document.getElementById('slider-points');
        const radioButtons = document.querySelectorAll('input[name="math-method"]');
        const leftPlotTitle = document.getElementById('left-plot-title');
        const timingCells = {
            'OrZweig': document.getElementById('timing-orzweig'),
            'OrOpt': document.getElementById('timing-oropt'),
            'TwoOpt': document.getElementById('timing-twoopt'),
            'CCA': document.getElementById('timing-cca'),
            '3-Opt': document.getElementById('timing-3opt')
        };
        const resultCells = {
            'OrZweig': document.getElementById('result-orzweig'),
            'OrOpt': document.getElementById('result-oropt'),
            'TwoOpt': document.getElementById('result-twoopt'),
            'CCA': document.getElementById('result-cca'),
