        };
        const winnerDisplay = document.getElementById('winner-display');

        let points = [];
        let tours = {};
        let timings = {};
        let results = {};

        function seededRandom(seed) {
            let m = 2**35 - 31;
            let a = 185852;
            let s = seed % m;
            return function() {
                return (s = s * a % m) / m;
            };
        }

        function nearestNeighbor(points) {
            let unvisited = [...points];
            let tour = [unvisited.shift()];
            while (unvisited.length > 0) {
                let nextPoint = unvisited.reduce((closest, point) => {
                    let d1 = dist(tour[tour.length - 1], point);
                    let d2 = dist(closest, point);
                    return d1 < d2 ? point : closest;
                });
                tour.push(nextPoint);
                unvisited = unvisited.filter(point => point !== nextPoint);
            }
            return tour;
        }

        function dist(p1, p2) {
            return Math.sqrt(Math.pow(p1.x - p2.x, 2) + Math.pow(p1.y - p2.y, 2));
        }

        function tourLength(tour) {
            let length = 0;
            for (let i = 0; i < tour.length; i++) {
                length += dist(tour[i], tour[(i + 1) % tour.length]);
            }
            return length;
