        function solveTwoOpt(points, initialTour) {
            let tour = [...initialTour];
            let improved = true;
            while (improved) {
                improved = false;
                for (let i = 1; i < tour.length - 1; i++) {
                    for (let j = i + 1; j < tour.length; j++) {
                        let before = dist(tour[i - 1], tour[i]) + dist(tour[j], tour[(j + 1) % tour.length]);
                        let after = dist(tour[i - 1], tour[j]) + dist(tour[i], tour[(j + 1) % tour.length]);
                        if (after < before) {
                            tour = tour.slice(0, i).concat(tour.slice(i, j + 1).reverse()).concat(tour.slice(j + 1));
                            improved = true;
                        }
                    }
                }
            }
            return tour;
        }

        function solve3Opt(points, initialTour) {
            let tour = [...initialTour];
            let improved = true;
            while (improved) {
                improved = false;
                for (let i = 0; i < tour.length - 2; i++) {
                    for (let j = i + 2; j < tour.length - 1; j++) {
                        for (let k = j + 1; k < tour.length; k++) {
                            let a = tour[i];
                            let b = tour[i + 1];
                            let c = tour[j];
                            let d = tour[j + 1];
                            let e = tour[k];
                            let f = tour[(k + 1) % tour.length];

                            let originalDist = dist(a, b) + dist(c, d) + dist(e, f);
                            let newDist1 = dist(a, c) + dist(b, d) + dist(e, f);
                            let newDist2 = dist(a, b) + dist(c, e) + dist(d, f);
                            let newDist3 = dist(a, d) + dist(e, b) + dist(c, f);
                            let newDist4 = dist(a, c) + dist(b, e) + dist(d, f);

                            let newDist = Math.min(newDist1, newDist2, newDist3, newDist4);
                            if (newDist < originalDist) {
                                if (newDist === newDist1) {
