                for (let i = 1; i < tour.length - 2; i++) {
                    for (let k = i + 2; k < tour.length; k++) {
                        for (let len = 1; len <= 3 && i + len <= k; len++) {
                            let segmentStart = tour[i];
                            let segmentEnd = tour[i + len - 1];
                            let insertAfter = tour[k];

                            let originalDist = dist(segmentStart, segmentEnd) + dist(insertAfter, tour[(k + 1) % tour.length]);
                            let newDist = dist(segmentStart, tour[(k + 1) % tour.length]) + dist(insertAfter, segmentEnd);

