                        tour = solveOrZweig(points, initialTour);
                        break;
                    case 'OrOpt':
                        tour = solveOrOpt(points, initialTour);
                        break;
                    case 'TwoOpt':
                        tour = solveTwoOpt(points, initialTour);
                        break;
                    case 'CCA':
                        tour = solveCCA(points);
                        break;
                    case '3-Opt':
                        tour = solve3Opt(points, initialTour);
                        break;
                }
                let endTime = performance.now();
                let duration = (endTime - startTime) / 1000;

                tours[method] = tour;
                timings[method] = duration.toFixed(6);
                results[method] = tourLength(tour).toFixed(6);
            });

            methods.forEach(method => {
                timingCells[method].textContent = timings[method];
                resultCells[method].textContent = results[method];
            });

            let mathMethods = methods.slice(0, 4);
            let minMathResult = Math.min(...mathMethods.map(method => parseFloat(results[method])));
            let result3Opt = parseFloat(results['3-Opt']);

            if (result3Opt < minMathResult) {
                winnerDisplay.textContent = '3-Opt wins';
            } else if (minMathResult < result3Opt) {
                winnerDisplay.textContent = 'Mathematica wins';
            } else {
                winnerDisplay.textContent = 'Tie';
            }

            leftPlotTitle.textContent = document.querySelector('input[name="math-method"]:checked').value;
            renderPlots();
        }

        function renderPlots() {
            let selectedMethod = document.querySelector('input[name="math-method"]:checked').value;
            let leftTour = tours[selectedMethod];
            let rightTour = tours['3-Opt'];

            new p5(sketch(leftTour, '#canvas-left-wrapper', '#0072B2'));
            new p5(sketch(rightTour, '#canvas-right-wrapper', '#E69F00'));
        }

        function sketch(tour, containerId, strokeColor) {
            return function(p) {
                let margin = 50;
                let width = 400;
                let height = 400;

                p.setup = function() {
                    p.createCanvas(width, height);
                    p.noLoop();
                };

                p.draw = function() {
                    p.background(255);
                    p.stroke(strokeColor);
                    p.strokeWeight(2);

                    // Draw axes
                    p.stroke(0);
                    p.line(margin, margin, margin, height - margin);
                    p.line(margin, height - margin, width - margin, height - margin);

                    // Draw axis labels
                    p.textAlign(p.CENTER, p.BOTTOM);
                    p.text('0.0', margin, height - margin);
                    p.text('0.2', margin, height - margin - 0.2 * (height - 2 * margin));
                    p.text('0.4', margin, height - margin - 0.4 * (height - 2 * margin));
                    p.text('0.6', margin, height - margin - 0.6 * (height - 2 * margin));
                    p.text('0.8', margin, height - margin - 0.8 * (height - 2 * margin));
                    p.text('1.0', margin, margin);

                    p.textAlign(p.LEFT, p.CENTER);
                    p.text('0.0', margin, height - margin);
                    p.text('0.2', margin + 0.2 * (width - 2 * margin), height - margin);
                    p.text('0.4', margin + 0.4 * (width - 2 * margin), height - margin);
                    p.text('0.6', margin + 0.6 * (width - 2 * margin), height - margin);
                    p.text('0.8', margin + 0.8 * (width - 2 * margin), height - margin);
                    p.text('1.0', width - margin, height - margin);

                    // Draw points
                    p.fill('red');
                    p.noStroke();
                    points.forEach(point => {
                        p.ellipse(margin + point.x * (width - 2 * margin), height - margin - point.y * (height - 2 * margin), 5, 5);
                    });

                    // Draw tour path
                    p.stroke(strokeColor);
                    p.beginShape();
                    tour.forEach(point => {
                        p.vertex(margin + point.x * (width - 2 * margin), height - margin - point.y * (height - 2 * margin));
                    });
                    p.endShape(p.CLOSE);
                };
            };
        }

        btnRandomSet.addEventListener('click', () => {
            seedInput.value = Math.floor(Math.random() * 99999) + 1;
            calculateAndRender();
        });

        btnResetSeed.addEventListener('click', () => {
            seedInput.value = 0;
            calculateAndRender();
        });

        sliderPoints.addEventListener('change', calculateAndRender);

        radioButtons.forEach(radio => {
            radio.addEventListener('change', () => {
                leftPlotTitle.textContent = radio.value;
                renderPlots();
            });
        });

        calculateAndRender();
    </script>
</body>
</html>
