    <script>
        // Function to reverse the lowest 15 bits of an integer
        function bitReverse15(n) {
            let result = 0;
            for (let i = 0; i < 15; i++) {
                result = (result << 1) | (n & 1);
                n >>= 1;
            }
            return result;
        }

        // Automaton function f(n)
        function f(n) {
            return (n + bitReverse15(Math.floor(n / 2))) % 65536;
        }

        // Global variables
        let n0 = 462;
        let maxSteps = 9;
        let checkboxes = {
            cycle4: false,
            cycle2: false,
            cycle1: false,
            allPoints: false,
            modify: false
        };

        // Cycle detection
        function detectCycle(n0, maxSteps) {
            let sequence = [];
            let seen = new Map();
            let n = n0;

            for (let i = 0; i <= maxSteps; i++) {
                if (seen.has(n)) {
                    return { cycleStart: seen.get(n), cycleLength: i - seen.get(n) };
                }
                seen.set(n, i);
                sequence.push(n);
                n = f(n);
            }

            return null;
        }

        // p5.js setup
        function setup() {
            createCanvas(512, 512);
            background('#e0f0e0');
            noLoop();
            updateSimulation();
