            // All Map Points
            if (checkboxes.allPoints) {
                for (let n = 0; n < 65536; n++) {
                    let nNext = f(n);
                    let x = nNext & 255;
                    let y = (nNext >> 8) & 255;
                    stroke('#808080');
                    point(x * 2, y * 2);
                }
            }

            // Automaton Path
            let sequence = [];
            let n = n0;
            for (let i = 0; i <= maxSteps; i++) {
                sequence.push(n);
                n = f(n);
            }

            beginShape();
            stroke('#800080');
            strokeWeight(2);
            for (let n of sequence) {
                let x = n & 255;
                let y = (n >> 8) & 255;
                vertex(x * 2, y * 2);
            }
            endShape();

            // Start and End Points
            fill('#FF0000');
            ellipse(sequence[0] & 255 * 2, (sequence[0] >> 8) & 255 * 2, 5, 5);
            fill('#0000FF');
            ellipse(sequence[sequence.length - 1] & 255 * 2, (sequence[sequence.length - 1] >> 8) & 255 * 2, 5, 5);

            // Length 1 Cycle Points
            if (checkboxes.cycle1) {
                for (let n = 0; n < 65536; n++) {
                    if (f(n) === n) {
                        let x = n & 255;
                        let y = (n >> 8) & 255;
                        fill('#00FFFF');
                        noStroke();
                        ellipse(x * 2, y * 2, 3, 3);
                    }
                }
            }

            // Length 2 Cycles
            if (checkboxes.cycle2) {
                const cyclePairs = [
                    [10922, 21845], [13107, 26214], [13653, 27306], [15339, 30678],
                    [21844, 43691], [26213, 52427], [27305, 54611], [30677, 61355], [43690, 54613]
                ];
                stroke('#008000');
                for (let [n1, n2] of cyclePairs) {
                    let x1 = n1 & 255;
                    let y1 = (n1 >> 8) & 255;
                    let x2 = n2 & 255;
                    let y2 = (n2 >> 8) & 255;
                    line(x1 * 2, y1 * 2, x2 * 2, y2 * 2);
                }
            }

            // Length 4 Cycle
            if (checkboxes.cycle4) {
                const cyclePoints = [43690, 54612, 32768, 49152];
                stroke('#008000');
                noFill();
                beginShape();
                for (let n of cyclePoints) {
                    let x = n & 255;
                    let y = (n >> 8) & 255;
                    vertex(x * 2, y * 2);
                }
                endShape(CLOSE);

                // Bounding Box
                let minX = Math.min(...cyclePoints.map(n => (n & 255) * 2));
                let maxX = Math.max(...cyclePoints.map(n => (n & 255) * 2));
                let minY = Math.min(...cyclePoints.map(n => ((n >> 8) & 255) * 2));
                let maxY = Math.max(...cyclePoints.map(n => ((n >> 8) & 255) * 2));
                stroke('#FFA500');
                noFill();
                rect(minX, minY, maxX - minX, maxY - minY);

                // Perpendicular Bisectors
                for (let i = 0; i < 4; i++) {
                    let n1 = cyclePoints[i];
                    let n2 = cyclePoints[(i + 1) % 4];
                    let x1 = n1 & 255;
                    let y1 = (n1 >> 8) & 255;
                    let x2 = n2 & 255;
                    let y2 = (n2 >> 8) & 255;
                    let midX = (x1 + x2) / 2;
                    let midY = (y1 + y2) / 2;
                    let dx = x2 - x1;
                    let dy = y2 - y1;
                    let perpDX = -dy;
                    let perpDY = dx;
                    let length = 10;
                    stroke('#008000');
                    line(midX * 2, midY * 2, (midX + perpDX / 100 * length) * 2, (midY + perpDY / 100 * length) * 2);
                }
            }
