            let params = simulationParams[stepType][plotType];
            if (plotType === 'composition') {
                params = params[component];
            }

            const yInitial = params.initial;
            const Kp = params.Kp;
            const tau = params.tau;

            const time = Array.from({ length: 201 }, (_, i) => i * 0.1);
            const y = time.map(t => {
                if (t < 10) {
                    return yInitial;
                } else {
                    return yInitial + Kp * stepPercent * (1 - Math.exp(-(t - 10) / tau));
                }
            });

            const trace = {
                x: time,
                y: y,
                mode: 'lines',
                line: {
                    color: plotType === 'composition' ? (component === 'benzene' ? '#FF0000' : component === 'toluene' ? '#0000FF' : '#008B8B') : '#000000'
