                    btn.classList.add('active');
                    document.getElementById('component-selector-container').style.display = btn.id === 'btn-composition' ? 'block' : 'none';
                    updatePlot();
                });
            });

            document.getElementById('step-percent').addEventListener('input', updatePlot);
        });
    </script>
