        <div id="checkbox-container">
            <input type="checkbox" id="check-sphere" checked><label for="check-sphere">celestial sphere</label><br>
            <input type="checkbox" id="check-stars" checked><label for="check-stars">1000 brightest stars</label><br>
            <input type="checkbox" id="check-equator" checked><label for="check-equator">celestial equator</label><br>
            <input type="checkbox" id="check-ecliptic" checked><label for="check-ecliptic">ecliptic</label><br>
            <input type="checkbox" id="check-zenith" checked><label for="check-zenith">zenith</label><br>
            <input type="checkbox" id="check-aries" checked><label for="check-aries">first point of Aries</label><br>
        </div>

        <div id="slider-container">
            <label for="slider-ra">zenith right ascension</label>
            <input type="range" id="slider-ra" min="0" max="360" step="1" value="180"><br>
            <label for="slider-dec">zenith declination</label>
            <input type="range" id="slider-dec" min="-90" max="90" step="1" value="0">
        </div>
    </div>
    <div id="visualization-container"></div>

    <script src="https://cdnjs.cloudflare.com/ajax/libs/three.js/r128/three.min.js"></script>
    <script src="https://threejs.org/examples/js/controls/OrbitControls.js"></script>
    <script src="https://threejs.org/examples/js/renderers/CSS2DRenderer.js"></script>

    <script>
        // Sample data for constellations and stars
        const constellationFamilies = {
            "Zodiac": ["Aries", "Taurus", "Gemini", "Cancer", "Leo", "Virgo", "Libra", "Scorpius", "Sagittarius", "Capricornus", "Aquarius", "Pisces"],
            "Ursa Major Family": ["Ursa Major", "Ursa Minor"],
            "Perseus Family": ["Perseus", "Andromeda"],
            "Hercules Family": ["Hercules", "Lyra"],
            "Orion Family": ["Orion"],
            "Bayer Group": ["Canis Major", "Canis Minor"],
            "La Caille Family": ["Volans", "Mensa"]
        };

        const constellations = {
            "Aries": [[0, 0], [1, 1], [2, 2]], // Placeholder coordinates
            "Taurus": [[3, 3], [4, 4], [5, 5]],
            // Add more constellations with actual coordinates
        };

        const stars = [
            { ra: 0, dec: 0 }, // Placeholder star coordinates
            { ra: 1, dec: 1 },
            // Add more stars with actual coordinates
        ];

        const R = 1; // Radius of the celestial sphere

        // Convert RA/Dec to Cartesian coordinates
        function raDecToCartesian(ra, dec) {
            const phi = (90 - dec) * (Math.PI / 180); // Convert declination to polar angle
            const theta = ra * (Math.PI / 180); // Convert right ascension to azimuthal angle
            return {
                x: R * Math.sin(phi) * Math.cos(theta),
                y: R * Math.cos(phi),
                z: R * Math.sin(phi) * Math.sin(theta)
            };
        }

        // Initialize scene, camera, and renderer
        const scene = new THREE.Scene();
        const camera = new THREE.PerspectiveCamera(75, window.innerWidth / window.innerHeight, 0.1, 1000);
        const renderer = new THREE.WebGLRenderer({ antialias: true });
        renderer.setSize(window.innerWidth, window.innerHeight);
        document.getElementById('visualization-container').appendChild(renderer.domElement);

        // Orbit Controls
        const controls = new THREE.OrbitControls(camera, renderer.domElement);
        controls.enableDamping = true;
        controls.dampingFactor = 0.25;
        controls.enableZoom = true;

        // Celestial Sphere
        const sphereGeometry = new THREE.SphereGeometry(R, 32, 32);
        const sphereMaterial = new THREE.MeshBasicMaterial({
            transparent: true,
            opacity: 0.4,
            side: THREE.DoubleSide,
            vertexColors: true
        });
        const colors = [];
        for (let i = 0; i < sphereGeometry.attributes.position.count; i++) {
            const phi = Math.acos(sphereGeometry.attributes.position.array[i * 3 + 1] / R);
            const color = new THREE.Color();
            color.setHSL((phi / Math.PI) % 1, 1, 0.5);
            colors.push(color.r, color.g, color.b);
        }
        sphereGeometry.setAttribute('color', new THREE.Float32BufferAttribute(colors, 3));
        const celestialSphere = new THREE.Mesh(sphereGeometry, sphereMaterial);
        scene.add(celestialSphere);

        // Celestial Equator
        const equatorGeometry = new THREE.BufferGeometry();
        const equatorVertices = [];
        for (let i = 0; i <= 360; i += 10) {
            const theta = i * (Math.PI / 180);
            equatorVertices.push(R * Math.cos(theta), 0, R * Math.sin(theta));
        }
        equatorGeometry.setAttribute('position', new THREE.Float32BufferAttribute(equatorVertices, 3));
        const equatorMaterial = new THREE.LineBasicMaterial({ color: 0x0000ff });
        const celestialEquator = new THREE.LineLoop(equatorGeometry, equatorMaterial);
        scene.add(celestialEquator);

        // Ecliptic
        const eclipticGeometry = new THREE.BufferGeometry();
        const eclipticVertices = [];
        const eclipticTilt = 23.5 * (Math.PI / 180);
        for (let i = 0; i <= 360; i += 10) {
            const theta = i * (Math.PI / 180);
            const x = R * Math.cos(theta);
            const z = R * Math.sin(theta);
            const y = z * Math.tan(eclipticTilt);
            eclipticVertices.push(x, y, z);
        }
        eclipticGeometry.setAttribute('position', new THREE.Float32BufferAttribute(eclipticVertices, 3));
        const eclipticMaterial = new THREE.LineBasicMaterial({ color: 0xff69b4 });
        const ecliptic = new THREE.LineLoop(eclipticGeometry, eclipticMaterial);
        scene.add(ecliptic);

        // Brightest Stars
        const starGeometry = new THREE.BufferGeometry();
        const starPositions = stars.map(star => Object.values(raDecToCartesian(star.ra, star.dec)));
        starGeometry.setAttribute('position', new THREE.Float32BufferAttribute(starPositions.flat(), 3));
        const starMaterial = new THREE.PointsMaterial({ color: 0xffffff, size: 0.01 });
        const starsObject = new THREE.Points(starGeometry, starMaterial);
        scene.add(starsObject);

        // Constellations
        let currentConstellationLines = null;
        let currentConstellationStars = null;

        function updateConstellation() {
            const family = document.getElementById('select-family').value;
            const constellation = document.getElementById('select-constellation').value;

            if (currentConstellationLines) scene.remove(currentConstellationLines);
            if (currentConstellationStars) scene.remove(currentConstellationStars);

            if (constellation !== "None" && constellations[constellation]) {
                const linesGeometry = new THREE.BufferGeometry();
                const linesVertices = [];
                constellations[constellation].forEach(([ra, dec]) => {
                    const { x, y, z } = raDecToCartesian(ra, dec);
                    linesVertices.push(x, y, z);
                });
                linesGeometry.setAttribute('position', new THREE.Float32BufferAttribute(linesVertices.flat(), 3));
                const linesMaterial = new THREE.LineBasicMaterial({ color: 0xff0000 });
                currentConstellationLines = new THREE.LineSegments(linesGeometry, linesMaterial);
                scene.add(currentConstellationLines);

                const starsGeometry = new THREE.BufferGeometry();
                const starsVertices = [];
                const starsColors = [];
                constellations[constellation].forEach(([ra, dec]) => {
                    const { x, y, z } = raDecToCartesian(ra, dec);
                    starsVertices.push(x, y, z);
                    starsColors.push(1, 0, 0); // Red color for constellation stars
                });
                starsGeometry.setAttribute('position', new THREE.Float32BufferAttribute(starsVertices.flat(), 3));
                starsGeometry.setAttribute('color', new THREE.Float32BufferAttribute(starsColors.flat(), 3));
                const starsMaterial = new THREE.PointsMaterial({ vertexColors: true, size: 0.02 });
                currentConstellationStars = new THREE.Points(starsGeometry, starsMaterial);
                scene.add(currentConstellationStars);
            }
        }

        // First Point of Aries
        const ariesGeometry = new THREE.SphereGeometry(0.02, 32, 32);
        const ariesMaterial = new THREE.MeshBasicMaterial({ color: 0xffff00 });
        const firstPointOfAries = new THREE.Mesh(ariesGeometry, ariesMaterial);
        firstPointOfAries.position.set(R, 0, 0);
        scene.add(firstPointOfAries);

        // Zenith
        const zenithGroup = new THREE.Group();
        const zenithGeometry = new THREE.SphereGeometry(0.02, 32, 32);
        const zenithMaterial = new THREE.MeshBasicMaterial({ color: 0x000000 });
        const zenithSphere = new THREE.Mesh(zenithGeometry, zenithMaterial);
        const zenithLineGeometry = new THREE.BufferGeometry().setFromPoints([
            new THREE.Vector3(0, 0, 0),
            new THREE.Vector3(0, R, 0)
        ]);
        const zenithLineMaterial = new THREE.LineBasicMaterial({ color: 0x000000 });
        const zenithLine = new THREE.Line(zenithLineGeometry, zenithLineMaterial);
        zenithGroup.add(zenithSphere, zenithLine);
        scene.add(zenithGroup);

        function updateZenith() {
            const ra = parseFloat(document.getElementById('slider-ra').value);
            const dec = parseFloat(document.getElementById('slider-dec').value);
            const { x, y, z } = raDecToCartesian(ra, dec);
            zenithSphere.position.set(x, y, z);
            zenithLine.geometry.setFromPoints([new THREE.Vector3(0, 0, 0), new THREE.Vector3(x, y, z)]);
        }

        // Pole Labels
        const labelRenderer = new THREE.CSS2DRenderer();
        labelRenderer.setSize(window.innerWidth, window.innerHeight);
        labelRenderer.domElement.style.position = 'absolute';
        labelRenderer.domElement.style.top = 0;
        document.body.appendChild(labelRenderer.domElement);

        function addLabel(text, position) {
            const div = document.createElement('div');
            div.className = 'label';
            div.textContent = text;
            div.style.color = '#ffffff';
            div.style.fontSize = '20px';
            div.style.marginTop = '-1em';

            const label = new THREE.CSS2DObject(div);
            label.position.copy(position);
            scene.add(label);
        }

        addLabel('N', new THREE.Vector3(0, R, 0));
        addLabel('S', new THREE.Vector3(0, -R, 0));

        // Initial setup
        function populateConstellations() {
            const family = document.getElementById('select-family').value;
            const selectConstellation = document.getElementById('select-constellation');
            selectConstellation.innerHTML = '';
            if (family === "all") {
                Object.keys(constellations).forEach(constellation => {
                    const option = document.createElement('option');
                    option.value = constellation;
                    option.textContent = constellation;
                    selectConstellation.appendChild(option);
                });
                selectConstellation.value = "None";
            } else {
                constellationFamilies[family].forEach(constellation => {
                    const option = document.createElement('option');
                    option.value = constellation;
                    option.textContent = constellation;
                    selectConstellation.appendChild(option);
                });
                selectConstellation.value = constellationFamilies[family][0];
            }
            updateConstellation();
        }

        document.getElementById('select-family').addEventListener('change', populateConstellations);
        document.getElementById('select-constellation').addEventListener('change', updateConstellation);
        document.getElementById('slider-ra').addEventListener('input', updateZenith);
        document.getElementById('slider-dec').addEventListener('input', updateZenith);

        document.querySelectorAll('input[type="checkbox"]').forEach(checkbox => {
            checkbox.addEventListener('change', () => {
                const object = scene.getObjectByName(checkbox.id.replace('check-', ''));
                if (object) object.visible = checkbox.checked;
            });
        });

        populateConstellations();

        // Animation Loop
        function animate() {
            requestAnimationFrame(animate);
            controls.update();
            renderer.render(scene, camera);
            labelRenderer.render(scene, camera);
        }

        camera.position.z = 2 * R;
        animate();
    </script>
</body>
</html>
