        }
        .control-group button.active {
            background-color: #0056b3;
        }
        #plot-signal, #plot-spectrum {
            width: 100%;
            height: 50%;
            padding: 10px;
            box-sizing: border-box;
        }
    </style>
</head>
<body>
    <div id="app-container">
        <!-- Left side: Control Panel -->
        <div id="control-panel">
            <!-- Coefficients Section -->
            <h3>Coefficients</h3>
            <div class="control-group">
                <label for="slider-c1">c<sub>1</sub></label>
                <input type="range" id="slider-c1" min="-2" max="2" step="0.1" value="0">
                <span id="c1-value-display">0</span>
                <button id="reset-c1">+</button>
            </div>
            <div class="control-group">
                <label for="slider-c2">c<sub>2</sub></label>
                <input type="range" id="slider-c2" min="-2" max="2" step="0.1" value="0">
                <span id="c2-value-display">0</span>
                <button id="reset-c2">+</button>
            </div>

            <!-- Clipping Section -->
            <h3>Clipping</h3>
            <div class="control-group">
                <input type="checkbox" id="checkbox-clipping">
                <label for="checkbox-clipping" style="display: inline; margin-left: 5px;"></label>
            </div>
            <div id="level-control-group" class="control-group" style="display: none;">
                <label for="slider-level">Level</label>
                <input type="range" id="slider-level" min="0" max="1.5" step="0.05" value="0.7">
                <span id="level-value-display">0.7</span>
            </div>

            <!-- DFT Section -->
            <h3>DFT</h3>
            <div class="control-group">
                <label>Scale</label>
                <button id="btn-linear" class="active">Linear</button>
                <button id="btn-decibel">Decibel</button>
            </div>
        </div>

        <!-- Right side: Visualization Area -->
        <div id="visualization-area">
            <div id="plot-signal"></div>
            <div id="plot-spectrum"></div>
        </div>
    </div>

    <!-- Plotly.js CDN -->
    <script src="https://cdn.plot.ly/plotly-latest.min.js"></script>
    <script>
        // FFT function
        function fft(real, imag) {
            const n = real.length;
            if (n <= 1) return;

            const h = Math.log2(n);
            for (let i = 0; i < h; ++i) {
                const m = 1 << i;
                const mh = 1 << (h - i - 1);
                for (let j = 0; j < m; ++j) {
                    const w = Math.PI * j / m;
                    const wr = Math.cos(w);
                    const wi = Math.sin(w);
                    for (let k = j; k < n; k += 2 * m) {
                        const tkr = wr * real[k + m] - wi * imag[k + m];
                        const tki = wr * imag[k + m] + wi * real[k + m];
                        real[k + m] = real[k] - tkr;
                        imag[k + m] = imag[k] - tki;
                        real[k] += tkr;
                        imag[k] += tki;
                    }
                }
            }

            let bitrev = 0;
            for (let i = 1; i < n - 1; ++i) {
                for (let j = n >> 1; j > (bitrev ^= j); j >>= 1);
                if (i < bitrev) {
                    [real[i], real[bitrev]] = [real[bitrev], real[i]];
                    [imag[i], imag[bitrev]] = [imag[bitrev], imag[i]];
                }
            }
        }

        // Global variables
        let normalizedMag = [];
        let scale = 'linear';

        // Initialize plots
        function initPlots() {
            Plotly.newPlot('plot-signal', [{
                x: [],
                y: [],
                mode: 'lines',
                line: { color: '#1f77b4' }
            }], {
                title: 'Signal',
                xaxis: { title: 'Time [s]', range: [0, 0.005], autorange: false },
                yaxis: { title: '', range: [-2, 2], autorange: false },
                margin: { l: 30, r: 20, b: 40, t: 40 }
            });

            Plotly.newPlot('plot-spectrum', [{
                x: [],
                y: [],
                type: 'bar',
                marker: { color: '#ff7f0e' },
                width: 5
            }], {
                title: 'Magnitude Spectrum (DFT)',
                xaxis: { title: 'Frequency [Hz]', range: [0, 8000], autorange: false },
                yaxis: { title: '' },
                margin: { l: 30, r: 20, b: 40, t: 40 }
            });
        }

        // Update simulation
        function updateSimulation() {
            const f = 1000; // Hz
            const A = 1; // Amplitude
            const N = 2048; // Number of samples
            const fs = 32000; // Sampling frequency
            const c1 = parseFloat(document.getElementById('slider-c1').value);
            const c2 = parseFloat(document.getElementById('slider-c2').value);
            const clippingEnabled = document.getElementById('checkbox-clipping').checked;
            const level = parseFloat(document.getElementById('slider-level').value);

            // Time vector
            const t = Array.from({ length: N }, (_, i) => i / fs);

            // Input signal
            const x = t.map(ti => A * Math.sin(2 * Math.PI * f * ti));

            // Output signal
            const y = x.map(xi => xi + c1 * xi * xi + c2 * xi * xi * xi);

            // Apply clipping
            if (clippingEnabled) {
                for (let i = 0; i < y.length; ++i) {
                    y[i] = Math.max(-level, Math.min(level, y[i]));
                }
            }

            // Update signal plot
            Plotly.react('plot-signal', [{
                x: t.slice(0, Math.floor(0.005 * fs)),
                y: y.slice(0, Math.floor(0.005 * fs))
            }]);

            // Perform DFT
            const real = y.slice();
            const imag = Array(N).fill(0);
            fft(real, imag);

            // Magnitude spectrum
            normalizedMag = real.map((re, i) => Math.sqrt(re * re + imag[i] * imag[i]));
            normalizedMag[0] /= N;
            for (let i = 1; i < N / 2; ++i) {
                normalizedMag[i] *= 2 / N;
            }

            // Update spectrum plot
            updateSpectrumScale();
        }

        // Update spectrum scale
        function updateSpectrumScale() {
            const freq = Array.from({ length: N / 2 }, (_, i) => i * fs / N);
            let yData;
            let yRange;
            let yTitle;

            if (scale === 'linear') {
                yData = normalizedMag.slice(0, N / 2);
                yRange = [0, 1.1];
                yTitle = '';
            } else {
                yData = normalizedMag.slice(0, N / 2).map(mag => 20 * Math.log10(Math.max(mag, 1e-10)));
                yRange = [-100, 0];
                yTitle = '[dB]';
            }

            Plotly.react('plot-spectrum', [{
                x: freq,
                y: yData,
                type: 'bar',
                marker: { color: '#ff7f0e' },
                width: 5
            }], {
                yaxis: { title: `Magnitude ${yTitle}`, range: yRange }
            });
        }

        // Event listeners
        document.getElementById('slider-c1').addEventListener('input', updateSimulation);
        document.getElementById('slider-c2').addEventListener('input', updateSimulation);
        document.getElementById('reset-c1').addEventListener('click', () => {
            document.getElementById('slider-c1').value = 0;
            updateSimulation();
        });
        document.getElementById('reset-c2').addEventListener('click', () => {
            document.getElementById('slider-c2').value = 0;
            updateSimulation();
        });
        document.getElementById('checkbox-clipping').addEventListener('change', () => {
            const levelControlGroup = document.getElementById('level-control-group');
            levelControlGroup.style.display = document.getElementById('checkbox-clipping').checked ? 'block' : 'none';
            updateSimulation();
        });
        document.getElementById('slider-level').addEventListener('input', updateSimulation);
        document.getElementById('btn-linear').addEventListener('click', () => {
            if (scale !== 'linear') {
                scale = 'linear';
                document.getElementById('btn-linear').classList.add('active');
                document.getElementById('btn-decibel').classList.remove('active');
                updateSpectrumScale();
            }
        });
        document.getElementById('btn-decibel').addEventListener('click', () => {
            if (scale !== 'decibel') {
                scale = 'decibel';
                document.getElementById('btn-linear').classList.remove('active');
                document.getElementById('btn-decibel').classList.add('active');
                updateSpectrumScale();
            }
        });

        // Update value displays
        document.getElementById('slider-c1').addEventListener('input', () => {
            document.getElementById('c1-value-display').textContent = document.getElementById('slider-c1').value;
        });
        document.getElementById('slider-c2').addEventListener('input', () => {
            document.getElementById('c2-value-display').textContent = document.getElementById('slider-c2').value;
        });
        document.getElementById('slider-level').addEventListener('input', () => {
            document.getElementById('level-value-display').textContent = document.getElementById('slider-level').value;
        });

        // Initialize on page load
        window.onload = () => {
            initPlots();
            updateSimulation();
        };
    </script>
</body>
</html>
