            <input type="checkbox" id="checkbox-beam-bottom" checked>
            <label for="checkbox-beam-bottom">beam bottom half</label>
        </div>
    </div>
    <div id="canvas-container">
        <canvas id="p5-canvas"></canvas>
    </div>

    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.js"></script>
    <script>
        let rayCount = 15;
        let beamWidth = 1.25;
        let incidentAngle = -45;
        let refractionRatio = 2.0;

        let showInjected = true;
        let showRefracted = true;
        let showReflected = true;
        let showEjected = true;
        let showNormals = false;
        let showIntersections = false;
        let showBeamTop = true;
        let showBeamBottom = true;

        function setup() {
            const canvas = createCanvas(800, 600);
            canvas.parent('p5-canvas');

            document.getElementById('slider-ray-count').addEventListener('input', updateControls);
            document.getElementById('slider-beam-width').addEventListener('input', updateControls);
            document.getElementById('slider-incident-angle').addEventListener('input', updateControls);
            document.getElementById('slider-refraction-ratio').addEventListener('input', updateControls);

            document.getElementById('checkbox-injected').addEventListener('change', updateControls);
            document.getElementById('checkbox-refracted').addEventListener('change', updateControls);
            document.getElementById('checkbox-reflected').addEventListener('change', updateControls);
            document.getElementById('checkbox-ejected').addEventListener('change', updateControls);
            document.getElementById('checkbox-normals').addEventListener('change', updateControls);
            document.getElementById('checkbox-intersections').addEventListener('change', updateControls);
            document.getElementById('checkbox-beam-top').addEventListener('change', updateControls);
            document.getElementById('checkbox-beam-bottom').addEventListener('change', updateControls);
        }

        function draw() {
            background(255);
            translate(width / 2, height / 2);
            scale(100);

            drawAxes();
            drawSphere();

            for (let i = 0; i < rayCount; i++) {
                let angleOffset = map(i, 0, rayCount - 1, -beamWidth / 2, beamWidth / 2);
                let rayAngle = radians(incidentAngle + angleOffset);
                let rayDir = p5.Vector.fromAngle(rayAngle);

                let intersection1 = intersectSphere(p5.Vector.mult(rayDir, -100), rayDir);
                if (!intersection1) continue;

                let normal1 = p5.Vector.sub(intersection1, createVector(0, 0)).normalize();
                let theta1 = rayDir.angleBetween(normal1);
                let theta2 = asin(sin(theta1) / refractionRatio);
                let refractedDir = p5.Vector.fromAngle(rayDir.angleBetween(createVector(1, 0)) + theta2).mult(sign(rayDir.y));

                let intersection2 = intersectSphere(intersection1, refractedDir);
                if (!intersection2) continue;

                let normal2 = p5.Vector.sub(intersection2, createVector(0, 0)).normalize();
                let reflectedDir = p5.Vector.sub(rayDir.reflect(normal2), refractedDir).mult(-1);

                let intersection3 = intersectSphere(intersection2, reflectedDir);
                if (!intersection3) continue;

                let normal3 = p5.Vector.sub(intersection3, createVector(0, 0)).normalize();
                theta1 = reflectedDir.angleBetween(normal3);
                theta2 = asin(sin(theta1) * refractionRatio);
                let ejectedDir = p5.Vector.fromAngle(reflectedDir.angleBetween(createVector(1, 0)) + theta2).mult(sign(reflectedDir.y));

                if (showInjected) drawRay(p5.Vector.mult(rayDir, -100), intersection1, '#FF0000');
                if (showRefracted) drawRay(intersection1, intersection2, '#00FF00');
                if (showReflected) drawRay(intersection2, intersection3, '#0000FF');
                if (showEjected) drawRay(intersection3, p5.Vector.add(intersection3, p5.Vector.mult(ejectedDir, 100)), '#000000');

                if (showIntersections) {
                    drawIntersection(intersection1, '#FF0000');
                    drawIntersection(intersection2, '#00FF00');
                    drawIntersection(intersection3, '#0000FF');
                }

                if (showNormals) {
                    drawNormal(intersection1, normal1);
                    drawNormal(intersection2, normal2);
                    drawNormal(intersection3, normal3);
                }
            }
        }

        function drawAxes() {
            stroke(150);
            strokeWeight(0.1);
            line(-2.5, 0, 2.5, 0);
            line(0, -2.5, 0, 2.5);

            for (let i = -2; i <= 2; i++) {
                if (i !== 0) {
                    line(i, -0.05, i, 0.05);
                    line(-0.05, i, 0.05, i);
                    text(i, i + 0.05, -0.1);
                    text(i, 0.1, i + 0.1);
                }
            }
        }

        function drawSphere() {
            noFill();
            stroke(0);
            strokeWeight(0.1);
            ellipse(0, 0, 2, 2);
        }

        function drawRay(start, end, color) {
            stroke(color);
            strokeWeight(0.05);
            line(start.x, start.y, end.x, end.y);
        }

        function drawIntersection(point, color) {
            fill(color);
            noStroke();
            ellipse(point.x, point.y, 0.1, 0.1);
        }

        function drawNormal(point, normal) {
            stroke(0);
            strokeWeight(0.05);
            strokeDashArray([0.1, 0.1]);
            line(point.x, point.y, point.x + normal.x * 0.2, point.y + normal.y * 0.2);
            strokeDashArray([]);
        }

        function intersectSphere(start, dir) {
            let a = dir.dot(dir);
            let b = 2 * start.dot(dir);
            let c = start.dot(start) - 1;

            let discriminant = b * b - 4 * a * c;
            if (discriminant < 0) return null;

            let t1 = (-b - sqrt(discriminant)) / (2 * a);
            let t2 = (-b + sqrt(discriminant)) / (2 * a);

            let t = t1 > 0 ? t1 : t2;
            if (t < 0) return null;

            return p5.Vector.add(start, p5.Vector.mult(dir, t));
        }

        function updateControls() {
            rayCount = parseInt(document.getElementById('slider-ray-count').value);
            beamWidth = parseFloat(document.getElementById('slider-beam-width').value);
            incidentAngle = parseFloat(document.getElementById('slider-incident-angle').value);
            refractionRatio = parseFloat(document.getElementById('slider-refraction-ratio').value);

            showInjected = document.getElementById('checkbox-injected').checked;
            showRefracted = document.getElementById('checkbox-refracted').checked;
            showReflected = document.getElementById('checkbox-reflected').checked;
            showEjected = document.getElementById('checkbox-ejected').checked;
            showNormals = document.getElementById('checkbox-normals').checked;
            showIntersections = document.getElementById('checkbox-intersections').checked;
            showBeamTop = document.getElementById('checkbox-beam-top').checked;
            showBeamBottom = document.getElementById('checkbox-beam-bottom').checked;

            document.getElementById('value-ray-count').textContent = rayCount;
            document.getElementById('value-beam-width').textContent = beamWidth.toFixed(2);
            document.getElementById('value-incident-angle').textContent = incidentAngle;
            document.getElementById('value-refraction-ratio').textContent = refractionRatio.toFixed(2);
        }

        function sign(x) {
            return x < 0 ? -1 : 1;
        }
    </script>
</body>
</html>
