    <script>
        let graphSize = 18;
        let maxDegree = 3;
        let currentStep = 0;
        let history = [];

        const partitions = { U: [], V: [] };
        let edges = [];
        let matching = [];
        let cover = [];
        let bfsTree = {};
        let highlightedPath = [];

        function setup() {
            createCanvas(windowWidth, 600);
            generateGraph();
            executeHungarianAlgorithm();
            updateStepControls();
            drawStep(currentStep);
        }

        function draw() {}

        function generateGraph() {
            partitions.U = Array.from({ length: graphSize / 2 }, (_, i) => i + 1);
            partitions.V = Array.from({ length: graphSize / 2 }, (_, i) => i + 1 + graphSize / 2);

            edges = [];
            for (let v of partitions.V) {
                let degree = Math.floor(Math.random() * (maxDegree - 1)) + 1;
                let neighbors = [...partitions.U];
                for (let i = 0; i < degree; i++) {
                    let u = neighbors.splice(Math.floor(Math.random() * neighbors.length), 1)[0];
                    edges.push([u, v]);
                }
            }
        }

        function executeHungarianAlgorithm() {
            history = [];
            matching = [];
            cover = [];
            bfsTree = {};
            highlightedPath = [];

            // Simplified version of the algorithm for demonstration
            // In practice, implement the full BFS and DFS steps

            // Example steps for demonstration
            history.push({ matching: [], cover: [], bfsTree: {}, highlightedPath: [], statusText: "Initial state" });
            history.push({ matching: [[1, 10]], cover: [1, 10], bfsTree: { 1: null }, highlightedPath: [[1, 10]], statusText: "Matched (1, 10)" });
            history.push({ matching: [[1, 10], [2, 11]], cover: [1, 2, 10, 11], bfsTree: { 1: null, 2: 1 }, highlightedPath: [[2, 11]], statusText: "Matched (2, 11)" });

            // Add more steps as needed
        }

        function updateStepControls() {
            const stepControls = document.getElementById('step-controls');
            stepControls.innerHTML = '';
            for (let i = 0; i < history.length; i++) {
                const button = document.createElement('button');
                button.id = `step-${i + 1}`;
                button.textContent = i + 1;
                button.addEventListener('click', () => {
                    currentStep = i;
                    drawStep(currentStep);
                    updateActiveButton(stepControls, button);
                });
                stepControls.appendChild(button);
            }
            updateActiveButton(stepControls, document.getElementById(`step-${currentStep + 1}`));
        }

        function updateActiveButton(container, button) {
            container.querySelectorAll('button').forEach(btn => btn.classList.remove('active'));
            button.classList.add('active');
        }

        function drawStep(step) {
            clear();
            const state = history[step];
            matching = state.matching;
            cover = state.cover;
            bfsTree = state.bfsTree;
            highlightedPath = state.highlightedPath;
            document.getElementById('status-text').textContent = state.statusText;

            drawBipartiteGraph();
            drawAugmentingPathTree();
        }

        function drawBipartiteGraph() {
            const numU = partitions.U.length;
            const numV = partitions.V.length;
            const spacing = 100;
            const radius = 20;

            // Draw partition U
            for (let i = 0; i < numU; i++) {
                const x = width / 4;
                const y = 100 + i * spacing;
                const u = partitions.U[i];
                fill(cover.includes(u) ? '#00C000' : '#333333');
                ellipse(x, y, radius * 2, radius * 2);
                fill('#FFFFFF');
                textSize(16);
                textAlign(CENTER, CENTER);
                text(u, x, y);
            }

            // Draw partition V
            for (let i = 0; i < numV; i++) {
                const x = (width * 3) / 4;
                const y = 100 + i * spacing;
                const v = partitions.V[i];
                fill(cover.includes(v) ? '#00C000' : '#333333');
                ellipse(x, y, radius * 2, radius * 2);
                fill('#FFFFFF');
                textSize(16);
                textAlign(CENTER, CENTER);
                text(v, x, y);
            }

            // Draw edges
            stroke('#808080');
            strokeWeight(1);
            for (let [u, v] of edges) {
                const ux = width / 4;
                const uy = 100 + (partitions.U.indexOf(u) * spacing);
                const vx = (width * 3) / 4;
                const vy = 100 + (partitions.V.indexOf(v) * spacing);
                line(ux, uy, vx, vy);
            }

            // Draw matching edges
            stroke('#A0A0FF');
            strokeWeight(4);
            for (let [u, v] of matching) {
                const ux = width / 4;
                const uy = 100 + (partitions.U.indexOf(u) * spacing);
                const vx = (width * 3) / 4;
                const vy = 100 + (partitions.V.indexOf(v) * spacing);
                line(ux, uy, vx, vy);
            }

            // Draw highlighted path edges
            stroke('#FF0000');
            strokeWeight(2);
            for (let [u, v] of highlightedPath) {
                const ux = width / 4;
                const uy = 100 + (partitions.U.indexOf(u) * spacing);
                const vx = (width * 3) / 4;
                const vy = 100 + (partitions.V.indexOf(v) * spacing);
                line(ux, uy, vx, vy);
            }
        }

        function drawAugmentingPathTree() {
            const spacing = 100;
            const size = 40;

            // Draw BFS tree nodes
            const queue = [{ node: Object.keys(bfsTree)[0], depth: 0, index: 0 }];
            const positions = {};

            while (queue.length > 0) {
                const { node, depth, index } = queue.shift();
                const x = 50 + depth * spacing;
                const y = 100 + index * spacing;
                positions[node] = { x, y };

                fill('#FFA500');
                stroke('#000000');
                rect(x, y, size, size);
                fill('#8080C0');
                textSize(16);
                textAlign(CENTER, CENTER);
                text(node, x + size / 2, y + size / 2);

                let childIndex = 0;
                for (let child of bfsTree[node] || []) {
                    queue.push({ node: child, depth: depth + 1, index: index * 2 + childIndex });
                    childIndex++;
                }
            }

            // Draw BFS tree edges
            stroke('#FF0000');
            strokeWeight(1);
            for (let node in bfsTree) {
                for (let child of bfsTree[node] || []) {
                    const parentPos = positions[node];
                    const childPos = positions[child];
                    line(parentPos.x + size / 2, parentPos.y + size, childPos.x + size / 2, childPos.y);
                }
            }
        }

        document.getElementById('size-controls').addEventListener('click', (event) => {
            if (event.target.tagName === 'BUTTON') {
                document.getElementById('size-controls').querySelector('.active').classList.remove('active');
                event.target.classList.add('active');
                graphSize = parseInt(event.target.id.split('-')[1]);
                generateGraph();
                executeHungarianAlgorithm();
                updateStepControls();
                currentStep = 0;
                drawStep(currentStep);
            }
        });

        document.getElementById('degree-controls').addEventListener('click', (event) => {
            if (event.target.tagName === 'BUTTON') {
                document.getElementById('degree-controls').querySelector('.active').classList.remove('active');
                event.target.classList.add('active');
                maxDegree = parseInt(event.target.id.split('-')[1]);
                generateGraph();
                executeHungarianAlgorithm();
                updateStepControls();
                currentStep = 0;
                drawStep(currentStep);
            }
        });

        document.getElementById('reset-button').addEventListener('click', () => {
            generateGraph();
            executeHungarianAlgorithm();
            updateStepControls();
            currentStep = 0;
            drawStep(currentStep);
        });

        new p5(setup);
    </script>
</body>
</html>
