<body>
    <div id="main-container">
        <div class="control-panel">
            <div class="control-group">
                <label>Scale</label>
                <input type="radio" id="radio-scale-linear" name="scale-type" checked>
                <label for="radio-scale-linear">linear</label>
                <input type="radio" id="radio-scale-logarithmic" name="scale-type">
                <label for="radio-scale-logarithmic">logarithmic</label>
            </div>
            <div class="control-group">
                <label>Plot Range</label>
                <input type="radio" id="radio-range-fixed" name="range-type" checked>
                <label for="radio-range-fixed">fixed</label>
                <input type="radio" id="radio-range-automatic" name="range-type">
                <label for="radio-range-automatic">automatic</label>
            </div>
            <div class="control-group">
                <label>Number of Elements</label>
                <input type="range" id="slider-elements" min="1" max="20" step="1" value="10">
                <span id="display-elements">+ 10</span>
            </div>
            <div class="algorithm-group">
                <span>\(O(n^2)\)</span>
                <input type="checkbox" id="check-bubble" checked>
                <label for="check-bubble">bubble</label>
                <input type="checkbox" id="check-selection" checked>
                <label for="check-selection">selection</label>
                <input type="checkbox" id="check-insertion" checked>
                <label for="check-insertion">insertion</label>
            </div>
            <div class="algorithm-group">
                <span>\(O(n \log(n))\)</span>
                <input type="checkbox" id="check-merge">
                <label for="check-merge">merge</label>
                <input type="checkbox" id="check-quick">
                <label for="check-quick">quick</label>
                <input type="checkbox" id="check-heap">
                <label for="check-heap">heap</label>
            </div>
            <div class="algorithm-group">
                <span>\(O(n \cdot n!)\)</span>
                <input type="checkbox" id="check-bogo">
                <label for="check-bogo">bogo</label>
            </div>
        </div>
        <div id="plot-container"></div>
