    <script>
        // Helper function to calculate factorial
        function factorial(n) {
            if (n === 0 || n === 1) return 1;
            let result = 1;
            for (let i = 2; i <= n; i++) {
                result *= i;
            }
            return result;
        }

        // Function to update the plot
        function updatePlot() {
            const numElements = parseInt(document.getElementById('slider-elements').value);
            const xValues = Array.from({ length: numElements }, (_, i) => i + 1);

            const bubbleY = xValues.map(x => x * x);
            const selectionY = xValues.map(x => 0.25 * x * x);
            const insertionY = xValues.map(x => x * factorial(x));
            const mergeY = xValues.map(x => x * Math.log2(x));
            const quickY = xValues.map(x => 1.2 * x * Math.log2(x));
            const heapY = xValues.map(x => 1.5 * x * Math.log2(x));
            const bogoY = insertionY; // Same as insertion

            const traces = [
                { x: xValues, y: bubbleY, mode: 'lines', name: 'bubble', line: { color: 'green', width: 5 } },
                { x: xValues, y: selectionY, mode: 'lines', name: 'selection', line: { color: 'orange', dash: 'dot' } },
                { x: xValues, y: insertionY, mode: 'lines', name: 'insertion', line: { color: 'red', width: 2 }, visible: document.getElementById('check-insertion').checked || document.getElementById('check-bogo').checked },
                { x: xValues, y: mergeY, mode: 'lines', name: 'merge', line: { color: 'blue', width: 2 } },
                { x: xValues, y: quickY, mode: 'lines', name: 'quick', line: { color: 'purple', width: 2 } },
                { x: xValues, y: heapY, mode: 'lines', name: 'heap', line: { color: 'cyan', width: 2 } },
                { x: xValues, y: bogoY, mode: 'lines', name: 'bogo', line: { color: 'red', width: 2 }, visible: false }
            ];

            const layout = {
                title: 'time complexity',
                xaxis: {
                    title: 'number of elements',
                    range: document.getElementById('radio-range-fixed').checked ? [0, 20] : null,
                    autorange: document.getElementById('radio-range-automatic').checked
                },
                yaxis: {
                    title: 'operations',
                    type: document.getElementById('radio-scale-linear').checked ? 'linear' : 'log',
                    range: document.getElementById('radio-range-fixed').checked ? [0, 500] : null,
                    autorange: document.getElementById('radio-range-automatic').checked
                },
                legend: {
                    orientation: 'h',
                    yanchor: 'bottom',
                    y: 1.02,
                    xanchor: 'right',
                    x: 1
                }
            };

            Plotly.react('plot-container', traces, layout);
        }

        // Initial plot setup
        updatePlot();

        // Event listeners for controls
        document.getElementById('slider-elements').addEventListener('input', () => {
            const value = document.getElementById('slider-elements').value;
            document.getElementById('display-elements').textContent = `+ ${value}`;
            updatePlot();
        });

        document.querySelectorAll('input[name="scale-type"]').forEach(radio => {
            radio.addEventListener('change', updatePlot);
        });

        document.querySelectorAll('input[name="range-type"]').forEach(radio => {
            radio.addEventListener('change', updatePlot);
        });

        document.querySelectorAll('.algorithm-group input[type="checkbox"]').forEach(checkbox => {
            checkbox.addEventListener('change', updatePlot);
        });
    </script>
</body>
