    <script src="https://cdn.plot.ly/plotly-latest.min.js"></script>
</head>
<body>
    <div id="main-container">
        <div id="control-panel">
            <label for="slider-xD">distillate composition</label>
            <input type="range" id="slider-xD" min="0.80" max="1.00" step="0.01" value="0.84">
            <span id="label-xD">0.84</span>
        </div>
        <div id="plot"></div>
    </div>

    <script>
        const sliderxD = document.getElementById('slider-xD');
        const labelxD = document.getElementById('label-xD');

        function updatePlot(xD) {
            const a = 9;
            const b = -0.6;

            function y(x) {
                return (a * x) / (1 + (a - 1) * x + b * x * (1 - x));
            }

            function r(x, xD) {
                return (xD - y(x)) / (y(x) - x);
            }

            let x_vals = [];
            let r_vals = [];

            for (let x = 0.001; x <= xD; x += 0.001) {
                let ry = r(x, xD);
                if (ry >= 0 && ry < 5 && Number.isFinite(ry)) {
                    x_vals.push(x);
                    r_vals.push(ry);
                }
            }

            let pinch_x = [];
            let pinch_y = [];

            for (let i = 1; i < r_vals.length - 1; i++) {
                if (r_vals[i - 1] < r_vals[i] && r_vals[i] > r_vals[i + 1]) {
                    pinch_x.push(x_vals[i]);
                    pinch_y.push(r_vals[i]);
                    break;
                }
            }

            const trace1 = {
                x: x_vals,
                y: r_vals,
                mode: 'lines',
                type: 'scatter',
                line: { color: '#8B0000', width: 2.5 }
            };

            const trace2 = {
                x: pinch_x,
                y: pinch_y,
                mode: 'markers',
                type: 'scatter',
                marker: { color: 'royalblue', size: 10 }
            };

            const data = [trace1, trace2];

            const layout = {
                xaxis: { range: [0, 1.0], gridcolor: '#e0e0e0', tick0: 0, dtick: 0.2 },
                yaxis: { range: [0, 3.0], gridcolor: '#e0e0e0', tick0: 0, dtick: 0.5 },
                showlegend: false,
                margin: { l: 40, r: 20, b: 40, t: 20 },
                plot_bgcolor: 'white',
                paper_bgcolor: 'white'
            };

            Plotly.newPlot('plot', data, layout);
        }

        sliderxD.addEventListener('input', function() {
            const newValue = parseFloat(this.value).toFixed(2);
            labelxD.textContent = newValue;
            updatePlot(newValue);
        });

        updatePlot(sliderxD.value);
    </script>
</body>
</html>
