    <script>
        let sceneObjects = [];
        let selectedTool = null;
        let activeObject = null;
        let interactionMode = null;
        let offsetX, offsetY, startAngle;

        function setup() {
            const canvas = createCanvas(800, 600);
            canvas.parent('drawing-canvas');
            canvas.mousePressed(mousePressedHandler);
            canvas.mouseDragged(mouseDraggedHandler);
            canvas.mouseReleased(mouseReleasedHandler);
            canvas.mouseMoved(mouseMovedHandler);

            document.getElementById('btn-undo').addEventListener('click', () => {
                sceneObjects.pop();
                redraw();
            });

            document.getElementById('btn-add').addEventListener('click', () => {
                const structureType = document.getElementById('select-structure').value;
                sceneObjects.push({ type: 'structure', value: structureType, x: width / 2, y: height / 2, rotation: 0 });
                redraw();
            });

            document.querySelectorAll('#toolbar button').forEach(button => {
                button.addEventListener('click', () => {
                    document.querySelectorAll('#toolbar button').forEach(btn => btn.classList.remove('selected'));
                    button.classList.add('selected');

                    const type = button.id.startsWith('btn-charge') ? 'charge' : 'atom';
                    const value = button.textContent;
                    selectedTool = { type, value };
                });
            });
        }

        function draw() {
            background(255);

            sceneObjects.forEach(obj => {
                push();
                translate(obj.x, obj.y);
                rotate(obj.rotation);

                if (obj.type === 'atom' || obj.type === 'charge') {
                    textAlign(CENTER, CENTER);
                    textSize(16);
                    text(obj.value, 0, 0);
                } else if (obj.type === 'structure') {
                    switch (obj.value) {
                        case 'water':
                            drawWater();
                            break;
                        case 'cyclopropyl_ethane':
                            drawCyclopropylEthane();
                            break;
                        case 'borazine':
                            drawBorazine();
                            break;
                        case 'isopropylamine':
                            drawIsopropylamine();
                            break;
                        default:
                            break;
                    }
                }

                pop();
            });

            if (selectedTool && mouseX >= 0 && mouseY >= 0 && mouseX <= width && mouseY <= height) {
                push();
                translate(mouseX, mouseY);
                textAlign(CENTER, CENTER);
                textSize(16);
                text(selectedTool.value, 0, 0);
                pop();
            }
        }

        function mousePressedHandler() {
            for (let i = sceneObjects.length - 1; i >= 0; i--) {
                const obj = sceneObjects[i];
                const dx = mouseX - obj.x;
                const dy = mouseY - obj.y;
                const distance = sqrt(dx * dx + dy * dy);

                if (distance < 25) {
                    activeObject = obj;
                    offsetX = mouseX - obj.x;
                    offsetY = mouseY - obj.y;
                    startAngle = atan2(dy, dx) - obj.rotation;
                    interactionMode = distance < 10 ? 'rotate' : 'translate';
                    break;
                }
            }
        }

        function mouseDraggedHandler() {
            if (activeObject) {
                if (interactionMode === 'translate') {
                    activeObject.x = mouseX - offsetX;
                    activeObject.y = mouseY - offsetY;
                } else if (interactionMode === 'rotate') {
                    const dx = mouseX - activeObject.x;
                    const dy = mouseY - activeObject.y;
                    activeObject.rotation = atan2(dy, dx) - startAngle;
                }
                redraw();
            }
        }

        function mouseReleasedHandler() {
            activeObject = null;
            interactionMode = null;
        }

        function mouseMovedHandler() {
            if (selectedTool) {
                redraw();
            }
        }

        function drawWater() {
            text('O', 0, 0);
            text('H', -20, -20);
            text('H', 20, -20);
            line(-20, -20, 0, 0);
            line(20, -20, 0, 0);
