        }
    </style>
    <script src="https://cdn.jsdelivr.net/npm/mathjax@3/es5/tex-mml-chtml.js"></script>
    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.min.js"></script>
</head>
<body>
    <div id="controls-container">
        <div>
            <label for="slider-amplitude">\(\mathit{a}\)</label>
            <input type="range" id="slider-amplitude" min="0.1" max="2.0" step="0.01" value="1.5">
            <span id="value-amplitude">1.50</span>
        </div>
        <div>
            <label for="slider-frequency">\(\mathit{f}\)</label>
            <input type="range" id="slider-frequency" min="0.1" max="1.0" step="0.01" value="0.3">
            <span id="value-frequency">0.30</span>
        </div>
        <div>
            <label for="slider-wavelength">\(\lambda\)</label>
            <input type="range" id="slider-wavelength" min="1.0" max="10.0" step="0.1" value="4.5">
            <span id="value-wavelength">4.50</span>
        </div>
        <div>
            <label for="slider-time">\(\mathit{t}\)</label>
            <input type="range" id="slider-time" min="0" max="10" step="0.01" value="2.5">
            <span id="value-time">2.50</span>
        </div>
        <div>
            <label for="select-phase">initial phase \(\phi_0\)</label>
            <select id="select-phase">
                <option value="-3.14159">-\(\pi\)</option>
                <option value="-2.09439">-\(2\pi/3\)</option>
                <option value="-1.57080" selected>-\(\pi/2\)</option>
                <option value="-1.04720">-\(\pi/3\)</option>
                <option value="0">0</option>
                <option value="1.04720">\(\pi/3\)</option>
                <option value="1.57080">\(\pi/2\)</option>
                <option value="2.09439">\(2\pi/3\)</option>
                <option value="3.14159">\(\pi\)</option>
            </select>
            <span id="value-phase">-\(\pi/2\)</span>
        </div>
    </div>
    <div id="formula-display">\(y(x,t) = A \cos(\omega t - kx + \phi_0)\)</div>
    <div id="canvas-container"></div>

    <script>
        let A, f, lambda, t, phi0, k, omega;

        function updateValues() {
            A = parseFloat(document.getElementById('slider-amplitude').value);
            f = parseFloat(document.getElementById('slider-frequency').value);
            lambda = parseFloat(document.getElementById('slider-wavelength').value);
            t = parseFloat(document.getElementById('slider-time').value);
            phi0 = parseFloat(document.getElementById('select-phase').value);

            document.getElementById('value-amplitude').textContent = A.toFixed(2);
            document.getElementById('value-frequency').textContent = f.toFixed(2);
            document.getElementById('value-wavelength').textContent = lambda.toFixed(2);
            document.getElementById('value-time').textContent = t.toFixed(2);
            document.getElementById('value-phase').textContent = phi0.toFixed(2);

            k = 2 * Math.PI / lambda;
            omega = 2 * Math.PI * f;

            updateFormula();
        }

        function updateFormula() {
            let formula;
            if (phi0 === Math.PI / 2) {
                formula = `\(y(x,t) = ${A.toFixed(2)} \sin(${k.toFixed(2)}x - ${omega.toFixed(2)}t)\)`;
            } else if (phi0 === -Math.PI / 2) {
                formula = `\(y(x,t) = -${A.toFixed(2)} \sin(${k.toFixed(2)}x - ${omega.toFixed(2)}t)\)`;
            } else if (phi0 === Math.PI) {
                formula = `\(y(x,t) = -${A.toFixed(2)} \cos(${k.toFixed(2)}x - ${omega.toFixed(2)}t)\)`;
            } else if (phi0 === 0) {
                formula = `\(y(x,t) = ${A.toFixed(2)} \cos(${k.toFixed(2)}x - ${omega.toFixed(2)}t)\)`;
            } else {
                formula = `\(y(x,t) = ${A.toFixed(2)} \cos(${omega.toFixed(2)}t - ${k.toFixed(2)}x + ${phi0.toFixed(2)})\)`;
            }
            document.getElementById('formula-display').innerHTML = formula;
            MathJax.typesetPromise([document.getElementById('formula-display')]);
        }

        function drawArrow(baseX, baseY, vectorY, color, label) {
            const scale = 15; // Scale factor for vector length
            const headSize = 5; // Size of the arrowhead
            const tipX = baseX;
            const tipY = baseY - vectorY * scale;

            stroke(color);
            fill(color);
            line(baseX, baseY, tipX, tipY); // Arrow line

            // Arrowhead
            beginShape();
            vertex(tipX, tipY);
            vertex(tipX - headSize, tipY + headSize / 2);
            vertex(tipX + headSize, tipY + headSize / 2);
            endShape(CLOSE);

            // Label
            fill(color);
            noStroke();
            textSize(12);
            textAlign(LEFT, CENTER);
            text(label, tipX + headSize + 2, tipY);
        }

        function setup() {
            createCanvas(800, 400);
            updateValues();
        }

        function draw() {
            background(255);
            translate(width / 2, height / 2);
            scale(1, -1);

            // Draw axes
            stroke(0);
            line(-width / 2, 0, width / 2, 0); // X-axis
            line(0, -height / 2, 0, height / 2); // Y-axis

            // Arrowheads
            triangle(width / 2 - 10, -10, width / 2, 0, width / 2 - 10, 10);
            triangle(-10, height / 2 - 10, 0, height / 2, 10, height / 2 - 10);

            // Labels
            fill(0);
            noStroke();
            textSize(16);
            textAlign(CENTER, BOTTOM);
            text("x", width / 2 - 20, -10);
            textAlign(RIGHT, CENTER);
            text("y", 10, height / 2 - 20);

            // Draw grid and ticks
            const spacing = 30; // Pixels per unit
            stroke(150, 200, 150);
            strokeWeight(1);
            for (let i = 1; i <= 16; i++) {
                line(i * spacing, -height / 2, i * spacing, height / 2);
                stroke(0);
                point(i * spacing, 0);
                textAlign(CENTER, TOP);
                text(i, i * spacing, 5);
                stroke(150, 200, 150);
            }

            // Draw wave
            stroke(0, 0, 255);
            strokeWeight(2);
            noFill();
            beginShape();
            for (let xPixel = 0; xPixel < width; xPixel++) {
                const xWorld = (xPixel - width / 2) / spacing;
                const yWorld = A * Math.cos(omega * t - k * xWorld + phi0);
                vertex(xPixel, yWorld * spacing);
            }
            endShape();

            // Draw points and vectors
            stroke(0);
            strokeWeight(1);
            fill(0);
            for (let x = 1; x <= 16; x++) {
                const xWorld = x * spacing - width / 2;
                const yWorld = A * Math.cos(omega * t - k * xWorld + phi0);
                const v = -A * omega * Math.sin(omega * t - k * xWorld + phi0);
                const a = -A * omega * omega * Math.cos(omega * t - k * xWorld + phi0);

                ellipse(x * spacing, yWorld * spacing, 5, 5);

                // Velocity vector
                drawArrow(x * spacing, yWorld * spacing, v, 'red', '\\(\\vec{v}\\)');

                // Acceleration vector
                drawArrow(x * spacing, yWorld * spacing, a, 'black', '\\(\\vec{a}\\)');
            }
        }

        document.querySelectorAll('#controls-container input, #controls-container select').forEach(element => {
            element.addEventListener('input', updateValues);
            element.addEventListener('change', updateValues);
        });

        updateValues();
    </script>
</body>
</html>
