    <script>
        let epsilon = 0;
        let canvas;

        function setup() {
            canvas = createCanvas(700, 500);
            canvas.parent('canvas-container');
            document.getElementById('slider-eccentricity').addEventListener('input', updateEpsilon);
            updateEnergyDisplay();
            MathJax.typeset();
