        function draw() {
            clear();
            background('#f0f0f0');
            translate(width * 0.7, height / 2);
            scale(1, -1);

            // Draw axes
            stroke(0);
            strokeWeight(1);
            line(-15 * 30, 0, 5 * 30, 0); // x-axis
            line(0, -7 * 30, 0, 7 * 30); // y-axis

            // Draw tick marks
            for (let i = -15; i <= 5; i++) {
                line(i * 30, -3, i * 30, 3);
                text(i, i * 30 - 5, 15);
