            for (let i = -7; i <= 7; i++) {
                line(-3, i * 30, 3, i * 30);
                text(i, -20, i * 30 + 5);
            }

            // Label axes
            textSize(12);
            text('Global\'x', 5 * 30, 15);
            text('Global\'y', -15, -7 * 30 - 15);

            // Draw central body
            fill(0);
            noStroke();
            ellipse(0, 0, 6, 6);

            // Draw orbit path
            stroke('#4a80b5');
            strokeWeight(2);
            noFill();
            beginShape();
            for (let theta = 0; theta < TWO_PI; theta += 0.01) {
                let r;
                if (epsilon === 0) {
                    r = 2;
                    vertex((r * cos(theta) - 2) * 30, r * sin(theta) * 30);
                } else if (epsilon === 1) {
                    if (theta >= -2.2 && theta <= 2.2) {
                        r = 2 / (1 + epsilon * cos(theta));
                        vertex(r * cos(theta) * 30, r * sin(theta) * 30);
                    }
                } else if (epsilon > 1) {
                    let thetaMin = acos(-1 / epsilon);
                    if (theta >= -thetaMin && theta <= thetaMin) {
                        r = 2 / (1 + epsilon * cos(theta));
                        vertex(r * cos(theta) * 30, r * sin(theta) * 30);
                    }
