        }

        function updateEpsilon(event) {
            epsilon = parseFloat(event.target.value);
            document.getElementById('value-eccentricity').textContent = epsilon.toFixed(1);
            updateEnergyDisplay();
            redraw();
        }

        function updateEnergyDisplay() {
            const energy = -0.25 * (1 - epsilon ** 2);
            const energyDisplay = document.getElementById('energy-value');
            if (epsilon === 0) {
                energyDisplay.innerHTML = '\\(-\\frac{1}{4}\\)';
                MathJax.typeset();
            } else {
                energyDisplay.textContent = energy.toFixed(2);
            }
        }
    </script>
</body>
</html>
