            margin: 0 5px;
        }
    </style>
</head>
<body>
    <div class="container">
        <h1>Quantum Computer Search Algorithms</h1>
        <div class="control-panel">
            <div>
                <span>algorithm:</span>
                <button id="btn-unstructured">unstructured</button>
                <button id="btn-adiabatic">adiabatic</button>
                <button id="btn-discrete-adiabatic">discrete adiabatic</button>
                <button id="btn-heuristic" class="active">heuristic</button>
            </div>
            <div>
                <label for="slider-total-steps">number of steps to complete the search:</label>
                <input type="range" id="slider-total-steps" min="1" max="200" step="1" value="10">
                <span id="display-total-steps">10</span>
            </div>
            <div>
                <label id="label-current-step" for="slider-current-step">current step (0 to 10):</label>
                <input type="range" id="slider-current-step" min="0" max="10" step="1" value="0">
                <span id="display-current-step">0</span>
            </div>
        </div>
        <div class="visualization-area">
            <div class="plot-container">
                <h4>amplitudes</h4>
                <div id="plot-amplitudes"></div>
            </div>
            <div class="plot-container">
                <div class="plot-mode-buttons">
                    <button id="btn-conflicts" class="active">conflicts</button>
                    <button id="btn-eigenvalues">eigenvalues</button>
                </div>
                <div id="plot-probability"></div>
            </div>
        </div>
    </div>

    <script src="https://cdn.plot.ly/plotly-latest.min.js"></script>
    <script src="https://cdnjs.cloudflare.com/ajax/libs/mathjs/11.5.0/math.min.js"></script>
    <script>
        const N = 64;
        let solutions = [];
        let conflictNumbers = [];
        let stateVector = Array(N).fill(math.complex(1 / Math.sqrt(N), 0));
        let algorithm = 'heuristic';
        let totalSteps = 10;
        let currentStep = 0;
        let plotMode = 'conflicts';

        function initialize() {
            solutions = math.randomInt(0, N, 2);
            conflictNumbers = Array(N).fill(0).map((_, i) => {
                if (solutions.includes(i)) return 0;
                return solutions.map(sol => hammingDistance(i, sol)).reduce((min, dist) => Math.min(min, dist), Infinity);
            });
            update();
        }

        function hammingDistance(a, b) {
            let dist = 0;
            for (let i = 0; i < 6; i++) {
                if ((a & (1 << i)) !== (b & (1 << i))) dist++;
            }
            return dist;
        }

        function update() {
            readControls();
            calculateStateVector();
            drawAmplitudesPlot();
            drawProbabilityPlot();
        }

        function readControls() {
            algorithm = document.querySelector('.control-panel button.active').id.replace('btn-', '');
            totalSteps = parseInt(document.getElementById('slider-total-steps').value);
            currentStep = parseInt(document.getElementById('slider-current-step').value);
            plotMode = document.querySelector('.plot-mode-buttons button.active').id.replace('btn-', '');
        }

        function calculateStateVector() {
            stateVector = Array(N).fill(math.complex(1 / Math.sqrt(N), 0));
            const s = currentStep / totalSteps;

            if (algorithm === 'unstructured') {
                const oracle = Array.from({ length: N }, (_, i) => solutions.includes(i) ? -1 : 1);
                const Uw = math.diag(oracle);
                const P0 = Array.from({ length: N }, () => Array(N).fill(1 / N));
                const Us = math.subtract(math.multiply(2, P0), math.eye(N));
                const G = math.multiply(Us, Uw);

                for (let k = 0; k <= currentStep; k++) {
                    stateVector = math.multiply(G, stateVector);
                }
            } else if (algorithm === 'adiabatic' || algorithm === 'discrete-adiabatic' || algorithm === 'heuristic') {
                const H0 = math.multiply(-1, Array.from({ length: N }, () => Array(N).fill(1 / N)));
                const Hp = math.diag(conflictNumbers);
                const Hs = math.add(math.multiply(1 - s, H0), math.multiply(s, Hp));

                if (algorithm === 'adiabatic') {
                    const eigs = math.eigs(Hs);
                    stateVector = eigs.vectors[0];
                } else {
                    const T = algorithm === 'heuristic' ? 10 : 200;
                    for (let k = 0; k <= currentStep; k++) {
                        const Uk = math.expm(math.multiply(math.complex(0, -1), math.divide(Hs, T)));
                        stateVector = math.multiply(Uk, stateVector);
                    }
                }
            }
        }

        function drawAmplitudesPlot() {
            const realParts = stateVector.map(c => c.re);
            const imagParts = stateVector.map(c => c.im);
            const colors = conflictNumbers.map(c => c === 0 ? 'black' : c > 3 ? 'orange' : 'blue');
            colors[solutions[0]] = 'black';
            colors[solutions[1]] = 'black';
            if (currentStep === 0) colors[0] = 'red';

            const trace = {
                x: realParts,
                y: imagParts,
                mode: 'markers',
                marker: { color: colors, size: 10 },
                type: 'scattergl'
            };

            Plotly.newPlot('plot-amplitudes', [trace], {
                xaxis: { title: 'Re', zeroline: true },
                yaxis: { title: 'Im', zeroline: true },
                title: 'Amplitudes'
            });
        }

        function drawProbabilityPlot() {
            const probabilities = stateVector.map(c => math.pow(c.abs(), 2));
            let data;

            if (plotMode === 'conflicts') {
                const conflictProbabilities = conflictNumbers.reduce((acc, c, i) => {
                    acc[c] = (acc[c] || 0) + probabilities[i];
                    return acc;
                }, {});

                data = [
                    {
                        x: Object.keys(conflictProbabilities).map(Number),
                        y: Object.values(conflictProbabilities),
                        type: 'bar'
                    }
                ];
            } else if (algorithm === 'unstructured') {
                const oracle = Array.from({ length: N }, (_, i) => solutions.includes(i) ? -1 : 1);
                const Uw = math.diag(oracle);
                const P0 = Array.from({ length: N }, () => Array(N).fill(1 / N));
                const Us = math.subtract(math.multiply(2, P0), math.eye(N));
                const G = math.multiply(Us, Uw);
                const eigs = math.eigs(G);
                const projections = eigs.vectors.map(v => math.dot(stateVector, v));
                const args = projections.map(p => math.arg(p));
                const mags = projections.map(p => math.pow(p.abs(), 2));

                data = [
                    {
                        x: args,
                        y: mags,
                        type: 'bar'
                    }
                ];
            } else {
                const conflictProbabilities = conflictNumbers.reduce((acc, c, i) => {
                    acc[c] = (acc[c] || 0) + probabilities[i];
                    return acc;
                }, {});

                data = [
                    {
                        x: Object.keys(conflictProbabilities).map(Number),
                        y: Object.values(conflictProbabilities),
                        type: 'bar'
                    }
                ];
            }

            const layout = {
                yaxis: { title: 'probability', range: [0, 1] },
                title: 'Probability Distribution'
            };

            if (plotMode === 'conflicts' || algorithm !== 'unstructured') {
                layout.xaxis = { title: 'number of conflicts' };
            } else {
                layout.xaxis = { title: 'arg(eigenvalue)' };
            }

            Plotly.newPlot('plot-probability', data, layout);
        }

        document.querySelectorAll('.control-panel button').forEach(btn => btn.addEventListener('click', () => {
            document.querySelectorAll('.control-panel button').forEach(b => b.classList.remove('active'));
            btn.classList.add('active');

            totalSteps = btn.id === 'btn-unstructured' ? 5 :
                         btn.id === 'btn-adiabatic' || btn.id === 'btn-discrete-adiabatic' ? 200 : 10;
            currentStep = 0;

            document.getElementById('slider-total-steps').value = totalSteps;
            document.getElementById('slider-current-step').max = totalSteps;
            document.getElementById('slider-current-step').value = currentStep;

            document.getElementById('display-total-steps').textContent = totalSteps;
            document.getElementById('label-current-step').textContent = `current step (0 to ${totalSteps}):`;
            document.getElementById('display-current-step').textContent = currentStep;

            update();
        }));

        document.getElementById('slider-total-steps').addEventListener('input', () => {
            totalSteps = parseInt(document.getElementById('slider-total-steps').value);
            document.getElementById('display-total-steps').textContent = totalSteps;
            document.getElementById('slider-current-step').max = totalSteps;
            if (currentStep > totalSteps) {
                currentStep = totalSteps;
                document.getElementById('slider-current-step').value = currentStep;
                document.getElementById('display-current-step').textContent = currentStep;
            }
            document.getElementById('label-current-step').textContent = `current step (0 to ${totalSteps}):`;
            update();
        });

        document.getElementById('slider-current-step').addEventListener('input', () => {
            currentStep = parseInt(document.getElementById('slider-current-step').value);
            document.getElementById('display-current-step').textContent = currentStep;
            update();
        });

        document.querySelectorAll('.plot-mode-buttons button').forEach(btn => btn.addEventListener('click', () => {
            document.querySelectorAll('.plot-mode-buttons button').forEach(b => b.classList.remove('active'));
            btn.classList.add('active');
            update();
        }));

        initialize();
    </script>
</body>
</html>
