</head>
<body>
    <div id="main-container">
        <div id="controls-container">
            <div id="control-group-pressure" class="control-group">
                <label for="slider-pressure">pressure (bar)</label>
                <input type="range" id="slider-pressure" min="0.01" max="0.20" step="0.005" value="0.08">
                <span id="value-pressure">0.08</span>
            </div>
            <div id="control-group-temperature" class="control-group" style="display: none;">
                <label for="slider-temperature">temperature (K)</label>
                <input type="range" id="slider-temperature" min="450" max="490" step="1" value="475">
                <span id="value-temperature">475</span>
            </div>
            <div class="control-group">
                <select id="select-plot">
                    <option value="vs_temp">fugacity versus temperature</option>
                    <option value="vs_press">fugacity versus pressure</option>
                </select>
            </div>
            <div id="checkbox-container" class="control-group" style="display: none;">
                <input type="checkbox" id="checkbox-high-pressure" checked>
                <label for="checkbox-high-pressure">high pressure</label>
            </div>
        </div>
        <div id="plot-container"></div>
    </div>

    <script>
        const plotContainer = document.getElementById('plot-container');
        const selectPlot = document.getElementById('select-plot');
        const sliderPressure = document.getElementById('slider-pressure');
        const sliderTemperature = document.getElementById('slider-temperature');
        const checkboxHighPressure = document.getElementById('checkbox-high-pressure');
        const valuePressure = document.getElementById('value-pressure');
        const valueTemperature = document.getElementById('value-temperature');

        function updatePlot() {
            const plotType = selectPlot.value;
            if (plotType === 'vs_temp') {
                const pressure = parseFloat(sliderPressure.value);
                const temperatureSat = (5818 / (8.65 - Math.log10(pressure))) - 230;
                const xLiquid = Array.from({ length: 121 }, (_, i) => 280 + i);
                const yLiquid = xLiquid.map(temp => Math.pow(10, 8.65 - 5818 / (230 + temp)));
                const xVapor = Array.from({ length: 121 }, (_, i) => 280 + i);
                const yVapor = xVapor.map(() => pressure);

                const traceStableLiquid = {
                    x: xLiquid.filter(temp => temp <= temperatureSat),
                    y: yLiquid.filter((_, i) => xLiquid[i] <= temperatureSat),
                    mode: 'lines',
                    name: 'Stable Liquid',
                    line: { color: '#0000FF' }
                };
                const traceStableVapor = {
                    x: xVapor.filter(temp => temp >= temperatureSat),
                    y: yVapor.filter((_, i) => xVapor[i] >= temperatureSat),
                    mode: 'lines',
                    name: 'Stable Vapor',
                    line: { color: '#0000FF' }
                };
                const traceMetastableLiquid = {
                    x: xLiquid.filter(temp => temp >= temperatureSat),
                    y: yLiquid.filter((_, i) => xLiquid[i] >= temperatureSat),
                    mode: 'lines',
                    name: 'Metastable Liquid',
                    line: { color: '#A0A0FF', dash: 'dash' }
                };
                const traceMetastableVapor = {
                    x: xVapor.filter(temp => temp <= temperatureSat),
                    y: yVapor.filter((_, i) => xVapor[i] <= temperatureSat),
                    mode: 'lines',
                    name: 'Metastable Vapor',
                    line: { color: '#A0A0FF', dash: 'dash' }
                };
                const traceSaturationPoint = {
                    x: [temperatureSat],
                    y: [pressure],
                    mode: 'markers',
                    name: 'Saturation Point',
                    marker: { color: '#000000', size: 8 }
                };
                const traceIndicatorLine = {
                    x: [temperatureSat, temperatureSat],
                    y: [0, pressure],
                    mode: 'lines',
                    name: 'Indicator Line',
                    line: { color: '#000000', dash: 'dash' }
                };

                const data = [traceStableLiquid, traceStableVapor, traceMetastableLiquid, traceMetastableVapor, traceSaturationPoint, traceIndicatorLine];
                const layout = {
                    title: 'Fugacity versus Temperature',
                    xaxis: { title: 'temperature (°C)', range: [280, 400], gridcolor: '#e0e0e0' },
                    yaxis: { title: 'fugacity (bar)', range: [0, 0.22], gridcolor: '#e0e0e0' },
                    paper_bgcolor: 'white',
                    font: { family: 'Arial, sans-serif', size: 14 }
                };

                Plotly.react(plotContainer, data, layout);
            } else if (plotType === 'vs_press') {
                const temperature = parseFloat(sliderTemperature.value);
                const pressureSat = Math.pow(10, 10.83 - 5047 / temperature);
                const fugacitySat = pressureSat * (checkboxHighPressure.checked ? Math.exp(-25 * pressureSat / temperature) : 1);
                const xVapor = Array.from({ length: 301 }, (_, i) => i * 0.01);
                const yVapor = xVapor.map(pressure => pressure * (checkboxHighPressure.checked ? Math.exp(-25 * pressure / temperature) : 1));

                const traceStableVapor = {
                    x: xVapor.filter(pressure => pressure <= pressureSat),
                    y: yVapor.filter((_, i) => xVapor[i] <= pressureSat),
                    mode: 'lines',
                    name: 'Stable Vapor',
                    line: { color: '#0000FF' }
                };
                const traceStableLiquid = {
                    x: xVapor.filter(pressure => pressure >= pressureSat),
                    y: yVapor.filter((_, i) => xVapor[i] >= pressureSat).map(() => fugacitySat),
                    mode: 'lines',
                    name: 'Stable Liquid',
                    line: { color: '#0000FF' }
                };
                const traceMetastableVapor = {
                    x: xVapor.filter(pressure => pressure >= pressureSat),
                    y: yVapor.filter((_, i) => xVapor[i] >= pressureSat),
                    mode: 'lines',
                    name: 'Metastable Vapor',
                    line: { color: '#A0A0FF', dash: 'dash' }
                };
                const traceMetastableLiquid = {
                    x: xVapor.filter(pressure => pressure <= pressureSat),
                    y: yVapor.filter((_, i) => xVapor[i] <= pressureSat).map(() => fugacitySat),
                    mode: 'lines',
                    name: 'Metastable Liquid',
                    line: { color: '#A0A0FF', dash: 'dash' }
                };
                const traceSaturationPoint = {
                    x: [pressureSat],
                    y: [fugacitySat],
                    mode: 'markers',
                    name: 'Saturation Point',
                    marker: { color: '#000000', size: 8 }
                };
                const traceIndicatorLine = {
                    x: [pressureSat, pressureSat],
                    y: [0, fugacitySat],
                    mode: 'lines',
                    name: 'Indicator Line',
                    line: { color: '#000000', dash: 'dash' }
                };

                const data = [traceStableVapor, traceStableLiquid, traceMetastableVapor, traceMetastableLiquid, traceSaturationPoint, traceIndicatorLine];
                const layout = {
                    title: 'Fugacity versus Pressure',
                    xaxis: { title: 'pressure (MPa)', range: [0, 3.0], gridcolor: '#e0e0e0' },
                    yaxis: { title: 'fugacity (MPa)', range: [0, 3.0], gridcolor: '#e0e0e0' },
                    paper_bgcolor: 'white',
                    font: { family: 'Arial, sans-serif', size: 14 }
                };

                Plotly.react(plotContainer, data, layout);
            }
        }

        selectPlot.addEventListener('change', () => {
            if (selectPlot.value === 'vs_temp') {
                document.getElementById('control-group-pressure').style.display = 'block';
                document.getElementById('control-group-temperature').style.display = 'none';
                document.getElementById('checkbox-container').style.display = 'none';
            } else if (selectPlot.value === 'vs_press') {
                document.getElementById('control-group-pressure').style.display = 'none';
                document.getElementById('control-group-temperature').style.display = 'block';
                document.getElementById('checkbox-container').style.display = 'block';
            }
            updatePlot();
        });

        sliderPressure.addEventListener('input', () => {
            valuePressure.textContent = parseFloat(sliderPressure.value).toFixed(2);
            updatePlot();
        });

        sliderTemperature.addEventListener('input', () => {
            valueTemperature.textContent = parseInt(sliderTemperature.value);
            updatePlot();
        });

        checkboxHighPressure.addEventListener('change', updatePlot);

        // Initial plot
        updatePlot();
    </script>
</body>
</html>
