    <script>
        let theta = 0.44879;
        let c = { x: 0.3, y: 0.7 };
        let d_opt = { x: 0, y: 0 };
        const R = 150;

        function setup() {
            createCanvas(400, 400);
            translate(width / 2, height / 2);
            updateTheta();
        }

        function draw() {
            background(255);
            noFill();
