            if (document.getElementById('checkbox-optimal-pair').checked) {
                drawVector('c_opt', (sin(2 * theta) / 2) * R, (sin(2 * theta) / 2) * R, 'purple');
                drawVector('d_opt', (sin(2 * theta) / 2) * R, (sin(2 * theta) / 2) * R, 'purple');
            }

            if (document.getElementById('checkbox-optimal-region').checked) {
                noFill();
                stroke(0, 255, 0, 100);
                drawingContext.setLineDash([5, 5]);
                ellipse(0, 0, (sin(2 * theta) / 2) * R * 2);
                drawingContext.setLineDash([]);

                stroke(255, 165, 0, 100);
                drawingContext.setLineDash([5, 5]);
                ellipse(0, 0, (sin(2 * theta) / 2) * R * 2);
                drawingContext.setLineDash([]);
            }
        }

        function drawVector(label, x, y, color) {
            stroke(color);
            fill(color);
            line(0, 0, x, y);
            push();
            translate(x, y);
            rotate(atan2(y, x));
            triangle(0, 0, -5, -5, -5, 5);
            pop();
            text(label, x + 10, y);
        }

        function updateTheta() {
            theta = parseFloat(document.getElementById('slider-theta').value);
            document.getElementById('span-theta-value').innerText = theta.toFixed(5);
            document.getElementById('span-sinsq-theta-value').innerText = (Math.sin(theta) ** 2).toFixed(5);

            c = { x: 0.3, y: 0.7 }; // Reset c to default position
            calculateDopt();
            updatePlot();
        }

        function updatePlot() {
            const xData = [];
            const yData = [];
            const sin2theta = Math.sin(2 * theta);
            for (let x = 0; x <= sin2theta; x += 0.01) {
                xData.push(x);
                yData.push((sin2theta - Math.sqrt(x)) ** 2);
            }

            const trace1 = {
                x: xData,
                y: yData,
                mode: 'lines',
                name: 'Tradeoff Curve',
                line: { color: 'blue' }
            };

            const D_CA = 0.5 * (dist(0, 0, c.x * R, c.y * R) - dist(R * sin(theta), R * cos(theta), c.x * R, c.y * R)) ** 2;
            const D_DB = 0.5 * (dist(0, 0, d_opt.x * R, d_opt.y * R) - dist(-R * sin(theta), R * cos(theta), d_opt.x * R, d_opt.y * R)) ** 2;

            const trace2 = {
                x: [D_CA],
                y: [D_DB],
                mode: 'markers',
                name: 'Current Error',
                marker: { color: 'red' }
            };

            const optimalX = (sin2theta / 2) ** 2;
            const optimalY = (sin2theta / 2) ** 2;

            const trace3 = {
                x: document.getElementById('checkbox-optimal-pair').checked ? [optimalX] : [],
                y: document.getElementById('checkbox-optimal-pair').checked ? [optimalY] : [],
                mode: 'markers',
                name: 'Optimal Error',
                marker: { color: 'green' }
            };

            Plotly.newPlot('plot-error', [trace1, trace2, trace3], {
                title: 'Error Tradeoff',
                xaxis: { title: 'D(C, A)', range: [0, 1.5] },
                yaxis: { title: 'D(D, B)', range: [0, 1.5] }
            });
        }

        function calculateDopt() {
            const normC = Math.sqrt(c.x ** 2 + c.y ** 2);
            const parallelB = (-R * sin(theta) * c.x + R * cos(theta) * c.y) / normC;
            const perpB = (R * cos(theta) * c.x + R * sin(theta) * c.y) / normC;

            const dParallel = parallelB / Math.sqrt(1 - normC ** 2);
            const dPerp = perpB;

            d_opt.x = dParallel / Math.sqrt(dParallel ** 2 + dPerp ** 2) * R;
            d_opt.y = dPerp / Math.sqrt(dParallel ** 2 + dPerp ** 2) * R;
        }

        function mousePressed() {
            const mousePos = getMousePos();
            const distToC = dist(mousePos.x, mousePos.y, c.x * R, c.y * R);
            if (distToC < 10) {
                isDragging = true;
            }
        }

        function mouseDragged() {
            if (isDragging) {
                const mousePos = getMousePos();
                const normMouse = Math.sqrt(mousePos.x ** 2 + mousePos.y ** 2);
                if (normMouse <= R) {
                    c.x = mousePos.x / R;
                    c.y = mousePos.y / R;
                } else {
                    c.x = mousePos.x / normMouse;
                    c.y = mousePos.y / normMouse;
                }
                calculateDopt();
                updatePlot();
            }
        }

        function mouseReleased() {
            isDragging = false;
        }

        function getMousePos() {
            return {
                x: mouseX - width / 2,
                y: mouseY - height / 2
            };
        }

        document.getElementById('slider-theta').addEventListener('input', updateTheta);
        document.getElementById('checkbox-metric-circle').addEventListener('change', draw);
        document.getElementById('checkbox-metric-region').addEventListener('change', draw);
        document.getElementById('checkbox-first-order-region').addEventListener('change', draw);
        document.getElementById('checkbox-first-order-vec').addEventListener('change', draw);
        document.getElementById('checkbox-optimal-pair').addEventListener('change', draw);
        document.getElementById('checkbox-optimal-region').addEventListener('change', draw);

        let isDragging = false;
    </script>
</body>
</html>
