<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Temperature Distribution Simulation</title>
    <style>
        #app-container {
            display: flex;
            flex-direction: column;
            align-items: center;
            padding: 20px;
        }
        #controls-container {
            background-color: #f4f4f4;
            padding: 10px;
            margin-bottom: 20px;
            width: 100%;
            max-width: 600px;
            box-sizing: border-box;
        }
        .control-row {
            display: flex;
            align-items: center;
            justify-content: space-between;
            margin-bottom: 10px;
        }
        #plot-container {
            border: 1px solid #ddd;
            width: 100%;
            max-width: 800px;
        }
    </style>
    <script src="https://cdn.plot.ly/plotly-latest.min.js"></script>
</head>
<body>
    <div id="app-container">
        <div id="controls-container">
            <div class="control-row">
                <label for="slider-points">collocation points</label>
                <input type="range" id="slider-points" min="5" max="30" step="1" value="20">
                <span id="label-points">20</span>
            </div>
            <div class="control-row">
                <label for="slider-time">t</label>
                <input type="range" id="slider-time" min="0.001" max="0.2" step="0.001" value="0.05">
                <span id="label-time">0.050</span>
            </div>
        </div>
        <div id="plot-container"></div>
    </div>

    <script>
        const sliderPoints = document.getElementById('slider-points');
        const sliderTime = document.getElementById('slider-time');
        const labelPoints = document.getElementById('label-points');
        const labelTime = document.getElementById('label-time');
        const plotContainer = document.getElementById('plot-container');

        // Function to calculate T(x, t) using the Fourier series
        function calculateTemperature(x, t) {
            let T = 0;
            const nMax = 100; // Number of terms in the Fourier series
            for (let n = 1; n <= nMax; n++) {
                T += (8 / Math.PI**2) * (1 / n**2) * Math.sin(n * Math.PI / 2) * Math.sin(n * Math.PI * x) * Math.exp(-2 * n**2 * Math.PI**2 * t);
            }
            return T;
        }

        // Function to generate Chebyshev-Gauss-Lobatto points
        function generateCollocationPoints(numPoints) {
            const N = numPoints - 1;
            const points = [];
            for (let j = 0; j <= N; j++) {
                points.push((Math.cos(j * Math.PI / N) + 1) / 2);
            }
            return points;
        }

        // Function to update the plot
        function updatePlot() {
            const numPoints = parseInt(sliderPoints.value);
            const t = parseFloat(sliderTime.value);

            // Generate x values for the analytical solution
            const xValues = Array.from({ length: 200 }, (_, i) => i / 199);
            const yValuesAnalytical = xValues.map(x => calculateTemperature(x, t));

            // Generate collocation points and corresponding temperatures
            const collocationPoints = generateCollocationPoints(numPoints);
            const yValuesNumerical = collocationPoints.map(x => calculateTemperature(x, t));

            // Create traces
            const traceAnalytical = {
                x: xValues,
                y: yValuesAnalytical,
                type: 'scatter',
                mode: 'lines',
                line: { color: 'blue', width: 3 }
            };

            const traceNumerical = {
                x: collocationPoints,
                y: yValuesNumerical,
                type: 'scatter',
                mode: 'markers',
                marker: { color: 'red', size: 8 }
            };

            // Define layout
            const layout = {
                xaxis: { title: 'x', range: [0, 1], tickmode: 'linear', dtick: 0.2 },
                yaxis: { title: 'T', range: [-0.1, 1.1], tickmode: 'linear', dtick: 0.2 },
                showlegend: false,
                grid: { x: { color: '#ddd' }, y: { color: '#ddd' } },
                margin: { t: 20, r: 20, b: 40, l: 40 }
            };

            // Update the plot
            Plotly.react(plotContainer, [traceAnalytical, traceNumerical], layout);
        }

        // Initialize the plot
        updatePlot();

        // Event listeners for sliders
        sliderPoints.addEventListener('input', () => {
            labelPoints.textContent = sliderPoints.value;
            updatePlot();
        });

        sliderTime.addEventListener('input', () => {
            labelTime.textContent = parseFloat(sliderTime.value).toFixed(3);
            updatePlot();
        });
    </script>
</body>
</html>
