                </div>
                <div class="slider-group">
                    <label for="slider-sd-z">Z</label>
                    <input type="range" id="slider-sd-z" min="1" max="10" step="0.1" value="1">
                    <span id="value-sd-z">1</span>
                </div>
            </div>
        </div>
        <div id="visualization-panel"></div>
    </div>

    <script>
        let means = { w: 0, x: 0, y: 0, z: 0 };
        let sds = { w: 1, x: 1, y: 1, z: 1 };
        let grandMean, msBetween, msWithin, fRatio;

        function setup() {
            const canvas = createCanvas(600, 400);
            canvas.parent('visualization-panel');
            noLoop();
            update();
        }

        function draw() {
            background(255);

            // Group Data Plot (Top Half)
            push();
            translate(50, 100);
            const yScale = 20; // Scale factor for y-axis
            stroke(0);
            line(0, -10 * yScale, 0, 10 * yScale); // y-axis
            for (let i = -10; i <= 10; i += 5) {
                line(-5, i * yScale, 5, i * yScale);
                text(i, -20, i * yScale);
            }
            line(0, 0, 400, 0); // x-axis
            text("W", 0, 20);
            text("X", 100, 20);
            text("Y", 200, 20);
            text("Z", 300, 20);

            // Grand Mean Line
            stroke(0);
            strokeDashArray([5, 5]);
            line(0, grandMean * yScale, 400, grandMean * yScale);

            // Group Bars
            const groups = ['w', 'x', 'y', 'z'];
            const colors = ['#F57F29', '#F57F29', '#F57F29', '#F57F29'];
            for (let i = 0; i < groups.length; i++) {
                fill(colors[i]);
                rect(i * 100, (means[groups[i]] - sds[groups[i]]) * yScale, 80, sds[groups[i]] * 2 * yScale);
                stroke(0);
                line(i * 100, means[groups[i]] * yScale, i * 100 + 80, means[groups[i]] * yScale);
            }

            // F-Ratio Text
            textSize(16);
            text(`F ratio = ${fRatio.toFixed(fRatio < 1 ? 4 : 2)}`, 20, -30);
            pop();

            // ANOVA Results Plot (Bottom Half)
            push();
            translate(50, 300);
            const xScale = 1; // Scale factor for x-axis
            stroke(0);
            line(0, 0, 200 * xScale, 0); // x-axis
            for (let i = 0; i <= 200; i += 50) {
                line(i * xScale, -5, i * xScale, 5);
                text(i, i * xScale - 10, 20);
            }
            line(0, 0, 0, -100); // y-axis
            text("MS Between", 220, -20);
            text("MS Within", 220, -60);
            text("F-Ratio", 220, -100);

            // Bars
            const stats = [msBetween, msWithin, fRatio];
            const statColors = ['#4691A5', '#D49D3C', '#8C3A8D'];
            for (let i = 0; i < stats.length; i++) {
                fill(statColors[i]);
                rect(0, -20 * (i + 1), min(stats[i] * xScale, 200 * xScale), 15);
            }
            pop();
        }

        function update() {
            // Read Slider Values
            means.w = parseFloat(document.getElementById('slider-mean-w').value);
            means.x = parseFloat(document.getElementById('slider-mean-x').value);
            means.y = parseFloat(document.getElementById('slider-mean-y').value);
            means.z = parseFloat(document.getElementById('slider-mean-z').value);
            sds.w = parseFloat(document.getElementById('slider-sd-w').value);
            sds.x = parseFloat(document.getElementById('slider-sd-x').value);
            sds.y = parseFloat(document.getElementById('slider-sd-y').value);
            sds.z = parseFloat(document.getElementById('slider-sd-z').value);

            // Update Slider Value Displays
            document.getElementById('value-mean-w').textContent = means.w.toFixed(1);
            document.getElementById('value-mean-x').textContent = means.x.toFixed(1);
            document.getElementById('value-mean-y').textContent = means.y.toFixed(1);
            document.getElementById('value-mean-z').textContent = means.z.toFixed(1);
            document.getElementById('value-sd-w').textContent = sds.w.toFixed(1);
            document.getElementById('value-sd-x').textContent = sds.x.toFixed(1);
            document.getElementById('value-sd-y').textContent = sds.y.toFixed(1);
            document.getElementById('value-sd-z').textContent = sds.z.toFixed(1);

            // Perform ANOVA Calculation
            const k = 4; // Number of groups
            const n = 10; // Number of subjects per group

            // Grand Mean
            grandMean = (means.w + means.x + means.y + means.z) / k;

            // Sum of Squares Between (SS_between)
            const ssBetween = n * ((means.w - grandMean) ** 2 + (means.x - grandMean) ** 2 + (means.y - grandMean) ** 2 + (means.z - grandMean) ** 2);

            // Mean Square Between (MS_between)
            msBetween = ssBetween / (k - 1);

            // Sum of Squares Within (SS_within)
            const ssWithin = (sds.w ** 2 * (n - 1)) + (sds.x ** 2 * (n - 1)) + (sds.y ** 2 * (n - 1)) + (sds.z ** 2 * (n - 1));

            // Mean Square Within (MS_within)
            msWithin = ssWithin / (n * k - k);

            // F-Ratio
            fRatio = msBetween / msWithin;

            // Trigger Redraw
            redraw();
        }

        document.querySelectorAll('input[type="range"]').forEach(slider => {
            slider.addEventListener('input', update);
        });

        // Initial State
        update();
    </script>
</body>
</html>
