            <input type="range" id="slider-sigma1" min="0.1" max="0.5" step="0.01" value="0.198">
            <input type="text" id="display-sigma1" readonly value="0.198">
        </div>
        <div class="slider-container">
            <label for="slider-xi1">shape ξ₁</label>
            <input type="range" id="slider-xi1" min="-0.5" max="0.5" step="0.01" value="-0.05">
            <input type="text" id="display-xi1" readonly value="-0.05">
        </div>

        <h3 style="color: red;">GEV distribution 2</h3>
        <div class="slider-container">
            <label for="slider-mu2">location μ₂</label>
            <input type="range" id="slider-mu2" min="2" max="6" step="0.01" value="3.87">
            <input type="text" id="display-mu2" readonly value="3.87">
        </div>
        <div class="slider-container">
            <label for="slider-sigma2">scale σ₂</label>
            <input type="range" id="slider-sigma2" min="0.1" max="0.5" step="0.01" value="0.3">
            <input type="text" id="display-sigma2" readonly value="0.3">
        </div>
        <div class="slider-container">
            <label for="slider-xi2">shape ξ₂</label>
            <input type="range" id="slider-xi2" min="-0.5" max="0.5" step="0.01" value="-0.05">
            <input type="text" id="display-xi2" readonly value="-0.05">
        </div>

        <h3>Return level curves</h3>
        <div class="slider-container">
            <label for="slider-pmax">P_max</label>
            <input type="range" id="slider-pmax" min="10" max="1000" step="1" value="100">
            <input type="text" id="display-pmax" readonly value="100">
        </div>
        <div id="plot-return-level" style="width: 100%; height: 400px;"></div>
    </div>
    <div id="pdf-plot-area">
        <div id="plot-pdf" style="width: 100%; height: 800px;"></div>
    </div>

    <script>
        const mu1Slider = document.getElementById('slider-mu1');
        const sigma1Slider = document.getElementById('slider-sigma1');
        const xi1Slider = document.getElementById('slider-xi1');
        const mu2Slider = document.getElementById('slider-mu2');
        const sigma2Slider = document.getElementById('slider-sigma2');
        const xi2Slider = document.getElementById('slider-xi2');
        const pmaxSlider = document.getElementById('slider-pmax');

        const mu1Display = document.getElementById('display-mu1');
        const sigma1Display = document.getElementById('display-sigma1');
        const xi1Display = document.getElementById('display-xi1');
        const mu2Display = document.getElementById('display-mu2');
        const sigma2Display = document.getElementById('display-sigma2');
        const xi2Display = document.getElementById('display-xi2');
        const pmaxDisplay = document.getElementById('display-pmax');

        const pdfPlotDiv = document.getElementById('plot-pdf');
        const returnLevelPlotDiv = document.getElementById('plot-return-level');

        function gevPdf(x, mu, sigma, xi) {
            if (xi === 0) {
                return (1 / sigma) * Math.exp(-(x - mu) / sigma) * Math.exp(-Math.exp(-(x - mu) / sigma));
            } else {
                const t = Math.pow(1 + xi * ((x - mu) / sigma), -1 / xi);
                return (1 / sigma) * Math.pow(t, xi + 1) * Math.exp(-t);
            }
        }

        function gevReturnLevel(p, mu, sigma, xi) {
            if (xi === 0) {
                return mu - sigma * Math.log(-Math.log(1 - 1 / p));
            } else {
                return mu - (sigma / xi) * (1 - Math.pow(-Math.log(1 - 1 / p), -xi));
            }
        }

        function update() {
            const mu1 = parseFloat(mu1Slider.value);
            const sigma1 = parseFloat(sigma1Slider.value);
            const xi1 = parseFloat(xi1Slider.value);
            const mu2 = parseFloat(mu2Slider.value);
            const sigma2 = parseFloat(sigma2Slider.value);
            const xi2 = parseFloat(xi2Slider.value);
            const pmax = parseInt(pmaxSlider.value);

            mu1Display.value = mu1.toFixed(2);
            sigma1Display.value = sigma1.toFixed(2);
            xi1Display.value = xi1.toFixed(2);
            mu2Display.value = mu2.toFixed(2);
            sigma2Display.value = sigma2.toFixed(2);
            xi2Display.value = xi2.toFixed(2);
            pmaxDisplay.value = pmax;

            const xValues = [];
            const yValues1 = [];
            const yValues2 = [];
            for (let x = 2.5; x <= 7; x += 0.01) {
                xValues.push(x);
                yValues1.push(gevPdf(x, mu1, sigma1, xi1));
                yValues2.push(gevPdf(x, mu2, sigma2, xi2));
            }

            const pdfData = [
                { x: xValues, y: yValues1, mode: 'lines', fill: 'tozeroy', line: { color: 'blue' }, fillcolor: 'rgba(0, 0, 255, 0.2)', name: 'GEV 1' },
                { x: xValues, y: yValues2, mode: 'lines', fill: 'tozeroy', line: { color: 'red' }, fillcolor: 'rgba(255, 0, 0, 0.2)', name: 'GEV 2' }
            ];

            const pdfLayout = {
                title: 'PDFs of GEV Distributions 1 and 2',
                xaxis: { range: [2.5, 7], title: '' },
                yaxis: { title: '' },
                margin: { t: 50 }
            };

            Plotly.newPlot(pdfPlotDiv, pdfData, pdfLayout);

            const returnPeriods = [];
            const returnLevels1 = [];
            const returnLevels2 = [];
            for (let p = 1; p <= pmax; p++) {
                returnPeriods.push(p);
                returnLevels1.push(gevReturnLevel(p, mu1, sigma1, xi1));
                returnLevels2.push(gevReturnLevel(p, mu2, sigma2, xi2));
            }

            const returnLevelData = [
                { x: returnPeriods, y: returnLevels1, mode: 'lines', line: { color: 'blue' }, name: 'GEV 1' },
                { x: returnPeriods, y: returnLevels2, mode: 'lines', line: { color: 'red' }, name: 'GEV 2' }
            ];

            const returnLevelLayout = {
                xaxis: { range: [0, pmax], title: 'Return Period (years)' },
                yaxis: { title: 'Maximum Sea Level (meters)' },
                margin: { t: 50 }
            };

            Plotly.newPlot(returnLevelPlotDiv, returnLevelData, returnLevelLayout);
        }

        mu1Slider.addEventListener('input', update);
        sigma1Slider.addEventListener('input', update);
        xi1Slider.addEventListener('input', update);
        mu2Slider.addEventListener('input', update);
        sigma2Slider.addEventListener('input', update);
        xi2Slider.addEventListener('input', update);
        pmaxSlider.addEventListener('input', update);

        update();
    </script>
</body>
</html>
