        UH: [
            [[1, 0], [1, 4], [1.5, 4], [1.5, 1], [2.5, 1], [2.5, 4], [3, 4], [3, 0]],
            [[4, 0], [4, 4], [4.5, 4], [4.5, 2], [5.5, 2], [5.5, 4], [6, 4], [6, 0]]
        ]
    };

    let a = 2;
    let b = 4;
    let T = b / a;

    const plotTop = document.getElementById('plot-top');
    const plotBottom = document.getElementById('plot-bottom');

    function createTrace(data, color, name) {
        return {
            x: data.map(d => d[0]),
            y: data.map(d => d[1]),
            mode: 'lines',
            line: { color, width: 2.5 },
            name
        };
    }

    function updatePlots() {
        T = b / a;
        const tValues = Array.from({ length: 301 }, (_, i) => -10 + i * 0.1);
        const xData = signals[selectedSignal].flat().length === 8 ? signals[selectedSignal][0] : signals[selectedSignal];
        const x = tValues.map(t => xData.find(([tx]) => tx >= t) || xData[xData.length - 1])[1];

        const yData = tValues.map(t => xData.find(([tx]) => tx >= a * t - b) || xData[xData.length - 1])[1];
        const zData = tValues.map(t => xData.find(([tx]) => tx >= t - b) || xData[xData.length - 1])[1];
        const wData = tValues.map(t => xData.find(([tx]) => tx >= a * t) || xData[xData.length - 1])[1];

        const xTrace = createTrace(xData, 'rgb(70, 130, 180)', '\(x(t)\)');
        const yTrace = createTrace(tValues.map((_, i) => [tValues[i], yData[i]]), 'red', '\(y(t) = x(at-b) = x(a(t-T))\)');
        const zTrace = createTrace(tValues.map((_, i) => [tValues[i], zData[i]]), 'olivedrab', '\(z(t) = x(t-b)\)');
        const wTrace = createTrace(tValues.map((_, i) => [tValues[i], wData[i]]), 'saddlebrown', '\(w(t) = x(at)\)');

        const layoutTop = {
            title: 'signal',
            xaxis: { title: 'time (s)', range: [-10, 20], zeroline: true },
            yaxis: { range: [-0.5, 4.5], zeroline: true },
            showlegend: true,
            legend: { x: 0.01, y: 0.75 },
            shapes: [
                { type: 'circle', xref: 'x', yref: 'y', x0: T - 0.1, y0: -0.1, x1: T + 0.1, y1: 0.1, fillcolor: 'red', line: { color: 'red' } },
                { type: 'circle', xref: 'x', yref: 'y', x0: b - 0.1, y0: -0.1, x1: b + 0.1, y1: 0.1, fillcolor: 'green', line: { color: 'green' } }
            ],
            annotations: [
                { x: T, y: 0, text: 'T', xanchor: 'center', yanchor: 'bottom', showarrow: false, font: { color: 'red' } },
                { x: b, y: 0, text: 'b', xanchor: 'center', yanchor: 'bottom', showarrow: false, font: { color: 'green' } }
            ]
        };

        const layoutBottom = {
            xaxis: { title: 'time (s)', range: [-10, 20], zeroline: true },
            yaxis: { range: [-0.5, 4.5], zeroline: true },
            showlegend: true,
            legend: { x: 0.01, y: 0.98 },
            shapes: layoutTop.shapes,
            annotations: layoutTop.annotations
        };

        Plotly.newPlot(plotTop, [xTrace], layoutTop);

        const visibleTraces = {
            'check-x': xTrace,
            'check-y': yTrace,
            'check-z': zTrace,
            'check-w': wTrace
        };

        const bottomData = Object.keys(visibleTraces).filter(id => document.getElementById(id).checked).map(id => visibleTraces[id]);
        Plotly.newPlot(plotBottom, bottomData, layoutBottom);
    }

    let selectedSignal = 'Triangle';

    document.querySelectorAll('#control-panel button').forEach(btn => {
        btn.addEventListener('click', () => {
            document.querySelector('#control-panel .active').classList.remove('active');
            btn.classList.add('active');
            selectedSignal = btn.id.replace('btn-', '');
            updatePlots();
        });
    });

    document.getElementById('slider-a').addEventListener('input', (e) => {
        a = parseFloat(e.target.value);
        document.getElementById('slider-a-value').textContent = a.toFixed(1);
        document.getElementById('slider-t').value = (b / a).toFixed(1);
        document.getElementById('slider-t-value').textContent = (b / a).toFixed(1);
        T = b / a;
        updatePlots();
    });

    document.getElementById('slider-b').addEventListener('input', (e) => {
        b = parseFloat(e.target.value);
        document.getElementById('slider-b-value').textContent = b.toFixed(1);
        document.getElementById('slider-t').value = (b / a).toFixed(1);
        document.getElementById('slider-t-value').textContent = (b / a).toFixed(1);
        T = b / a;
        updatePlots();
    });

    document.getElementById('slider-t').addEventListener('input', (e) => {
        T = parseFloat(e.target.value);
        document.getElementById('slider-t-value').textContent = T.toFixed(1);
        b = a * T;
        document.getElementById('slider-b').value = b.toFixed(1);
        document.getElementById('slider-b-value').textContent = b.toFixed(1);
        updatePlots();
    });

    document.querySelectorAll('#control-panel input[type="checkbox"]').forEach(cb => {
        cb.addEventListener('change', () => updatePlots());
    });

    updatePlots();
</script>
</body>
</html>
