    <script src="https://cdn.jsdelivr.net/npm/three@0.128.0/examples/js/controls/OrbitControls.js"></script>
    <script>
        const scene = new THREE.Scene();
        scene.background = new THREE.Color(0xf0f0f0);

        const camera = new THREE.PerspectiveCamera(75, window.innerWidth / window.innerHeight, 0.1, 1000);
        camera.position.z = 5;

        const renderer = new THREE.WebGLRenderer({ canvas: document.getElementById('main-canvas') });
        renderer.setSize(window.innerWidth, window.innerHeight);

        const controls = new THREE.OrbitControls(camera, renderer.domElement);

        const hemisphereLight = new THREE.HemisphereLight(0xffffff, 0x444444, 0.8);
        scene.add(hemisphereLight);

        const directionalLight = new THREE.DirectionalLight(0xffffff, 0.8);
        directionalLight.position.set(1, 1, 1).normalize();
        scene.add(directionalLight);

        const latticeGroup = new THREE.Group();
        scene.add(latticeGroup);

        function createCurvedTriangle(trim, colorStart, colorEnd) {
            const geometry = new THREE.BufferGeometry();
            const vertices = [];
            const colors = [];

            const radius = 1;
            const angleStep = (2 * Math.PI) / 3;
            const angles = [0, angleStep, 2 * angleStep];

            for (let i = 0; i < 3; i++) {
                const angle = angles[i];
                const nextAngle = angles[(i + 1) % 3];
                const numSegments = 20;

                vertices.push(
                    Math.sin(angle) * radius,
                    Math.cos(angle) * radius,
                    0
                );

                colors.push(...colorStart.toArray());

                for (let j = 1; j <= numSegments; j++) {
                    const t = j / numSegments;
                    const theta = angle + (nextAngle - angle) * t;
                    const u = 1 - t;

                    const x = (Math.pow(u, trim) + Math.pow(t, trim)) * Math.sin(theta);
                    const y = (Math.pow(u, trim) + Math.pow(t, trim)) * Math.cos(theta);
                    const z = Math.pow(u, trim) * Math.pow(t, trim);

                    vertices.push(x, y, z);

                    const color = new THREE.Color().lerpColors(colorStart, colorEnd, t);
                    colors.push(...color.toArray());
                }
            }

            const indices = [];
            for (let i = 0; i < 3; i++) {
                indices.push(0, i + 1, i + 2);
            }

            geometry.setAttribute('position', new THREE.Float32BufferAttribute(vertices, 3));
            geometry.setAttribute('color', new THREE.Float32BufferAttribute(colors, 3));
            geometry.setIndex(indices);

            const material = new THREE.MeshStandardMaterial({ vertexColors: true });
            const mesh = new THREE.Mesh(geometry, material);

            const outlineGeometry = new THREE.EdgesGeometry(geometry);
            const outlineMaterial = new THREE.LineBasicMaterial({ color: 0x000000, linewidth: 2 });
            const outline = new THREE.LineSegments(outlineGeometry, outlineMaterial);

            return { mesh, outline };
        }

        function updateScene() {
            latticeGroup.clear();

            const unitsX = parseInt(document.querySelector('input[name="unitsX"]:checked').value);
            const unitsY = parseInt(document.querySelector('input[name="unitsY"]:checked').value);
            const trim = parseFloat(document.getElementById('slider-trim').value);
            const double = document.getElementById('checkbox-double').checked;
            const sphere = document.getElementById('checkbox-sphere').checked;
            const triangles = document.getElementById('checkbox-triangles').checked;

            const basisVector1 = new THREE.Vector3(Math.sqrt(3) / 2, -0.5, 0);
            const basisVector2 = new THREE.Vector3(0, 1, 0);

            for (let i = -unitsX; i <= unitsX; i++) {
                for (let j = -unitsY; j <= unitsY; j++) {
                    const offset = new THREE.Vector3().addScaledVector(basisVector1, i).addScaledVector(basisVector2, j);
                    const rotation = (i + j) % 2 === 0 ? 0 : Math.PI;

                    const { mesh, outline } = createCurvedTriangle(trim, new THREE.Color(0xffc0cb), new THREE.Color(0x904090));
                    mesh.rotation.z = rotation;
                    mesh.position.copy(offset);
                    outline.rotation.z = rotation;
                    outline.position.copy(offset);

                    latticeGroup.add(mesh);
                    latticeGroup.add(outline);

                    if (double) {
                        const doubleOffset = new THREE.Vector3().addVectors(offset, basisVector1.clone().add(basisVector2).multiplyScalar(0.5));
                        const doubleRotation = rotation + Math.PI;

                        const { mesh: doubleMesh, outline: doubleOutline } = createCurvedTriangle(trim, new THREE.Color(0x80ffff), new THREE.Color(0x008080));
                        doubleMesh.rotation.z = doubleRotation;
                        doubleMesh.position.copy(doubleOffset);
                        doubleOutline.rotation.z = doubleRotation;
                        doubleOutline.position.copy(doubleOffset);

                        latticeGroup.add(doubleMesh);
                        latticeGroup.add(doubleOutline);
                    }
                }
            }

            if (sphere) {
                const sphereGeometry = new THREE.SphereGeometry(1, 32, 32);
                const sphereMaterial = new THREE.MeshPhongMaterial({ color: 0xdddddd, shininess: 80 });
                const sphereMesh = new THREE.Mesh(sphereGeometry, sphereMaterial);
                sphereMesh.position.set(0, 0, -1);
                latticeGroup.add(sphereMesh);
            }

            if (triangles) {
                const triangleVertices = [];
                const triangleIndices = [];
                const pointPositions = [];

                for (let i = -unitsX; i <= unitsX; i++) {
                    for (let j = -unitsY; j <= unitsY; j++) {
                        const offset = new THREE.Vector3().addScaledVector(basisVector1, i).addScaledVector(basisVector2, j);
                        const rotation = (i + j) % 2 === 0 ? 0 : Math.PI;

                        const angles = [0, (2 * Math.PI) / 3, (4 * Math.PI) / 3];
                        const vertices = angles.map(angle => new THREE.Vector3(Math.sin(angle), Math.cos(angle), 0));

                        vertices.forEach(vertex => {
                            vertex.applyAxisAngle(new THREE.Vector3(0, 0, 1), rotation);
                            vertex.add(offset);
                            triangleVertices.push(vertex.x, vertex.y, vertex.z);
                            pointPositions.push(vertex.x, vertex.y, vertex.z);
                        });

                        const baseIndex = triangleVertices.length / 3 - 3;
                        triangleIndices.push(baseIndex, baseIndex + 1, baseIndex + 2);
                    }
                }

                const triangleGeometry = new THREE.BufferGeometry();
                triangleGeometry.setAttribute('position', new THREE.Float32BufferAttribute(triangleVertices, 3));
                triangleGeometry.setIndex(triangleIndices);

                const triangleMaterial = new THREE.MeshBasicMaterial({ color: 0x800080, transparent: true, opacity: 0.4 });
                const triangleMesh = new THREE.Mesh(triangleGeometry, triangleMaterial);
                latticeGroup.add(triangleMesh);

                const pointsGeometry = new THREE.BufferGeometry();
                pointsGeometry.setAttribute('position', new THREE.Float32BufferAttribute(pointPositions, 3));

                const pointsMaterial = new THREE.PointsMaterial({ color: 0x000000, size: 3 });
                const points = new THREE.Points(pointsGeometry, pointsMaterial);
                latticeGroup.add(points);
            }
        }

        document.querySelectorAll('input[name="unitsX"], input[name="unitsY"], #slider-trim, #checkbox-double, #checkbox-sphere, #checkbox-triangles')
            .forEach(input => input.addEventListener('change', updateScene));
        document.getElementById('slider-trim').addEventListener('input', () => {
            const trimValue = parseFloat(document.getElementById('slider-trim').value).toFixed(3);
            document.getElementById('label-trim-value').textContent = trimValue;
            updateScene();
        });

        document.getElementById('btn-reset-view').addEventListener('click', () => controls.reset());

        function animate() {
            requestAnimationFrame(animate);
            renderer.render(scene, camera);
        }

        window.addEventListener('resize', () => {
            camera.aspect = window.innerWidth / window.innerHeight;
            camera.updateProjectionMatrix();
            renderer.setSize(window.innerWidth, window.innerHeight);
        });

        updateScene();
        animate();
    </script>
</body>
</html>
