<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Signal Visualization</title>
    <style>
        body {
            margin: 0;
            font-family: Arial, sans-serif;
        }
        #main-container {
            background-color: #f0f0f0;
            padding: 20px;
            position: relative;
            height: 100vh;
            display: flex;
            flex-direction: column;
        }
        #info-button {
            position: absolute;
            top: 10px;
            right: 10px;
            padding: 5px 10px;
            cursor: pointer;
        }
        #description-panel {
            margin-top: 10px;
            padding: 10px;
            background-color: #fff;
            border: 1px solid #ccc;
            box-shadow: 0 2px 5px rgba(0,0,0,0.1);
        }
        #control-panel {
            margin-bottom: 20px;
        }
        #control-panel label {
            display: block;
            margin-top: 10px;
        }
        #control-panel input[type="range"] {
            width: 100%;
        }
        #function-buttons, #encoding-buttons {
            display: flex;
            gap: 5px;
            margin-top: 5px;
        }
        #function-buttons button, #encoding-buttons button {
            padding: 5px 10px;
            cursor: pointer;
            border: 1px solid #ccc;
            background-color: #fff;
        }
        #function-buttons button.active, #encoding-buttons button.active {
            background-color: #ccc;
            border-style: inset;
        }
        #canvas-container {
            border: 1px solid #ccc;
            background-color: #fff;
            flex-grow: 1;
        }
    </style>
    <script src="https://cdn.jsdelivr.net/npm/p5@1.4.1/lib/p5.js"></script>
</head>
<body>
    <div id="main-container">
        <button id="info-button">+</button>
        <div id="description-panel" style="display: none;">
            <p>This demo visualizes analog signals and their encoded representations using different methods. Adjust the start time, total time, signal function, and encoding method to see how the signals change.</p>
        </div>
        <div id="control-panel">
            <label for="slider-start-time">start time</label>
            <input type="range" id="slider-start-time" min="-5" max="5" step="0.1" value="-1.0">
            <label for="slider-total-time">total time</label>
            <input type="range" id="slider-total-time" min="1" max="5" step="0.1" value="2.2">
            <label>function</label>
            <div id="function-buttons">
                <button id="btn-f">f</button>
                <button id="btn-g" class="active">g</button>
                <button id="btn-sin">sin</button>
                <button id="btn-cos">cos</button>
                <button id="btn-tan">tan</button>
            </div>
            <label>encoding method</label>
            <div id="encoding-buttons">
                <button id="btn-asdm" class="active">ASDM</button>
                <button id="btn-iaf">IAF</button>
            </div>
        </div>
        <div id="canvas-container"></div>
    </div>

    <script>
        let startTime = -1.0;
        let totalTime = 2.2;
        let selectedFunction = 'g';
        let selectedEncoding = 'ASDM';

        const functionMap = {
            f: (t) => 0.5 * t,
            g: (t) => Math.sin(2 * Math.PI * t) + 0.3 * Math.cos(3 * 2 * Math.PI * t),
            sin: (t) => Math.sin(2 * Math.PI * t),
            cos: (t) => Math.cos(2 * Math.PI * t),
            tan: (t) => Math.tan(Math.PI * t)
        };

        const margin = 50;
        const width = 800;
        const height = 600;

        const sketch = (p) => {
            p.setup = () => {
                p.createCanvas(width, height);
                p.noLoop();
            };

            p.draw = () => {
                p.background(255);
                drawAxes(p);
                drawAnalogSignal(p);
                drawEncodedSignal(p);
            };

            function drawAxes(p) {
                p.stroke(0);
                p.line(margin, margin, margin, height - margin);
                p.line(margin, height - margin, width - margin, height - margin);

                for (let i = margin; i <= width - margin; i += (width - 2 * margin) / 10) {
                    p.line(i, height - margin - 5, i, height - margin + 5);
                    p.text((i - margin) * (startTime + totalTime - startTime) / (width - 2 * margin) + startTime, i, height - margin + 15);
                }

                for (let i = margin; i <= height - margin; i += (height - 2 * margin) / 5) {
                    p.line(margin - 5, i, margin + 5, i);
                    p.text((i - margin) * (-1.2 - 1.2) / (height - 2 * margin) + 1.2, margin - 20, i);
                }
            }

            function drawAnalogSignal(p) {
                p.stroke(0, 0, 255);
                p.beginShape();
                for (let i = 0; i <= width - 2 * margin; i++) {
                    let t = map(i, 0, width - 2 * margin, startTime, startTime + totalTime);
                    let y = functionMap[selectedFunction](t);
                    let x = map(t, startTime, startTime + totalTime, margin, width - margin);
                    let py = map(y, -1.2, 1.2, height - margin, margin);
                    if (!isNaN(py)) {
                        p.vertex(x, py);
                    }
                }
                p.endShape();
            }

            function drawEncodedSignal(p) {
                p.stroke(255, 0, 0);
                if (selectedEncoding === 'ASDM') {
                    drawASDM(p);
                } else if (selectedEncoding === 'IAF') {
                    drawIAF(p);
                }
            }

            function drawASDM(p) {
                const B = 0.25;
                const delta = 0.05;
                let currentTime = startTime;
                let outputLevel = B;
                let integral = 0;
                let dt = 0.001;
                let lastFlipTime = startTime;

                while (currentTime <= startTime + totalTime) {
                    integral += (functionMap[selectedFunction](currentTime) - outputLevel) * dt;
                    if ((outputLevel == B && integral < -delta) || (outputLevel == -B && integral > delta)) {
                        let x1 = map(lastFlipTime, startTime, startTime + totalTime, margin, width - margin);
                        let y1 = map(outputLevel, -1.2, 1.2, height - margin, margin);
                        let x2 = map(currentTime, startTime, startTime + totalTime, margin, width - margin);
                        let y2 = map(outputLevel, -1.2, 1.2, height - margin, margin);
                        p.line(x1, y1, x2, y1);
                        p.line(x2, y1, x2, y2);
                        lastFlipTime = currentTime;
                        outputLevel = -outputLevel;
                        integral = 0;
                    }
                    currentTime += dt;
                }

                let x1 = map(lastFlipTime, startTime, startTime + totalTime, margin, width - margin);
                let y1 = map(outputLevel, -1.2, 1.2, height - margin, margin);
                let x2 = map(startTime + totalTime, startTime, startTime + totalTime, margin, width - margin);
                p.line(x1, y1, x2, y1);
            }

            function drawIAF(p) {
                const theta = 0.05;
                const ySpike = 0.3;
                let currentTime = startTime;
                let integral = 0;
                let dt = 0.001;
                let spikeTimes = [];

                while (currentTime <= startTime + totalTime) {
                    integral += functionMap[selectedFunction](currentTime) * dt;
                    if (integral >= theta) {
                        spikeTimes.push(currentTime);
                        integral = 0;
                    }
                    currentTime += dt;
                }

                spikeTimes.forEach(time => {
                    let x = map(time, startTime, startTime + totalTime, margin, width - margin);
                    let y = map(ySpike, -1.2, 1.2, height - margin, margin);
                    p.line(x, height - margin, x, y);
                    p.ellipse(x, y, 5, 5);
                });
            }
        };

        const myp5 = new p5(sketch, 'canvas-container');

        document.getElementById('info-button').addEventListener('click', () => {
            const panel = document.getElementById('description-panel');
            panel.style.display = panel.style.display === 'block' ? 'none' : 'block';
        });

        document.getElementById('slider-start-time').addEventListener('input', (e) => {
            startTime = parseFloat(e.target.value);
            myp5.redraw();
        });

        document.getElementById('slider-total-time').addEventListener('input', (e) => {
            totalTime = parseFloat(e.target.value);
            myp5.redraw();
        });

        document.querySelectorAll('#function-buttons button').forEach(btn => {
            btn.addEventListener('click', () => {
                document.querySelectorAll('#function-buttons button').forEach(b => b.classList.remove('active'));
                btn.classList.add('active');
                selectedFunction = btn.id.replace('btn-', '');
                myp5.redraw();
            });
        });

        document.querySelectorAll('#encoding-buttons button').forEach(btn => {
            btn.addEventListener('click', () => {
                document.querySelectorAll('#encoding-buttons button').forEach(b => b.classList.remove('active'));
                btn.classList.add('active');
                selectedEncoding = btn.id.replace('btn-', '');
                myp5.redraw();
            });
        });
    </script>
