    <script>
        // Global Variables
        let time = 0;
        let isPlaying = false;
        let plotPoints = 40;
        let pWaveType = 'sine';
        let pWaveAmplitude = 0.1;
        let pWaveWavelength = 2.0;
        let sWaveType = 'sine';
        let sWaveAmplitude = 0.1;
        let sWaveWavelength = 2.5;
        let sWaveDirection = 'xy';
        let viewpoint = 'top';

        // Scene Setup
        const scene = new THREE.Scene();
        scene.background = new THREE.Color(0xe0e0e0);

        const camera = new THREE.PerspectiveCamera(75, window.innerWidth / window.innerHeight, 0.1, 1000);
        camera.position.set(10, 10, 10);
        camera.lookAt(0, 0, 0);

        const orthoCamera = new THREE.OrthographicCamera(
            -window.innerWidth / window.innerHeight * 5, window.innerWidth / window.innerHeight * 5,
            5, -5, 0.1, 1000
        );
        orthoCamera.position.set(0, 0, 10);
        orthoCamera.lookAt(0, 0, 0);

        const renderer = new THREE.WebGLRenderer({ antialias: true });
        renderer.setSize(window.innerWidth, window.innerHeight);
        document.getElementById('visualization-container').appendChild(renderer.domElement);

        const ambientLight = new THREE.AmbientLight(0x404040);
        scene.add(ambientLight);

        const directionalLight = new THREE.DirectionalLight(0xffffff, 0.8);
        directionalLight.position.set(1, 1, 1).normalize();
        scene.add(directionalLight);

        // Grid Geometry and Material
        let geometry = new THREE.PlaneBufferGeometry(10, 10, plotPoints - 1, plotPoints - 1);
        let material = new THREE.MeshLambertMaterial({ vertexColors: true });

        let grid = new THREE.Mesh(geometry, material);
        scene.add(grid);

        let wireframe = new THREE.LineSegments(new THREE.WireframeGeometry(geometry), new THREE.LineBasicMaterial({ color: 0x556b2f }));
        scene.add(wireframe);

        // Animation Loop
        function animate() {
            requestAnimationFrame(animate);

            if (isPlaying) {
                time += 0.1;
            }

            document.getElementById('time-slider').value = time;

            updateGrid();

            renderer.render(scene, viewpoint === 'top' ? orthoCamera : camera);
        }

        function updateGrid() {
            const positions = geometry.attributes.position.array;
            const colors = geometry.attributes.color.array;

            for (let i = 0; i < positions.length; i += 3) {
                const x = positions[i];
                const y = positions[i + 1];
                const r = Math.sqrt(x * x + y * y);

                let pDisplacement = [0, 0, 0];
                let sDisplacement = [0, 0, 0];

                // P-wave
                if (pWaveType === 'sine') {
                    pDisplacement[0] = pWaveAmplitude * Math.sin(2 * Math.PI * ((r / pWaveWavelength) - time)) * (x / r);
                    pDisplacement[1] = pWaveAmplitude * Math.sin(2 * Math.PI * ((r / pWaveWavelength) - time)) * (y / r);
                } else if (pWaveType === 'pulse') {
                    pDisplacement[0] = pWaveAmplitude * Math.exp(-Math.pow(((r / pWaveWavelength) - time), 2)) * (x / r);
                    pDisplacement[1] = pWaveAmplitude * Math.exp(-Math.pow(((r / pWaveWavelength) - time), 2)) * (y / r);
                }

                // S-wave
                if (sWaveType === 'sine') {
                    if (sWaveDirection === 'xy') {
                        sDisplacement[0] = sWaveAmplitude * Math.sin(2 * Math.PI * ((r / sWaveWavelength) - time)) * (-y / r);
                        sDisplacement[1] = sWaveAmplitude * Math.sin(2 * Math.PI * ((r / sWaveWavelength) - time)) * (x / r);
                    } else if (sWaveDirection === 'z') {
                        sDisplacement[2] = sWaveAmplitude * Math.sin(2 * Math.PI * ((r / sWaveWavelength) - time));
                    }
                } else if (sWaveType === 'pulse') {
                    if (sWaveDirection === 'xy') {
                        sDisplacement[0] = sWaveAmplitude * Math.exp(-Math.pow(((r / sWaveWavelength) - time), 2)) * (-y / r);
                        sDisplacement[1] = sWaveAmplitude * Math.exp(-Math.pow(((r / sWaveWavelength) - time), 2)) * (x / r);
                    } else if (sWaveDirection === 'z') {
                        sDisplacement[2] = sWaveAmplitude * Math.exp(-Math.pow(((r / sWaveWavelength) - time), 2));
                    }
                }

                positions[i] = x + pDisplacement[0] + sDisplacement[0];
                positions[i + 1] = y + pDisplacement[1] + sDisplacement[1];
                positions[i + 2] = pDisplacement[2] + sDisplacement[2];

                // Color mapping
                const z = positions[i + 2];
                const color = new THREE.Color().setHSL((z + 0.3) / 0.6, 1, 0.5);
                colors[i] = color.r;
                colors[i + 1] = color.g;
                colors[i + 2] = color.b;
            }

            geometry.attributes.position.needsUpdate = true;
            geometry.attributes.color.needsUpdate = true;
        }

        // Event Listeners
        document.getElementById('time-slider').addEventListener('input', function(e) {
            time = parseFloat(e.target.value);
            isPlaying = false;
            document.getElementById('play-pause-button').textContent = '►';
            updateGrid();
        });

        document.getElementById('play-pause-button').addEventListener('click', function() {
            isPlaying = !isPlaying;
            this.textContent = isPlaying ? '❚❚' : '►';
        });

        document.getElementById('reset-button').addEventListener('click', function() {
            time = 0;
            isPlaying = false;
            document.getElementById('play-pause-button').textContent = '►';
            document.getElementById('time-slider').value = time;
            updateGrid();
        });

        document.getElementById('step-button').addEventListener('click', function() {
            time += 0.1;
            document.getElementById('time-slider').value = time;
            updateGrid();
        });

        document.getElementById('plot-points-slider').addEventListener('input', function(e) {
            plotPoints = parseInt(e.target.value);
            scene.remove(grid);
            scene.remove(wireframe);
            geometry.dispose();
            geometry = new THREE.PlaneBufferGeometry(10, 10, plotPoints - 1, plotPoints - 1);
            grid = new THREE.Mesh(geometry, material);
            wireframe = new THREE.LineSegments(new THREE.WireframeGeometry(geometry), new THREE.LineBasicMaterial({ color: 0x556b2f }));
            scene.add(grid);
            scene.add(wireframe);
            updateGrid();
        });

        document.getElementById('p-wave-sine-button').addEventListener('click', function() {
            pWaveType = 'sine';
            this.classList.add('active');
            document.getElementById('p-wave-pulse-button').classList.remove('active');
            updateGrid();
        });

        document.getElementById('p-wave-pulse-button').addEventListener('click', function() {
            pWaveType = 'pulse';
            this.classList.add('active');
            document.getElementById('p-wave-sine-button').classList.remove('active');
            updateGrid();
        });

        document.getElementById('p-wave-amplitude-slider').addEventListener('input', function(e) {
            pWaveAmplitude = parseFloat(e.target.value);
            updateGrid();
        });

        document.getElementById('p-wave-wavelength-slider').addEventListener('input', function(e) {
            pWaveWavelength = parseFloat(e.target.value);
            updateGrid();
        });

        document.getElementById('s-wave-sine-button').addEventListener('click', function() {
            sWaveType = 'sine';
            this.classList.add('active');
            document.getElementById('s-wave-pulse-button').classList.remove('active');
            updateGrid();
        });

        document.getElementById('s-wave-pulse-button').addEventListener('click', function() {
            sWaveType = 'pulse';
            this.classList.add('active');
            document.getElementById('s-wave-sine-button').classList.remove('active');
            updateGrid();
        });

        document.getElementById('s-wave-amplitude-slider').addEventListener('input', function(e) {
            sWaveAmplitude = parseFloat(e.target.value);
            updateGrid();
        });

        document.getElementById('s-wave-wavelength-slider').addEventListener('input', function(e) {
            sWaveWavelength = parseFloat(e.target.value);
            updateGrid();
        });

        document.getElementById('s-wave-dir-xy-button').addEventListener('click', function() {
            sWaveDirection = 'xy';
            this.classList.add('active');
            document.getElementById('s-wave-dir-z-button').classList.remove('active');
            updateGrid();
        });

        document.getElementById('s-wave-dir-z-button').addEventListener('click', function() {
            sWaveDirection = 'z';
            this.classList.add('active');
            document.getElementById('s-wave-dir-xy-button').classList.remove('active');
            updateGrid();
        });

        document.getElementById('viewpoint-top-button').addEventListener('click', function() {
            viewpoint = 'top';
            this.classList.add('active');
            document.getElementById('viewpoint-default-button').classList.remove('active');
        });

        document.getElementById('viewpoint-default-button').addEventListener('click', function() {
            viewpoint = 'default';
            this.classList.add('active');
            document.getElementById('viewpoint-top-button').classList.remove('active');
        });

        // Start Animation
        animate();
    </script>
</body>
