    <script src="https://polyfill.io/v3/polyfill.min.js?features=es6"></script>
    <script id="MathJax-script" async src="https://cdn.jsdelivr.net/npm/mathjax@3/es5/tex-mml-chtml.js"></script>
    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.min.js"></script>
    <script>
        let z = 1.5;
        let plotRange = 4;
        let showLabels = false;
        let showCircle = false;
        let showAxes = false;
        let showGridLines = false;
        let a = 1, b = -1, c = -1;

        const calculatePoints = () => {
            const O = { x: 0, y: 0 };
            const L1 = { x: a, y: 0 };
            const L2 = { x: a, y: b };
            const L3 = { x: a - c, y: b };
            const L1p = { x: a, y: -z * a };
            const L2p = { x: a + b * z + a * z * z, y: b };
            return { O, L1, L2, L3, L1p, L2p };
        };

        const calculateValues = ({ O, L1, L2, L3, L1p, L2p }) => {
            const Pz = a * z * z + b * z + c;
            const L3L2pLength = Math.sqrt(Math.pow(L3.x - L2p.x, 2) + Math.pow(L3.y - L2p.y, 2));
            return { Pz, L3L2pLength };
        };

        const update = () => {
            const { O, L1, L2, L3, L1p, L2p } = calculatePoints();
            const { Pz, L3L2pLength } = calculateValues({ O, L1, L2, L3, L1p, L2p });
            document.getElementById('value-pz').textContent = Pz.toFixed(4);
            document.getElementById('value-l3l2').textContent = L3L2pLength.toFixed(4);
            document.getElementById('value-z').textContent = z.toFixed(2);
            document.getElementById('label-z').textContent = z.toFixed(2);
            document.getElementById('label-plot-range').textContent = plotRange.toFixed(1);
            redraw();
        };

        const drawGridAndAxes = () => {
            const gridSize = plotRange / 5;
            for (let i = -plotRange; i <= plotRange; i += gridSize) {
                stroke(200);
                if (i !== 0) {
                    line(i * 50, -plotRange * 50, i * 50, plotRange * 50);
                    line(-plotRange * 50, i * 50, plotRange * 50, i * 50);
                }
            }
            stroke(0);
            strokeWeight(2);
            line(-plotRange * 50, 0, plotRange * 50, 0);
            line(0, -plotRange * 50, 0, plotRange * 50);
            strokeWeight(1);
        };

        const drawLabels = (points) => {
            textSize(12);
            textAlign(CENTER, CENTER);
            Object.keys(points).forEach(key => {
                const point = points[key];
                text(key, point.x * 50, point.y * 50 - 10);
            });
        };

        const drawCircle = (center, radius) => {
            noFill();
            stroke(0);
            ellipse(center.x * 50, center.y * 50, radius * 100, radius * 100);
        };

        const setup = () => {
            createCanvas(document.getElementById('canvas-container').clientWidth, document.getElementById('canvas-container').clientHeight);
            noLoop();
            update();
        };

        const draw = () => {
            background(255);
            translate(width / 2, height / 2);
            scale(50);
            scale(1, -1);

            if (showGridLines) drawGridAndAxes();
            if (showAxes) drawGridAndAxes();

            const { O, L1, L2, L3, L1p, L2p } = calculatePoints();

            stroke(0);
            line(O.x, O.y, L1.x, L1.y);
            line(L1.x, L1.y, L2.x, L2.y);
            line(L2.x, L2.y, L3.x, L3.y);

            stroke(0);
            fill(0);
            ellipse(O.x, O.y, 0.1, 0.1);
            ellipse(L1.x, L1.y, 0.1, 0.1);
            ellipse(L2.x, L2.y, 0.1, 0.1);
            ellipse(L3.x, L3.y, 0.1, 0.1);

            stroke(0);
            line(O.x, O.y, L1p.x, L1p.y);

            stroke(255, 0, 255);
            line(L1p.x, L1p.y, L2p.x, L2p.y);

            stroke(255, 0, 0);
            line(L2p.x, L2p.y, L3.x, L3.y);

            stroke(0);
            fill(0);
            ellipse(L1p.x, L1p.y, 0.1, 0.1);
            ellipse(L2p.x, L2p.y, 0.1, 0.1);

            if (showLabels) drawLabels({ O, L1, L2, L3, L1p, L2p });

            if (showCircle) {
                const center = { x: (a - c) / 2, y: b / 2 };
                const radius = Math.sqrt((a - c) * (a - c) + b * b) / 2;
                drawCircle(center, radius);
            }
        };

        const newExample = () => {
            a = [1, 2, 3, -1, -2, -3][Math.floor(Math.random() * 6)];
            b = Math.floor(Math.random() * 11) - 5;
            c = Math.floor(Math.random() * 11) - 5;
            document.getElementById('formula-text').textContent = `${a}z^2 + ${b}z + ${c}`;
            MathJax.typesetPromise(document.getElementById('formula-display'));
            update();
        };

        const goldenRatio = () => {
            a = 1;
            b = -1;
            c = -1;
            document.getElementById('formula-text').textContent = `z^2 - z - 1`;
            MathJax.typesetPromise(document.getElementById('formula-display'));
            update();
        };

        document.getElementById('slider-z').addEventListener('input', (e) => {
            z = parseFloat(e.target.value);
            update();
        });

        document.getElementById('slider-plot-range').addEventListener('input', (e) => {
            plotRange = parseFloat(e.target.value);
            update();
        });

        document.getElementById('check-show-labels').addEventListener('change', (e) => {
            showLabels = e.target.checked;
            redraw();
        });

        document.getElementById('check-show-circle').addEventListener('change', (e) => {
            showCircle = e.target.checked;
            redraw();
        });

        document.getElementById('check-show-axes').addEventListener('change', (e) => {
            showAxes = e.target.checked;
            redraw();
        });

        document.getElementById('check-show-grid-lines').addEventListener('change', (e) => {
            showGridLines = e.target.checked;
            redraw();
        });

        document.getElementById('btn-new-example').addEventListener('click', newExample);
        document.getElementById('btn-golden-ratio').addEventListener('click', goldenRatio);

        new p5(setup, document.getElementById('canvas-container'));
    </script>
</body>
</html>
