    </style>
</head>
<body>
    <div id="app-container">
        <div id="header-controls">
            <div id="view-switcher">
                <button id="btn-plot" class="active">T₂(α)</button>
                <button id="btn-table">solution list</button>
                <button id="btn-turning-points">turning points</button>
            </div>
            <div id="info-button-container">
                <button id="btn-info">+</button>
            </div>
        </div>
        <div id="parameter-controls">
            <label for="slider-alpha">α</label>
            <input type="range" id="slider-alpha" min="0.00" max="0.07" step="0.0001" value="0.039">
            <span id="alpha-value-display">0.039</span>
        </div>
        <div id="main-display">
            <div id="plot-view"></div>
            <div id="table-view" style="display: none;">
                <table id="solutions-table">
                    <thead>
                        <tr>
                            <th>root</th>
                            <th>X₁</th>
                            <th>X₂</th>
                            <th>T₁</th>
                            <th>T₂</th>
                        </tr>
                    </thead>
                    <tbody id="solutions-table-body">
                    </tbody>
                </table>
            </div>
        </div>
    </div>
    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.min.js"></script>
    <script>
        let currentView = 'plot_solutions';
        const solutionCurveData = [
            {alpha: 0.00, x1: 0.1, x2: 0.2, t1: 1.0, t2: 2.0},
            {alpha: 0.01, x1: 0.11, x2: 0.21, t1: 1.1, t2: 2.1},
            // Add more data points as needed
            {alpha: 0.07, x1: 0.17, x2: 0.27, t1: 1.7, t2: 2.7}
        ];
        const turningPointsData = [
            {alpha: 0.02, t2: 3.0},
            {alpha: 0.03, t2: 4.0},
            {alpha: 0.04, t2: 5.0},
            {alpha: 0.05, t2: 6.0},
            {alpha: 0.06, t2: 7.0},
            {alpha: 0.07, t2: 8.0}
        ];
        let alphaValue = 0.039;

        function setup() {
            createCanvas(600, 600);
            document.getElementById('slider-alpha').addEventListener('input', updateAlpha);
            document.getElementById('btn-plot').addEventListener('click', () => switchView('plot_solutions'));
            document.getElementById('btn-table').addEventListener('click', () => switchView('table'));
            document.getElementById('btn-turning-points').addEventListener('click', () => switchView('plot_turning_points'));
        }

        function draw() {
            background(255);
            drawGrid();
            drawAxes();
            drawSolutionCurve();
            if (currentView === 'plot_solutions') {
                drawVerticalLine(alphaValue);
                drawIntersectionPoints(alphaValue);
            } else if (currentView === 'plot_turning_points') {
                drawTurningPoints();
            }
        }

        function drawGrid() {
            stroke(200);
            for (let i = 0; i <= 600; i += 50) {
                line(i, 0, i, 600);
                line(0, i, 600, i);
            }
        }

        function drawAxes() {
            stroke(0);
            line(50, 550, 550, 550);
            line(50, 550, 50, 50);
            for (let i = 0; i <= 12; i++) {
                let x = map(i * 0.005, 0, 0.07, 50, 550);
                line(x, 550, x, 540);
                text((i * 0.005).toFixed(3), x, 560);
            }
            for (let i = 0; i <= 8; i++) {
                let y = map(i, 0, 8, 550, 50);
                line(50, y, 60, y);
                text(i, 40, y + 5);
            }
            text('α', 560, 550);
            text('T₂(α)', 25, 40);
        }

        function drawSolutionCurve() {
            stroke(255, 0, 0);
            noFill();
            strokeWeight(2);
            beginShape();
            for (let point of solutionCurveData) {
                vertex(map(point.alpha, 0, 0.07, 50, 550), map(point.t2, 0, 8, 550, 50));
            }
            endShape();
        }

        function drawVerticalLine(alpha) {
            stroke(0, 255, 0);
            strokeWeight(2);
            line(map(alpha, 0, 0.07, 50, 550), 550, map(alpha, 0, 0.07, 50, 550), 50);
        }

        function drawIntersectionPoints(alpha) {
            fill(0, 0, 255);
            noStroke();
            for (let i = 0; i < solutionCurveData.length - 1; i++) {
                let point1 = solutionCurveData[i];
                let point2 = solutionCurveData[i + 1];
                if ((point1.alpha <= alpha && alpha <= point2.alpha) || (point2.alpha <= alpha && alpha <= point1.alpha)) {
                    let t = (alpha - point1.alpha) / (point2.alpha - point1.alpha);
                    let x1 = lerp(point1.x1, point2.x1, t);
                    let x2 = lerp(point1.x2, point2.x2, t);
                    let t1 = lerp(point1.t1, point2.t1, t);
                    let t2 = lerp(point1.t2, point2.t2, t);
                    ellipse(map(alpha, 0, 0.07, 50, 550), map(t2, 0, 8, 550, 50), 10, 10);
                }
            }
        }

        function drawTurningPoints() {
            fill(0, 255, 0);
            noStroke();
            for (let point of turningPointsData) {
                ellipse(map(point.alpha, 0, 0.07, 50, 550), map(point.t2, 0, 8, 550, 50), 10, 10);
            }
        }

        function updateAlpha(event) {
            alphaValue = parseFloat(event.target.value);
            document.getElementById('alpha-value-display').textContent = alphaValue.toFixed(3);
            if (currentView === 'plot_solutions') {
                redraw();
            } else if (currentView === 'table') {
                updateTable(alphaValue);
            }
        }

        function switchView(view) {
            currentView = view;
            document.querySelectorAll('#view-switcher button').forEach(btn => btn.classList.remove('active'));
            document.getElementById(`btn-${view.replace('_', '-')}`).classList.add('active');
            document.getElementById('plot-view').style.display = view === 'plot_turning_points' ? 'block' : 'none';
            document.getElementById('table-view').style.display = view === 'table' ? 'block' : 'none';
            document.getElementById('parameter-controls').style.display = view === 'plot_turning_points' ? 'none' : 'flex';
            redraw();
            if (view === 'table') {
                updateTable(alphaValue);
            }
        }

        function updateTable(alpha) {
            const tbody = document.getElementById('solutions-table-body');
            tbody.innerHTML = '';
            let intersections = [];
            for (let i = 0; i < solutionCurveData.length - 1; i++) {
                let point1 = solutionCurveData[i];
                let point2 = solutionCurveData[i + 1];
                if ((point1.alpha <= alpha && alpha <= point2.alpha) || (point2.alpha <= alpha && alpha <= point1.alpha)) {
                    let t = (alpha - point1.alpha) / (point2.alpha - point1.alpha);
                    let x1 = lerp(point1.x1, point2.x1, t);
                    let x2 = lerp(point1.x2, point2.x2, t);
                    let t1 = lerp(point1.t1, point2.t1, t);
                    let t2 = lerp(point1.t2, point2.t2, t);
                    intersections.push({x1, x2, t1, t2});
                }
            }
            intersections.forEach((intersection, index) => {
                const row = document.createElement('tr');
                row.innerHTML = `
                    <td>${index + 1}</td>
                    <td>${intersection.x1.toFixed(4)}</td>
                    <td>${intersection.x2.toFixed(4)}</td>
                    <td>${intersection.t1.toFixed(4)}</td>
                    <td>${intersection.t2.toFixed(4)}</td>
                `;
                tbody.appendChild(row);
            });
        }
    </script>
</body>
</html>
