<body>
    <div id="main-container">
        <div id="control-panel">
            <div id="controls-row-1">
                <div id="type-controls" class="button-group">
                    <span>type</span>
                    <button id="btn-type-single">single</button>
                    <button id="btn-type-double" class="active">double</button>
                </div>
                <div id="item-controls" class="button-group">
                    <span>item</span>
                    <button id="btn-item-none">none</button>
                    <button id="btn-item-point" class="active">point</button>
                    <button id="btn-item-fox">fox</button>
                    <button id="btn-item-filled-fox">filled fox</button>
                </div>
            </div>
            <div id="controls-row-2">
                <div id="growth-slider-container">
                    <label for="slider-growth">spiral growth</label>
                    <input type="range" id="slider-growth" min="0.01" max="0.5" step="0.0005" value="0.15">
                    <span id="display-growth">0.15</span>
                    <button id="btn-reset-growth">+</button>
                </div>
            </div>
        </div>
        <div id="canvas-container">
            <canvas id="p5-canvas"></canvas>
        </div>
    </div>

    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.js"></script>
    <script>
        let spiralType = 'double';
        let itemType = 'point';
        let spiralGrowth = 0.15;
        let z0_loc = { x: 0, y: 0 };
        let z_plus_loc = { x: -100, y: 0 };
        let z_minus_loc = { x: 100, y: 0 };
        let dragging = null;

        const fox_vertices = [
            { x: -15, y: 0 }, { x: -10, y: -5 }, { x: -5, y: -4 }, { x: 0, y: -5 },
            { x: 5, y: -5 }, { x: 8, y: -2 }, { x: 10, y: -6 }, { x: 12, y: -6 },
            { x: 15, y: 0 }, { x: 12, y: 6 }, { x: 10, y: 6 }, { x: 8, y: 2 },
            { x: 5, y: 5 }, { x: 0, y: 5 }, { x: -5, y: 4 }, { x: -10, y: 5 }
        ];

        function setup() {
            const canvasContainer = document.getElementById('canvas-container');
            const width = canvasContainer.clientWidth;
            const height = canvasContainer.clientHeight;
            createCanvas(width, height);
            translate(width / 2, height / 2);
        }

        function draw() {
            background(255);
            drawSpiral();
            drawLocators();
        }

        function drawSpiral() {
            noFill();
            stroke('#4A6DA8');
            strokeWeight(2);

            if (spiralType === 'single') {
                beginShape();
                for (let t = -8 * PI; t <= 8 * PI; t += 0.01) {
                    const x = z0_loc.x + exp(spiralGrowth * t) * cos(t);
                    const y = z0_loc.y - exp(spiralGrowth * t) * sin(t);
                    curveVertex(x, y);
                }
                endShape();
                if (itemType !== 'none') {
                    for (let t = -8 * PI; t <= 8 * PI; t += 0.1) {
                        const x = z0_loc.x + exp(spiralGrowth * t) * cos(t);
                        const y = z0_loc.y - exp(spiralGrowth * t) * sin(t);
                        drawItem(x, y, t);
                    }
                }
            } else if (spiralType === 'double') {
                const p = complex(z_plus_loc.x, z_plus_loc.y);
                const q = complex(z_minus_loc.x, z_minus_loc.y);
                const z0 = p.add(q).div(2);
                text('z0Loc', z0.re, z0.im);

                beginShape();
                for (let t = -8 * PI; t >= -0.01; t -= 0.01) {
                    const w = complex(exp(spiralGrowth * t) * cos(t), exp(spiralGrowth * t) * sin(t));
                    const z = p.mult(w).sub(q).div(w.sub(1));
                    curveVertex(z.re, z.im);
                }
                endShape();

                beginShape();
                for (let t = 0.01; t <= 8 * PI; t += 0.01) {
                    const w = complex(exp(spiralGrowth * t) * cos(t), exp(spiralGrowth * t) * sin(t));
                    const z = p.mult(w).sub(q).div(w.sub(1));
                    curveVertex(z.re, z.im);
                }
                endShape();

                if (itemType !== 'none') {
                    for (let t = -8 * PI; t >= -0.01; t -= 0.1) {
                        const w = complex(exp(spiralGrowth * t) * cos(t), exp(spiralGrowth * t) * sin(t));
                        const z = p.mult(w).sub(q).div(w.sub(1));
                        drawItem(z.re, z.im, t);
                    }
                    for (let t = 0.01; t <= 8 * PI; t += 0.1) {
                        const w = complex(exp(spiralGrowth * t) * cos(t), exp(spiralGrowth * t) * sin(t));
                        const z = p.mult(w).sub(q).div(w.sub(1));
                        drawItem(z.re, z.im, t);
                    }
                }
            }
        }

        function drawItem(x, y, t) {
            if (itemType === 'point') {
                fill('#CB3335');
                noStroke();
                ellipse(x, y, 5, 5);
            } else if (itemType === 'fox' || itemType === 'filled-fox') {
                const dw = complex(-exp(spiralGrowth * t) * sin(t) + exp(spiralGrowth * t) * cos(t),
                                    exp(spiralGrowth * t) * cos(t) + exp(spiralGrowth * t) * sin(t));
                const dz = complex(z_plus_loc.x - z_minus_loc.x, z_plus_loc.y - z_minus_loc.y)
                            .mult(complex(spiralGrowth + 1j))
                            .mult(dw)
                            .div(complex(exp(spiralGrowth * t) * cos(t) - exp(spiralGrowth * t) * sin(t) - 1, 
                                        exp(spiralGrowth * t) * sin(t) + exp(spiralGrowth * t) * cos(t)));
                const angle = atan2(dz.im, dz.re);

                push();
                translate(x, y);
                rotate(angle);
                scale(2);
                beginShape();
                for (const vertex of fox_vertices) {
                    vertexShape(vertex.x, vertex.y);
                }
                vertexShape(fox_vertices[0].x, fox_vertices[0].y);
                if (itemType === 'fox') {
                    noFill();
                    stroke('#D6292D');
                    strokeWeight(1);
                } else {
                    fill('#D6292D');
                    noStroke();
                }
                endShape();
                pop();
            }
        }

        function drawLocators() {
            if (spiralType === 'single') {
                drawLocator(z0_loc.x, z0_loc.y, '#ffff00', 'z0Loc');
            } else if (spiralType === 'double') {
                drawLocator(z_plus_loc.x, z_plus_loc.y, '#ffff00', '');
                drawLocator(z_minus_loc.x, z_minus_loc.y, '#00ff00', '');
                const z0 = complex(z_plus_loc.x, z_plus_loc.y).add(complex(z_minus_loc.x, z_minus_loc.y)).div(2);
                text('z0Loc', z0.re, z0.im);
            }
        }

        function drawLocator(x, y, color, label) {
            fill(color, 200);
            stroke(0);
            ellipse(x, y, 20, 20);
            line(x - 10, y, x + 10, y);
            line(x, y - 10, x, y + 10);
            if (label) {
                fill(0);
                noStroke();
                textSize(12);
                textAlign(CENTER, CENTER);
                text(label, x, y + 30);
            }
        }

        function mousePressed() {
            if (spiralType === 'single') {
                if (dist(mouseX, mouseY, z0_loc.x + width / 2, height / 2 - z0_loc.y) < 10) {
                    dragging = 'z0_loc';
                }
            } else if (spiralType === 'double') {
                if (dist(mouseX, mouseY, z_plus_loc.x + width / 2, height / 2 - z_plus_loc.y) < 10) {
                    dragging = 'z_plus_loc';
                } else if (dist(mouseX, mouseY, z_minus_loc.x + width / 2, height / 2 - z_minus_loc.y) < 10) {
                    dragging = 'z_minus_loc';
                }
            }
        }

        function mouseDragged() {
            if (dragging === 'z0_loc') {
                z0_loc.x = mouseX - width / 2;
                z0_loc.y = height / 2 - mouseY;
            } else if (dragging === 'z_plus_loc') {
                z_plus_loc.x = mouseX - width / 2;
                z_plus_loc.y = height / 2 - mouseY;
            } else if (dragging === 'z_minus_loc') {
                z_minus_loc.x = mouseX - width / 2;
                z_minus_loc.y = height / 2 - mouseY;
            }
        }

        function mouseReleased() {
            dragging = null;
        }

        function updateButtons() {
            document.querySelectorAll('#type-controls button').forEach(btn => btn.classList.remove('active'));
            document.querySelector(`#btn-type-${spiralType}`).classList.add('active');

            document.querySelectorAll('#item-controls button').forEach(btn => btn.classList.remove('active'));
            document.querySelector(`#btn-item-${itemType}`).classList.add('active');
        }

        function setupControls() {
            document.querySelectorAll('#type-controls button').forEach(btn => {
                btn.addEventListener('click', () => {
                    spiralType = btn.id.split('-')[2];
                    updateButtons();
                    redraw();
                });
            });

            document.querySelectorAll('#item-controls button').forEach(btn => {
                btn.addEventListener('click', () => {
                    itemType = btn.id.split('-')[2];
                    updateButtons();
                    redraw();
                });
            });

            const growthSlider = document.getElementById('slider-growth');
            growthSlider.addEventListener('input', () => {
                spiralGrowth = parseFloat(growthSlider.value);
                document.getElementById('display-growth').textContent = spiralGrowth.toFixed(4);
                redraw();
            });

            document.getElementById('btn-reset-growth').addEventListener('click', () => {
                spiralGrowth = 0.25;
                growthSlider.value = spiralGrowth;
                document.getElementById('display-growth').textContent = spiralGrowth.toFixed(4);
                redraw();
            });
        }

        function complex(re, im) {
            return {
                re: re,
                im: im,
                add: function(other) {
                    return complex(this.re + other.re, this.im + other.im);
                },
                sub: function(other) {
                    return complex(this.re - other.re, this.im - other.im);
                },
                mult: function(other) {
                    return complex(this.re * other.re - this.im * other.im, this.re * other.im + this.im * other.re);
                },
                div: function(other) {
                    const denom = other.re * other.re + other.im * other.im;
                    return complex((this.re * other.re + this.im * other.im) / denom, (this.im * other.re - this.re * other.im) / denom);
                }
            };
        }

        function exp(x) {
            return Math.exp(x);
        }

        function atan2(y, x) {
            return Math.atan2(y, x);
        }

        function dist(x1, y1, x2, y2) {
            return Math.sqrt((x2 - x1) ** 2 + (y2 - y1) ** 2);
        }

        function vertexShape(x, y) {
            vertex(x, y);
        }

        function windowResized() {
            const canvasContainer = document.getElementById('canvas-container');
            const width = canvasContainer.clientWidth;
            const height = canvasContainer.clientHeight;
            resizeCanvas(width, height);
            translate(width / 2, height / 2);
        }

        updateButtons();
        setupControls();
    </script>
</body>
