            gap: 10px;
            margin-bottom: 20px;
        }
        .vertex-grid button {
            padding: 10px;
            border: none;
            cursor: pointer;
            background-color: #ddd;
            transition: background-color 0.3s;
        }
        .vertex-grid button.active {
            background-color: #333;
            color: white;
        }
        #canvas-container {
            width: 600px;
            height: 600px;
        }
    </style>
    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.1/p5.min.js"></script>
</head>
<body>
    <div id="control-panel">
        <button id="start-over-btn">start over</button>
        <label>initial vertex</label>
        <div class="vertex-grid">
            <button id="btn-m">m</button>
            <button id="btn-n">n</button>
            <button id="btn-o">o</button>
            <button id="btn-p">p</button>
            <button id="btn-i">i</button>
            <button id="btn-j">j</button>
            <button id="btn-k">k</button>
            <button id="btn-l">l</button>
            <button id="btn-e">e</button>
            <button id="btn-f">f</button>
            <button id="btn-g">g</button>
            <button id="btn-h">h</button>
            <button id="btn-a">a</button>
            <button id="btn-b">b</button>
            <button id="btn-c">c</button>
            <button id="btn-d">d</button>
        </div>
        <button id="fixed-example-btn">fixed example</button>
        <button id="random-example-btn">random example</button>
        <label>implement algorithm</label>
        <button id="next-step-btn">next step</button>
    </div>
    <div id="viz-container">
        <h1 id="title-display">Dijkstra's algorithm starting at vertex m</h1>
        <div id="canvas-container"></div>
    </div>

    <script>
        const vertices = ['m', 'n', 'o', 'p', 'i', 'j', 'k', 'l', 'e', 'f', 'g', 'h', 'a', 'b', 'c', 'd'];
        const fixedWeights = {
            'mn': 8, 'no': 7, 'op': 7,
            'ij': 9, 'jk': 7, 'kl': 1,
            'ef': 1, 'fg': 7, 'gh': 6,
            'ab': 10, 'bc': 4, 'cd': 8,
            'mi': 6, 'ie': 9, 'ea': 9,
            'nj': 7, 'jf': 6, 'fb': 5,
            'ok': 1, 'kg': 8, 'gc': 8,
            'pl': 2, 'lh': 1, 'hd': 7
        };
        let weights = { ...fixedWeights };
        let startVertex = 'm';
        let distances = {};
        let visited = new Set();
        let predecessors = {};
        let shortestPathTree = [];

        function setup() {
            createCanvas(600, 600);
            noLoop();
            resetAlgorithm();
            drawGraph();
        }

        function drawGraph() {
            background(255);
            const pos = {
                'm': [150, 150], 'n': [300, 150], 'o': [450, 150], 'p': [600, 150],
                'i': [150, 300], 'j': [300, 300], 'k': [450, 300], 'l': [600, 300],
                'e': [150, 450], 'f': [300, 450], 'g': [450, 450], 'h': [600, 450],
                'a': [150, 600], 'b': [300, 600], 'c': [450, 600], 'd': [600, 600]
            };

            // Draw edges
            strokeWeight(2);
            for (const [edge, weight] of Object.entries(weights)) {
                const [u, v] = edge.split('');
                const [x1, y1] = pos[u];
                const [x2, y2] = pos[v];
                stroke(shortestPathTree.includes(edge) ? 'blue' : 'black');
                strokeWeight(shortestPathTree.includes(edge) ? 4 : 2);
                line(x1, y1, x2, y2);

                // Draw edge weights
                const midX = (x1 + x2) / 2;
                const midY = (y1 + y2) / 2;
                fill(255);
                rect(midX - 10, midY - 10, 20, 20);
                fill('black');
                textSize(12);
                textAlign(CENTER, CENTER);
                text(weight, midX, midY);
            }

            // Draw vertices
            for (const vertex of vertices) {
                const [x, y] = pos[vertex];
                fill(visited.has(vertex) ? 'red' : 'black');
                circle(x, y, 20);

                // Draw vertex labels
                fill('black');
                textSize(16);
                textAlign(CENTER, CENTER);
                text(vertex, x, y + 30);

                // Draw distance labels
                const distance = distances[vertex];
                const distText = distance === Infinity ? '∞' : `(${distance})`;
                textSize(14);
                text(distText, x, y - 20);
                fill(visited.has(vertex) ? 'red' : 'black');
            }
        }

        function resetAlgorithm() {
            distances = {};
            visited.clear();
            predecessors = {};
            shortestPathTree = [];
            for (const vertex of vertices) {
                distances[vertex] = vertex === startVertex ? 0 : Infinity;
            }
            document.getElementById('next-step-btn').disabled = false;
            document.getElementById('title-display').textContent = `Dijkstra's algorithm starting at vertex ${startVertex}`;
        }

        function nextStep() {
            let minDistance = Infinity;
            let currentVertex = null;

            for (const vertex of vertices) {
                if (!visited.has(vertex) && distances[vertex] < minDistance) {
                    minDistance = distances[vertex];
                    currentVertex = vertex;
                }
            }

            if (currentVertex === null) {
                document.getElementById('next-step-btn').disabled = true;
                return;
            }

            visited.add(currentVertex);

            for (const neighbor of getNeighbors(currentVertex)) {
                const edge = `${currentVertex}${neighbor}`;
                const weight = weights[edge] || weights[`${neighbor}${currentVertex}`];
                const newDistance = distances[currentVertex] + weight;

                if (newDistance < distances[neighbor]) {
                    distances[neighbor] = newDistance;
                    predecessors[neighbor] = currentVertex;
                    shortestPathTree.push(edge);
                }
            }

            redraw();
        }

        function getNeighbors(vertex) {
            const neighbors = [];
            for (const edge of Object.keys(weights)) {
                if (edge.startsWith(vertex)) {
                    neighbors.push(edge[1]);
                } else if (edge.endsWith(vertex)) {
                    neighbors.push(edge[0]);
                }
            }
            return neighbors;
        }

        function loadFixedExample() {
            weights = { ...fixedWeights };
            resetAlgorithm();
            redraw();
        }

        function loadRandomExample() {
            weights = {};
            for (const vertex of vertices) {
                for (let i = 0; i < vertices.indexOf(vertex); i++) {
                    const neighbor = vertices[i];
                    const edge = `${vertex}${neighbor}`;
                    const reverseEdge = `${neighbor}${vertex}`;
                    if (!weights[edge] && !weights[reverseEdge]) {
                        weights[edge] = Math.floor(Math.random() * 10) + 1;
                    }
                }
            }
            resetAlgorithm();
            redraw();
        }

        function setStartVertex(vertex) {
            startVertex = vertex;
            resetAlgorithm();
            redraw();
            document.querySelectorAll('.vertex-grid button').forEach(btn => btn.classList.remove('active'));
            document.getElementById(`btn-${vertex}`).classList.add('active');
        }

        document.addEventListener('DOMContentLoaded', () => {
            document.getElementById('start-over-btn').addEventListener('click', () => {
                startVertex = 'm';
                weights = { ...fixedWeights };
                resetAlgorithm();
                redraw();
                document.querySelectorAll('.vertex-grid button').forEach(btn => btn.classList.remove('active'));
                document.getElementById('btn-m').classList.add('active');
            });

            document.getElementById('fixed-example-btn').addEventListener('click', loadFixedExample);
            document.getElementById('random-example-btn').addEventListener('click', loadRandomExample);
            document.getElementById('next-step-btn').addEventListener('click', nextStep);

            document.querySelectorAll('.vertex-grid button').forEach(btn => {
                btn.addEventListener('click', () => setStartVertex(btn.id.slice(4)));
            });

            setStartVertex('m');
        });
    </script>
</body>
</html>
