            flex-grow: 1;
        }
    </style>
</head>
<body>
    <div id="app-container">
        <div id="controls-panel">
            <label for="slider-sphere-diameter">diameter of sphere</label>
            <input type="range" id="slider-sphere-diameter" min="0.5" max="3.0" step="0.01" value="1.5">

            <label>polyhedron vertices</label>
            <div id="polyhedron-buttons">
                <button id="btn-cube">Cube</button>
                <button id="btn-dodecahedron">Dodecahedron</button>
                <button id="btn-icosahedron">Icosahedron</button>
                <button id="btn-octahedron" class="active">Octahedron</button>
                <button id="btn-tetrahedron">Tetrahedron</button>
            </div>

            <label for="slider-cone-height">height</label>
            <input type="range" id="slider-cone-height" min="0.1" max="4.0" step="0.01" value="1.5">

            <label for="slider-cone-base">base</label>
            <input type="range" id="slider-cone-base" min="0.05" max="1.0" step="0.01" value="0.4">
        </div>
        <div id="canvas-container"></div>
    </div>

    <script src="https://cdnjs.cloudflare.com/ajax/libs/three.js/r128/three.min.js"></script>
    <script src="https://cdn.jsdelivr.net/npm/three@0.128.0/examples/js/controls/OrbitControls.js"></script>
    <script>
        const scene = new THREE.Scene();
        const camera = new THREE.PerspectiveCamera(45, window.innerWidth / window.innerHeight, 0.1, 1000);
        const renderer = new THREE.WebGLRenderer({ antialias: true });
        renderer.setSize(window.innerWidth, window.innerHeight);
        document.getElementById('canvas-container').appendChild(renderer.domElement);

        renderer.setClearColor(0xf0f0f0);

        camera.position.z = 8;

        const ambientLight = new THREE.AmbientLight(0x404040);
        scene.add(ambientLight);

        const light1 = new THREE.DirectionalLight(0xffffff, 0.7);
        light1.position.set(1, 1, 1).normalize();
        scene.add(light1);

        const light2 = new THREE.DirectionalLight(0xffffff, 0.5);
        light2.position.set(-1, -1, -1).normalize();
        scene.add(light2);

        const sphereGeometry = new THREE.SphereGeometry(0.75, 32, 32);
        const sphereMaterial = new THREE.MeshPhongMaterial({ color: 0xdddddd, shininess: 50 });
        const sphere = new THREE.Mesh(sphereGeometry, sphereMaterial);
        scene.add(sphere);

        let cones = [];
        let currentPolyhedron = 'octahedron';

        function createCones(polyhedronType, sphereRadius, coneHeight, coneBase) {
            removeCones();

            let geometry;
            switch (polyhedronType) {
                case 'cube':
                    geometry = new THREE.BoxGeometry(1, 1, 1);
                    break;
                case 'dodecahedron':
                    geometry = new THREE.DodecahedronGeometry(1);
                    break;
                case 'icosahedron':
                    geometry = new THREE.IcosahedronGeometry(1);
                    break;
                case 'octahedron':
                    geometry = new THREE.OctahedronGeometry(1);
                    break;
                case 'tetrahedron':
                    geometry = new THREE.TetrahedronGeometry(1);
                    break;
            }

            const positions = geometry.attributes.position.array;
            for (let i = 0; i < positions.length; i += 3) {
                const vertex = new THREE.Vector3(positions[i], positions[i + 1], positions[i + 2]);
                const direction = vertex.clone().normalize();
                const position = direction.clone().multiplyScalar(sphereRadius + coneHeight / 2);

                const coneGeometry = new THREE.ConeGeometry(coneBase, coneHeight, 32);
                const coneMaterial = new THREE.MeshPhongMaterial({ color: 0xffa500, shininess: 30 });
                const cone = new THREE.Mesh(coneGeometry, coneMaterial);
                cone.position.copy(position);
                cone.quaternion.setFromUnitVectors(new THREE.Vector3(0, 1, 0), direction);
                scene.add(cone);
                cones.push(cone);
            }
        }

        function removeCones() {
            cones.forEach(cone => scene.remove(cone));
            cones = [];
        }

        function updateSphereRadius(radius) {
            sphere.scale.set(radius, radius, radius);
        }

        function updateConeGeometry(height, base) {
            cones.forEach(cone => {
                const geometry = new THREE.ConeGeometry(base, height, 32);
                cone.geometry.dispose();
                cone.geometry = geometry;
            });
        }

        function updateConePositions(sphereRadius, coneHeight) {
            cones.forEach((cone, index) => {
                const position = cone.position.clone().normalize().multiplyScalar(sphereRadius + coneHeight / 2);
                cone.position.copy(position);
            });
        }

        function handlePolyhedronButtonClick(event) {
            document.querySelectorAll('#polyhedron-buttons button').forEach(button => button.classList.remove('active'));
            event.target.classList.add('active');
            currentPolyhedron = event.target.id.replace('btn-', '');
            const sphereRadius = parseFloat(document.getElementById('slider-sphere-diameter').value) / 2;
            const coneHeight = parseFloat(document.getElementById('slider-cone-height').value);
            const coneBase = parseFloat(document.getElementById('slider-cone-base').value);
            createCones(currentPolyhedron, sphereRadius, coneHeight, coneBase);
        }

        document.getElementById('slider-sphere-diameter').addEventListener('input', () => {
            const newDiameter = parseFloat(document.getElementById('slider-sphere-diameter').value);
            const newRadius = newDiameter / 2;
            updateSphereRadius(newRadius);
            const coneHeight = parseFloat(document.getElementById('slider-cone-height').value);
            updateConePositions(newRadius, coneHeight);
        });

        document.getElementById('slider-cone-height').addEventListener('input', () => {
            const newHeight = parseFloat(document.getElementById('slider-cone-height').value);
            const coneBase = parseFloat(document.getElementById('slider-cone-base').value);
            updateConeGeometry(newHeight, coneBase);
            const sphereRadius = parseFloat(document.getElementById('slider-sphere-diameter').value) / 2;
            updateConePositions(sphereRadius, newHeight);
        });

        document.getElementById('slider-cone-base').addEventListener('input', () => {
            const coneHeight = parseFloat(document.getElementById('slider-cone-height').value);
            const newBase = parseFloat(document.getElementById('slider-cone-base').value);
            updateConeGeometry(coneHeight, newBase);
        });

        document.querySelectorAll('#polyhedron-buttons button').forEach(button => {
            button.addEventListener('click', handlePolyhedronButtonClick);
        });

        const controls = new THREE.OrbitControls(camera, renderer.domElement);
        controls.enableDamping = true;
        controls.dampingFactor = 0.25;
        controls.enableZoom = true;

        function animate() {
            requestAnimationFrame(animate);
            controls.update();
            renderer.render(scene, camera);
        }

        window.addEventListener('resize', () => {
            const width = window.innerWidth;
            const height = window.innerHeight;
            renderer.setSize(width, height);
            camera.aspect = width / height;
            camera.updateProjectionMatrix();
        });

        createCones(currentPolyhedron, 0.75, 1.5, 0.4);
        animate();
    </script>
</body>
</html>
