<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Analemma Simulation</title>
    <style>
        #main-container {
            display: flex;
        }
        #control-panel {
            width: 300px;
            padding: 20px;
            background-color: #f0f0f0;
        }
        #visualization-area {
            flex-grow: 1;
            padding: 20px;
        }
        label {
            display: block;
            margin-top: 10px;
        }
        input[type="range"]:disabled {
            opacity: 0.5;
        }
    </style>
    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.js"></script>
</head>
<body>
    <div id="main-container">
        <div id="control-panel">
            <label for="select-planet">planet</label>
            <select id="select-planet">
                <option value="manual" selected>-- choose orbit manually --</option>
                <option value="Earth">Earth</option>
                <option value="Mars">Mars</option>
            </select>
            <div id="manual-controls">
                <input type="checkbox" id="checkbox-manual" checked>
                <label for="checkbox-manual">choose orbit manually</label>
                <div>
                    <label for="slider-tilt">axis angle relative to ecliptic</label>
                    <input type="range" id="slider-tilt" min="0" max="90" step="0.1" value="23.5">
                </div>
                <div>
                    <label for="slider-equinox">spring equinox point</label>
                    <input type="range" id="slider-equinox" min="-180" max="180" step="1" value="0">
                </div>
                <div>
                    <label for="slider-eccentricity">orbit eccentricity</label>
                    <input type="range" id="slider-eccentricity" min="0" max="0.5" step="0.001" value="0.1">
                </div>
            </div>
            <div id="display-controls">
                <input type="checkbox" id="checkbox-snapshots" checked>
                <label for="checkbox-snapshots">show sun snapshots</label>
                <input type="checkbox" id="checkbox-scales" checked>
                <label for="checkbox-scales">show scales</label>
            </div>
        </div>
        <div id="visualization-area">
            <canvas id="analemma-canvas"></canvas>
        </div>
    </div>

    <script>
        const planetData = {
            "Earth": { tilt: 23.44, eccentricity: 0.0167, equinox: 11.26 },
            "Mars": { tilt: 25.19, eccentricity: 0.0934, equinox: -86.9 }
        };

        let tiltSlider, equinoxSlider, eccentricitySlider, manualCheckbox, snapshotsCheckbox, scalesCheckbox;
        let canvasWidth, canvasHeight, centerX, centerY, scale;

        function setup() {
            const visualizationArea = document.getElementById('visualization-area');
            canvasWidth = visualizationArea.clientWidth;
            canvasHeight = visualizationArea.clientHeight;
            createCanvas(canvasWidth, canvasHeight);
            centerX = width / 2;
            centerY = height / 2;
            scale = 200;

            tiltSlider = document.getElementById('slider-tilt');
            equinoxSlider = document.getElementById('slider-equinox');
            eccentricitySlider = document.getElementById('slider-eccentricity');
            manualCheckbox = document.getElementById('checkbox-manual');
            snapshotsCheckbox = document.getElementById('checkbox-snapshots');
            scalesCheckbox = document.getElementById('checkbox-scales');

            document.getElementById('select-planet').addEventListener('change', updateControls);
            manualCheckbox.addEventListener('change', updateControls);
            tiltSlider.addEventListener('input', drawAnalemma);
            equinoxSlider.addEventListener('input', drawAnalemma);
            eccentricitySlider.addEventListener('input', drawAnalemma);
            snapshotsCheckbox.addEventListener('change', drawAnalemma);
            scalesCheckbox.addEventListener('change', drawAnalemma);

            drawAnalemma();
        }

        function updateControls() {
            const planetSelect = document.getElementById('select-planet');
            const planet = planetSelect.value;
            const manualEnabled = manualCheckbox.checked;

            if (planet !== 'manual' && !manualEnabled) {
                const data = planetData[planet];
                tiltSlider.value = data.tilt;
                equinoxSlider.value = data.equinox;
                eccentricitySlider.value = data.eccentricity;
                manualCheckbox.checked = false;
                tiltSlider.disabled = true;
                equinoxSlider.disabled = true;
                eccentricitySlider.disabled = true;
            } else {
                manualCheckbox.checked = true;
                tiltSlider.disabled = false;
                equinoxSlider.disabled = false;
                eccentricitySlider.disabled = false;
            }

            drawAnalemma();
        }

        function drawAnalemma() {
            background(255);
            const tilt = radians(parseFloat(tiltSlider.value));
            const eccentricity = parseFloat(eccentricitySlider.value);
            const equinoxAngle = radians(parseFloat(equinoxSlider.value));
            const snapshots = snapshotsCheckbox.checked;
            const scales = scalesCheckbox.checked;

            // Draw orbit ellipse
            noFill();
            stroke(139, 69, 19); // Brown color
            strokeWeight(1);
            const orbitRadiusX = 100 * (1 + eccentricity);
            const orbitRadiusY = 100 * Math.sqrt(1 - eccentricity * eccentricity);
            ellipse(centerX, centerY - 100, orbitRadiusX * 2, orbitRadiusY * 2);

            // Draw scales
            if (scales) {
                stroke(0);
                strokeWeight(1);
                line(centerX - 300, centerY, centerX + 300, centerY); // X-axis
                line(centerX, centerY - 300, centerX, centerY + 300); // Y-axis

                for (let i = -300; i <= 300; i += 100) {
                    line(centerX + i, centerY - 5, centerX + i, centerY + 5); // X-ticks
                    line(centerX - 5, centerY + i, centerX + 5, centerY + i); // Y-ticks
                }

                fill(0);
                textSize(12);
                textAlign(CENTER, CENTER);
                for (let i = -300; i <= 300; i += 100) {
                    text((i / scale).toFixed(1), centerX + i, centerY + 15); // X-labels
                    text((i / scale).toFixed(1), centerX - 15, centerY + i); // Y-labels
                }
            }

            // Calculate analemma points
            const analemmaPoints = [];
            for (let d = 0; d < 365; d++) {
                const meanAnomaly = (2 * Math.PI * d) / 365.25;
                let eccentricAnomaly = meanAnomaly;
                for (let i = 0; i < 10; i++) {
                    eccentricAnomaly = meanAnomaly + eccentricity * Math.sin(eccentricAnomaly);
                }
                const trueAnomaly = 2 * Math.atan2(Math.sqrt(1 + eccentricity) * Math.sin(eccentricAnomaly / 2), Math.sqrt(1 - eccentricity) * Math.cos(eccentricAnomaly / 2));
                const eclipticLongitude = trueAnomaly + equinoxAngle;
                const declination = Math.asin(Math.sin(tilt) * Math.sin(eclipticLongitude));
                const rightAscension = Math.atan2(Math.cos(tilt) * Math.sin(eclipticLongitude), Math.cos(eclipticLongitude));
                const equationOfTime = meanAnomaly - rightAscension;
                const x = equationOfTime * scale + centerX;
                const y = -declination * scale + centerY;
                analemmaPoints.push({ x, y });
            }

            // Draw analemma curve
            noFill();
            stroke('orange');
            strokeWeight(2);
            beginShape();
            for (const point of analemmaPoints) {
                vertex(point.x, point.y);
            }
            endShape();

            // Draw sun snapshots
            if (snapshots) {
                fill('orange');
                noStroke();
                for (let i = 0; i < analemmaPoints.length; i += 20) {
                    const point = analemmaPoints[i];
                    circle(point.x, point.y, 8);
                }
            }
        }

        function radians(degrees) {
            return degrees * Math.PI / 180;
        }
    </script>
</body>
</html>
