            background-color: #ccc;
            cursor: not-allowed;
        }
        table {
            width: 100%;
            border-collapse: collapse;
            margin-top: 10px;
        }
        th, td {
            border: 1px solid #ddd;
            padding: 8px;
            text-align: left;
        }
        th {
            background-color: #f2f2f2;
        }
    </style>
    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.js"></script>
</head>
<body>
    <div id="app-container">
        <div id="control-panel">
            <div>
                <label>algorithm</label><br>
                <input type="radio" id="algo-cheapest-link" name="algorithm-choice" value="cheapest-link">
                <label for="algo-cheapest-link">cheapest link</label><br>
                <input type="radio" id="algo-nearest-neighbor" name="algorithm-choice" value="nearest-neighbor" checked>
                <label for="algo-nearest-neighbor">nearest neighbor</label>
            </div>
            <button id="btn-start-over">start over</button>
            <div>
                <button id="btn-fixed-example">fixed example</button>
                <button id="btn-random-example">random example</button>
            </div>
            <div id="starting-vertex-selector">
                <label>starting vertex for nearest neighbor</label><br>
                <div id="vertex-buttons-container">
                    <button id="btn-vertex-A" class="vertex-btn" data-vertex="A">A</button>
                    <button id="btn-vertex-B" class="vertex-btn" data-vertex="B">B</button>
                    <button id="btn-vertex-C" class="vertex-btn" data-vertex="C">C</button>
                    <button id="btn-vertex-D" class="vertex-btn" data-vertex="D">D</button>
                    <button id="btn-vertex-E" class="vertex-btn" data-vertex="E">E</button>
                    <button id="btn-vertex-F" class="vertex-btn" data-vertex="F">F</button>
                    <button id="btn-vertex-G" class="vertex-btn" data-vertex="G">G</button>
                </div>
            </div>
            <div>
                <label>Implement algorithm</label><br>
                <button id="btn-next-step" disabled>next step</button>
            </div>
            <div>
                <label for="checkbox-optimal">show optimal solution</label>
                <input type="checkbox" id="checkbox-optimal">
            </div>
        </div>
        <div id="visualization-panel">
            <div id="visualization-container">
                <div id="algo-result-container">
                    <h3 id="title-algo">The nearest neighbor algorithm</h3>
                    <div id="canvas-algo-wrapper"></div>
                    <p id="text-example-type-algo">fixed example</p>
                    <div id="table-container">
                        <h4 id="table-title"></h4>
                        <table id="table-edge-list"></table>
                    </div>
                    <p id="text-total-weight"></p>
                </div>
                <div id="optimal-result-container">
                    <h3 id="title-optimal">An optimal solution</h3>
                    <div id="canvas-optimal-wrapper"></div>
                    <p id="text-example-type-optimal"></p>
                    <p id="text-optimal-weight"></p>
                </div>
            </div>
        </div>
    </div>

    <script>
        let currentAlgorithm = 'nearest-neighbor';
        let graphData = {
            vertices: [
                { label: 'A', position: { x: 200, y: 60 } },
                { label: 'B', position: { x: 80, y: 120 } },
                { label: 'C', position: { x: 60, y: 240 } },
                { label: 'D', position: { x: 150, y: 340 } },
                { label: 'E', position: { x: 250, y: 340 } },
                { label: 'F', position: { x: 340, y: 240 } },
                { label: 'G', position: { x: 320, y: 120 } }
            ],
            adjacencyMatrix: [
                [0, 11, 16, 44, 30, 44, 23],
                [11, 0, 12, 12, 10, 30, 25],
                [16, 12, 0, 43, 32, 36, 44],
                [44, 12, 43, 0, 41, 30, 35],
                [30, 10, 32, 41, 0, 31, 20],
                [44, 30, 36, 30, 31, 0, 18],
                [23, 25, 44, 35, 20, 18, 0]
            ]
        };
        let optimalPath = ['A', 'C', 'B', 'D', 'F', 'G', 'E', 'A'];
        let optimalWeight = 138;
        let currentPath = [];
        let totalWeight = 0;
        let visitedVertices = new Set();
        let startingVertex = null;
        let stepCount = 0;
        let edges = [];

        function setup() {
            createCanvas(400, 400).parent('canvas-algo-wrapper');
            drawGraph();
            document.getElementById('starting-vertex-selector').style.display = 'block';
            document.querySelectorAll('.vertex-btn').forEach(btn => btn.disabled = false);
            document.getElementById('btn-next-step').disabled = true;
            document.getElementById('title-algo').textContent = 'Select a starting vertex';
        }

        function draw() {
            background(255);
            drawEdges();
            drawVertices();
            drawWeights();
        }

        function drawGraph() {
            background(255);
            drawEdges();
            drawVertices();
            drawWeights();
        }

        function drawVertices() {
            stroke(0);
            fill(255);
            graphData.vertices.forEach(vertex => {
                ellipse(vertex.position.x, vertex.position.y, 20, 20);
                fill(0);
                textAlign(CENTER, CENTER);
                text(vertex.label, vertex.position.x, vertex.position.y);
                fill(255);
            });
        }

        function drawEdges() {
            stroke(128);
            strokeWeight(1);
            graphData.adjacencyMatrix.forEach((row, i) => {
                row.forEach((weight, j) => {
                    if (weight > 0 && i < j) {
                        line(graphData.vertices[i].position.x, graphData.vertices[i].position.y,
                             graphData.vertices[j].position.x, graphData.vertices[j].position.y);
                    }
                });
            });

            stroke(255, 165, 0);
            strokeWeight(3);
            currentPath.forEach((edge, index) => {
                if (index < currentPath.length - 1) {
                    line(graphData.vertices[edge].position.x, graphData.vertices[edge].position.y,
                         graphData.vertices[currentPath[index + 1]].position.x, graphData.vertices[currentPath[index + 1]].position.y);
                }
            });

            if (document.getElementById('checkbox-optimal').checked) {
                stroke(0, 0, 255);
                strokeWeight(3);
                optimalPath.forEach((edge, index) => {
                    if (index < optimalPath.length - 1) {
                        line(graphData.vertices[getVertexIndex(edge)].position.x, graphData.vertices[getVertexIndex(edge)].position.y,
                             graphData.vertices[getVertexIndex(optimalPath[index + 1])].position.x, graphData.vertices[getVertexIndex(optimalPath[index + 1])].position.y);
                    }
                });
            }
        }

        function drawWeights() {
            fill(255);
            stroke(0);
            textSize(12);
            graphData.adjacencyMatrix.forEach((row, i) => {
                row.forEach((weight, j) => {
                    if (weight > 0 && i < j) {
                        const midX = (graphData.vertices[i].position.x + graphData.vertices[j].position.x) / 2;
                        const midY = (graphData.vertices[i].position.y + graphData.vertices[j].position.y) / 2;
                        text(weight, midX, midY);
                    }
                });
            });
        }

        function getVertexIndex(label) {
            return graphData.vertices.findIndex(v => v.label === label);
        }

        document.getElementById('algo-cheapest-link').addEventListener('change', () => {
            currentAlgorithm = 'cheapest-link';
            document.getElementById('starting-vertex-selector').style.display = 'none';
            resetSimulation();
        });

        document.getElementById('algo-nearest-neighbor').addEventListener('change', () => {
            currentAlgorithm = 'nearest-neighbor';
            document.getElementById('starting-vertex-selector').style.display = 'block';
            resetSimulation();
        });

        document.getElementById('btn-start-over').addEventListener('click', resetSimulation);

        document.getElementById('btn-fixed-example').addEventListener('click', () => {
            graphData = {
                vertices: [
                    { label: 'A', position: { x: 200, y: 60 } },
                    { label: 'B', position: { x: 80, y: 120 } },
                    { label: 'C', position: { x: 60, y: 240 } },
                    { label: 'D', position: { x: 150, y: 340 } },
                    { label: 'E', position: { x: 250, y: 340 } },
                    { label: 'F', position: { x: 340, y: 240 } },
                    { label: 'G', position: { x: 320, y: 120 } }
                ],
                adjacencyMatrix: [
                    [0, 11, 16, 44, 30, 44, 23],
                    [11, 0, 12, 12, 10, 30, 25],
                    [16, 12, 0, 43, 32, 36, 44],
                    [44, 12, 43, 0, 41, 30, 35],
                    [30, 10, 32, 41, 0, 31, 20],
                    [44, 30, 36, 30, 31, 0, 18],
                    [23, 25, 44, 35, 20, 18, 0]
                ]
            };
            optimalPath = ['A', 'C', 'B', 'D', 'F', 'G', 'E', 'A'];
            optimalWeight = 138;
            document.getElementById('text-example-type-algo').textContent = 'fixed example';
            document.getElementById('text-example-type-optimal').textContent = 'fixed example';
            resetSimulation();
        });

        document.getElementById('btn-random-example').addEventListener('click', () => {
            graphData = generateRandomGraph();
            optimalPath = findOptimalPath(graphData);
            optimalWeight = calculatePathWeight(optimalPath, graphData);
            document.getElementById('text-example-type-algo').textContent = 'random example';
            document.getElementById('text-example-type-optimal').textContent = 'random example';
            resetSimulation();
        });

        function generateRandomGraph() {
            const vertices = [
                { label: 'A', position: { x: 200, y: 60 } },
                { label: 'B', position: { x: 80, y: 120 } },
                { label: 'C', position: { x: 60, y: 240 } },
                { label: 'D', position: { x: 150, y: 340 } },
                { label: 'E', position: { x: 250, y: 340 } },
                { label: 'F', position: { x: 340, y: 240 } },
                { label: 'G', position: { x: 320, y: 120 } }
            ];
            const adjacencyMatrix = Array.from({ length: 7 }, () => Array(7).fill(0));
            for (let i = 0; i < 7; i++) {
                for (let j = i + 1; j < 7; j++) {
                    adjacencyMatrix[i][j] = Math.floor(Math.random() * 41) + 10;
                    adjacencyMatrix[j][i] = adjacencyMatrix[i][j];
                }
            }
            return { vertices, adjacencyMatrix };
        }

        function findOptimalPath(graph) {
            const n = graph.vertices.length;
            let minPath = [];
            let minWeight = Infinity;

            function permute(arr, m = []) {
                if (arr.length === 0) {
                    const path = [...m, m[0]];
                    const weight = calculatePathWeight(path, graph);
                    if (weight < minWeight) {
                        minWeight = weight;
                        minPath = path;
                    }
                } else {
                    for (let i = 0; i < arr.length; i++) {
                        let curr = arr.slice();
                        let next = curr.splice(i, 1);
                        permute(curr.slice(), m.concat(next));
                    }
                }
            }

            permute([...Array(n).keys()]);
            return minPath.map(index => graph.vertices[index].label);
        }

        function calculatePathWeight(path, graph) {
            let weight = 0;
            for (let i = 0; i < path.length - 1; i++) {
                const fromIndex = getVertexIndex(path[i]);
                const toIndex = getVertexIndex(path[i + 1]);
                weight += graph.adjacencyMatrix[fromIndex][toIndex];
            }
            return weight;
        }

        document.querySelectorAll('.vertex-btn').forEach(btn => {
            btn.addEventListener('click', () => {
                startingVertex = btn.getAttribute('data-vertex');
                document.querySelectorAll('.vertex-btn').forEach(b => b.disabled = true);
                document.getElementById('btn-next-step').disabled = false;
                document.getElementById('title-algo').textContent = `The nearest neighbor algorithm starting at vertex ${startingVertex}`;
                resetSimulation();
            });
        });

        document.getElementById('btn-next-step').addEventListener('click', () => {
            if (currentAlgorithm === 'nearest-neighbor') {
                nearestNeighborStep();
            } else if (currentAlgorithm === 'cheapest-link') {
                cheapestLinkStep();
            }
            updateUI();
        });

        function nearestNeighborStep() {
            if (stepCount === 0) {
                currentPath.push(getVertexIndex(startingVertex));
                visitedVertices.add(startingVertex);
            } else if (currentPath.length < graphData.vertices.length) {
                let nearestVertex = null;
                let minDistance = Infinity;
                const lastVertexIndex = currentPath[currentPath.length - 1];
                graphData.adjacencyMatrix[lastVertexIndex].forEach((distance, index) => {
                    const vertexLabel = graphData.vertices[index].label;
                    if (distance > 0 && !visitedVertices.has(vertexLabel) && distance < minDistance) {
                        minDistance = distance;
                        nearestVertex = vertexLabel;
                    }
                });
                if (nearestVertex) {
                    currentPath.push(getVertexIndex(nearestVertex));
                    visitedVertices.add(nearestVertex);
                    totalWeight += minDistance;
                }
            } else {
                const startIndex = getVertexIndex(startingVertex);
                const lastIndex = currentPath[currentPath.length - 1];
                totalWeight += graphData.adjacencyMatrix[lastIndex][startIndex];
                currentPath.push(startIndex);
                document.getElementById('btn-next-step').disabled = true;
                document.getElementById('text-total-weight').textContent = `Total weight: ${totalWeight}`;
            }
            stepCount++;
        }

        function cheapestLinkStep() {
            if (stepCount === 0) {
                edges = [];
                graphData.adjacencyMatrix.forEach((row, i) => {
                    row.forEach((weight, j) => {
                        if (weight > 0 && i < j) {
                            edges.push({ from: i, to: j, weight });
                        }
                    });
                });
                edges.sort((a, b) => a.weight - b.weight);
            }

            if (edges.length > 0) {
                const edge = edges.shift();
                const fromVertex = graphData.vertices[edge.from].label;
                const toVertex = graphData.vertices[edge.to].label;
                if (!formsCycle(currentPath, fromVertex, toVertex) && degreeCheck(currentPath, fromVertex, toVertex) < 3) {
                    currentPath.push(edge.from, edge.to);
                    totalWeight += edge.weight;
                }
            } else {
                document.getElementById('btn-next-step').disabled = true;
                document.getElementById('text-total-weight').textContent = `Total weight: ${totalWeight}`;
            }
            stepCount++;
        }

        function formsCycle(path, fromVertex, toVertex) {
            const fromIndex = getVertexIndex(fromVertex);
            const toIndex = getVertexIndex(toVertex);
            return path.includes(fromIndex) && path.includes(toIndex);
        }

        function degreeCheck(path, fromVertex, toVertex) {
            const fromIndex = getVertexIndex(fromVertex);
            const toIndex = getVertexIndex(toVertex);
            let fromDegree = 0;
            let toDegree = 0;
            for (let i = 0; i < path.length - 1; i += 2) {
                if (path[i] === fromIndex || path[i + 1] === fromIndex) {
                    fromDegree++;
                }
                if (path[i] === toIndex || path[i + 1] === toIndex) {
                    toDegree++;
                }
            }
            return Math.max(fromDegree, toDegree);
        }

        document.getElementById('checkbox-optimal').addEventListener('change', () => {
            if (document.getElementById('checkbox-optimal').checked) {
                document.getElementById('optimal-result-container').style.display = 'block';
                createCanvas(400, 400).parent('canvas-optimal-wrapper');
                drawOptimalGraph();
            } else {
                document.getElementById('optimal-result-container').style.display = 'none';
            }
        });

        function drawOptimalGraph() {
            background(255);
            drawEdges();
            drawVertices();
            drawWeights();
            stroke(0, 0, 255);
            strokeWeight(3);
            optimalPath.forEach((edge, index) => {
                if (index < optimalPath.length - 1) {
                    line(graphData.vertices[getVertexIndex(edge)].position.x, graphData.vertices[getVertexIndex(edge)].position.y,
                         graphData.vertices[getVertexIndex(optimalPath[index + 1])].position.x, graphData.vertices[getVertexIndex(optimalPath[index + 1])].position.y);
                }
            });
            document.getElementById('text-optimal-weight').textContent = `Total weight: ${optimalWeight}`;
        }

        function resetSimulation() {
            currentPath = [];
            totalWeight = 0;
            visitedVertices.clear();
            stepCount = 0;
            document.getElementById('table-title').textContent = '';
            document.getElementById('table-edge-list').innerHTML = '';
            document.getElementById('text-total-weight').textContent = '';
            drawGraph();
        }
    </script>
</body>
</html>
