<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Ullman's Theorem in Two Dimensions</title>
    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.js"></script>
    <script src="https://cdnjs.cloudflare.com/ajax/libs/mathjs/11.5.0/math.js"></script>
    <style>
        body {
            margin: 0;
            display: flex;
            justify-content: center;
            align-items: center;
            height: 100vh;
            background-color: #E0E0E0;
            font-family: Arial, sans-serif;
        }
        #main-container {
            display: flex;
            width: 90%;
            height: 90%;
            max-width: 1200px;
            max-height: 800px;
            box-shadow: 0 4px 8px rgba(0, 0, 0, 0.1);
            background-color: white;
        }
        #control-panel {
            width: 250px;
            padding: 20px;
            box-sizing: border-box;
            overflow-y: auto;
        }
        .slider-group {
            margin-bottom: 15px;
        }
        .slider-group label {
            display: block;
            margin-bottom: 5px;
        }
        .slider-group input[type="range"] {
            width: 100%;
            margin-bottom: 5px;
        }
        .slider-group span {
            display: block;
            text-align: center;
        }
        #canvas-container {
            flex-grow: 1;
            position: relative;
        }
    </style>
</head>
<body>
    <div id="main-container">
        <div id="control-panel">
            <div class="slider-group">
                <label for="slider-au">A.u</label>
                <input type="range" id="slider-au" min="-2" max="2" step="0.01" value="0.8">
                <span id="value-au">0.8</span>
            </div>
            <div class="slider-group">
                <label for="slider-bu">B.u</label>
                <input type="range" id="slider-bu" min="-2" max="2" step="0.01" value="-0.5">
                <span id="value-bu">-0.5</span>
            </div>
            <div class="slider-group">
                <label for="slider-av">A.v</label>
                <input type="range" id="slider-av" min="-2" max="2" step="0.01" value="1.2">
                <span id="value-av">1.2</span>
            </div>
            <div class="slider-group">
                <label for="slider-bv">B.v</label>
                <input type="range" id="slider-bv" min="-2" max="2" step="0.01" value="1.0">
                <span id="value-bv">1.0</span>
            </div>
            <div class="slider-group">
                <label for="slider-aw">A.w</label>
                <input type="range" id="slider-aw" min="-2" max="2" step="0.01" value="1.1">
                <span id="value-aw">1.1</span>
            </div>
            <div class="slider-group">
                <label for="slider-bw">B.w</label>
                <input type="range" id="slider-bw" min="-2" max="2" step="0.01" value="-0.2">
                <span id="value-bw">-0.2</span>
            </div>
            <div class="slider-group">
                <label for="slider-x-scale">x scale</label>
                <input type="range" id="slider-x-scale" min="20" max="200" step="1" value="80">
                <span id="value-x-scale">80</span>
            </div>
            <div class="slider-group">
                <label for="slider-y-scale">y scale</label>
                <input type="range" id="slider-y-scale" min="20" max="200" step="1" value="80">
                <span id="value-y-scale">80</span>
            </div>
        </div>
        <div id="canvas-container"></div>
    </div>

    <script>
        let u, v, w, A, B;
        let x_scale = 80, y_scale = 80;

        function setup() {
            const canvas = createCanvas(600, 600);
            canvas.parent('canvas-container');
            noLoop();

            const sliders = [
                { id: 'slider-au', valueId: 'value-au' },
                { id: 'slider-bu', valueId: 'value-bu' },
                { id: 'slider-av', valueId: 'value-av' },
                { id: 'slider-bv', valueId: 'value-bv' },
                { id: 'slider-aw', valueId: 'value-aw' },
                { id: 'slider-bw', valueId: 'value-bw' },
                { id: 'slider-x-scale', valueId: 'value-x-scale' },
                { id: 'slider-y-scale', valueId: 'value-y-scale' }
            ];

            sliders.forEach(slider => {
                const elem = document.getElementById(slider.id);
                const valueElem = document.getElementById(slider.valueId);
                elem.addEventListener('input', () => {
                    valueElem.textContent = elem.value;
                    if (slider.id.includes('scale')) {
                        if (slider.id === 'slider-x-scale') x_scale = parseFloat(elem.value);
                        else y_scale = parseFloat(elem.value);
                    } else {
                        calculateMatrices();
                    }
                    redraw();
                });
            });

            calculateMatrices();
        }

        function calculateMatrices() {
            const au = parseFloat(document.getElementById('slider-au').value);
            const bu = parseFloat(document.getElementById('slider-bu').value);
            const av = parseFloat(document.getElementById('slider-av').value);
            const bv = parseFloat(document.getElementById('slider-bv').value);
            const aw = parseFloat(document.getElementById('slider-aw').value);
            const bw = parseFloat(document.getElementById('slider-bw').value);

            const M = math.matrix([[au, bu], [av, bv], [aw, bw]]);
            const svd = math.svd(M);
            const U = svd.U;

            const K = math.subset(U, math.index(math.range(0, 3), math.range(0, 2)));
            const Kinv = math.inv(K);
            const l_vec = math.matrix([1, 1, 1]);
            try {
                const L_flat = math.multiply(Kinv, l_vec);
                const L = math.reshape(L_flat, [2, 2]);

                if (!isPositiveDefinite(L)) {
                    console.error("Matrix L is not positive definite.");
                    return;
                }

                const Q = math.cholesky(L);
                const Qinv = math.inv(Q);

                const S = math.multiply(U, Q);
                const P = math.multiply(Qinv, math.multiply(svd.D, svd.Vt));

                u = math.row(S, 0);
                v = math.row(S, 1);
                w = math.row(S, 2);
                A = math.column(P, 0);
                B = math.column(P, 1);
            } catch (error) {
                console.error("Error in calculations:", error);
            }
        }

        function isPositiveDefinite(matrix) {
            const eigenvalues = math.eigs(matrix).values;
            for (let i = 0; i < eigenvalues.length; i++) {
                if (eigenvalues.get([i]) <= 0) {
                    return false;
                }
            }
            return true;
        }

        function draw() {
            background('#F0F0F0');
            translate(width / 2, height / 2);
            scale(x_scale, -y_scale);

            // Draw camera axes
            drawLine(u, '#0000CD', 'u');
            drawLine(v, '#6495ED', 'v');
            drawLine(w, '#0000CD', 'w');

            // Draw points
            drawPoint(A, '#FF0000', 'A');
            drawPoint(B, '#FFA500', 'B');
            drawPoint(math.matrix([0, 0]), '#FF0000', 'O');

            // Draw projections
            drawProjection(A, u);
            drawProjection(A, v);
            drawProjection(A, w);
            drawProjection(B, u);
            drawProjection(B, v);
            drawProjection(B, w);
        }

        function drawLine(vector, color, label) {
            stroke(color);
            strokeWeight(3);
            line(0, 0, vector.get([0]), vector.get([1]));
            fill(color);
            textSize(16);
            text(label, vector.get([0]) + 5, vector.get([1]) + 5);
        }

        function drawPoint(point, color, label) {
            fill(color);
            ellipse(point.get([0]), point.get([1]), 10);
            fill(color);
            textSize(16);
            text(label, point.get([0]) + 10, point.get([1]) + 10);
        }

        function drawProjection(point, axis) {
            const projection = math.multiply(math.dot(point, axis), axis);
            stroke('#808080');
            line(point.get([0]), point.get([1]), projection.get([0]), projection.get([1]));
            fill('#00FF00');
            ellipse(projection.get([0]), projection.get([1]), 5);
        }
    </script>
</body>
</html>
