        #time-pad::before, #time-pad::after {
            content: '';
            position: absolute;
            background-color: #ccc;
        }
        #time-pad::before {
            width: 1px;
            height: 100%;
            left: 50%;
            transform: translateX(-50%);
        }
        #time-pad::after {
            width: 100%;
            height: 1px;
            top: 50%;
            transform: translateY(-50%);
        }
