    <script src="https://cdnjs.cloudflare.com/ajax/libs/three.js/r128/three.min.js"></script>
    <script src="https://cdn.jsdelivr.net/npm/three@0.128.0/examples/js/controls/OrbitControls.js"></script>
    <script>
        let currentType = '2A2';
        let currentDimension = '2D';
        let t1 = 0;
        let t2 = 0;

        const scene = new THREE.Scene();
        const camera = new THREE.OrthographicCamera(-5, 5, 5, -5, 0.1, 1000);
        const renderer = new THREE.WebGLRenderer({ antialias: true });
        renderer.setSize(window.innerWidth - 250, window.innerHeight);
        document.getElementById('visualization-container').appendChild(renderer.domElement);

        const ambientLight = new THREE.AmbientLight(0xffffff, 0.5);
        scene.add(ambientLight);

        const directionalLight = new THREE.DirectionalLight(0xffffff, 0.8);
        directionalLight.position.set(1, 1, 1).normalize();
        scene.add(directionalLight);

        let controls;
        let object;

        function init() {
            updateVisualization();
            document.querySelectorAll('.button-container button').forEach(button => {
                button.addEventListener('click', () => {
                    document.querySelectorAll('.button-container button').forEach(btn => btn.classList.remove('active'));
                    button.classList.add('active');
                    currentType = button.id.replace('btn-', '');
                    currentDimension = currentType.includes('3') ? '3D' : '2D';
                    updateCameraAndControls();
                    updateVisualization();
                });
            });

            const timePad = document.getElementById('time-pad');
            const timeHandle = document.getElementById('time-handle');

            timePad.addEventListener('mousedown', startDrag);
            timePad.addEventListener('mousemove', moveDrag);
            timePad.addEventListener('mouseup', endDrag);
            timePad.addEventListener('mouseleave', endDrag);
            timePad.addEventListener('click', jumpToPosition);

            function startDrag(e) {
                timeHandle.classList.add('dragging');
                moveDrag(e);
            }

            function moveDrag(e) {
                if (!timeHandle.classList.contains('dragging')) return;
                const rect = timePad.getBoundingClientRect();
                let left = e.clientX - rect.left;
                let top = e.clientY - rect.top;
                left = Math.max(0, Math.min(left, rect.width));
                top = Math.max(0, Math.min(top, rect.height));
                timeHandle.style.left = `${left}px`;
                timeHandle.style.top = `${top}px`;
                updateTValues(left, top);
                updateVisualization();
            }

            function endDrag() {
                timeHandle.classList.remove('dragging');
            }

            function jumpToPosition(e) {
                const rect = timePad.getBoundingClientRect();
                let left = e.clientX - rect.left;
                let top = e.clientY - rect.top;
                left = Math.max(0, Math.min(left, rect.width));
                top = Math.max(0, Math.min(top, rect.height));
                timeHandle.style.left = `${left}px`;
                timeHandle.style.top = `${top}px`;
                updateTValues(left, top);
                updateVisualization();
            }

            function updateTValues(left, top) {
                t1 = (left / timePad.offsetWidth) * 2 - 1;
                t2 = -(top / timePad.offsetHeight) * 2 + 1;
            }

            function animate() {
                requestAnimationFrame(animate);
                renderer.render(scene, camera);
            }

            animate();

            window.addEventListener('resize', onWindowResize, false);
        }

        function onWindowResize() {
            const width = window.innerWidth - 250;
            const height = window.innerHeight;
            if (currentDimension === '2D') {
                camera.left = -width / 100;
                camera.right = width / 100;
                camera.top = height / 100;
                camera.bottom = -height / 100;
            } else {
                camera.aspect = width / height;
            }
            camera.updateProjectionMatrix();
            renderer.setSize(width, height);
        }

        function updateCameraAndControls() {
            if (currentDimension === '2D') {
                if (controls) {
                    controls.dispose();
                    controls = undefined;
                }
                camera.setFromObject(new THREE.OrthographicCamera(-5, 5, 5, -5, 0.1, 1000));
            } else {
                camera.setFromObject(new THREE.PerspectiveCamera(75, (window.innerWidth - 250) / window.innerHeight, 0.1, 1000));
                if (!controls) {
                    controls = new THREE.OrbitControls(camera, renderer.domElement);
                    controls.enableDamping = true;
                    controls.dampingFactor = 0.25;
                    controls.enableZoom = true;
                }
            }
            camera.position.z = 10;
            camera.lookAt(0, 0, 0);
        }

        function updateVisualization() {
            if (object) {
                scene.remove(object);
            }

            let geometry, material;
            const T1 = t1 * 1.5;
            const T2 = t2 * 1.5;

            switch (currentType) {
                case '2A1':
                    geometry = new THREE.BufferGeometry();
                    const positions2A1 = [];
                    for (let i = 0; i <= 200; i++) {
                        const u = -2 + (i / 200) * 4;
                        const x = u + T1;
                        const y = u * u + T2;
                        positions2A1.push(x, y, 0);
                    }
                    geometry.setAttribute('position', new THREE.Float32BufferAttribute(positions2A1, 3));
                    material = new THREE.LineBasicMaterial({ color: 0x4a69bd });
                    object = new THREE.Line(geometry, material);
                    break;
                case '2A2':
                    geometry = new THREE.BufferGeometry();
                    const positions2A2 = [];
                    for (let i = 0; i <= 200; i++) {
                        const u = -1.5 + (i / 200) * 3;
                        const x = 2 * u * u * u + T1;
                        const y = 3 * u * u + T2;
                        positions2A2.push(x, y, 0);
                    }
                    geometry.setAttribute('position', new THREE.Float32BufferAttribute(positions2A2, 3));
                    material = new THREE.LineBasicMaterial({ color: 0x4a69bd });
                    object = new THREE.Line(geometry, material);
                    break;
                case '2B2':
                    geometry = new THREE.BufferGeometry();
                    const positions2B2 = [];
                    for (let i = 0; i <= 200; i++) {
                        const u = -2 + (i / 200) * 4;
                        const x = u * u + T1;
                        const y = u * u * u * u + T2;
                        positions2B2.push(x, y, 0);
                    }
                    geometry.setAttribute('position', new THREE.Float32BufferAttribute(positions2B2, 3));
                    material = new THREE.LineBasicMaterial({ color: 0x4a69bd });
                    object = new THREE.Line(geometry, material);
                    break;
                case '3A1p':
                    geometry = new THREE.ParametricGeometry((u, v) => {
                        const x = u + T1;
                        const y = v + T2;
                        const z = 0.3 * (u * u + v * v) + T1 * u + T2 * v;
                        return new THREE.Vector3(x, y, z);
                    }, 50, 50);
                    material = new THREE.MeshPhongMaterial({ color: 0xffc34d, side: THREE.DoubleSide });
                    object = new THREE.Mesh(geometry, material);
                    break;
                case '3A1m':
                    geometry = new THREE.ParametricGeometry((u, v) => {
                        const x = u + T1;
                        const y = v + T2;
                        const z = -0.3 * (u * u + v * v) + T1 * u + T2 * v;
                        return new THREE.Vector3(x, y, z);
                    }, 50, 50);
                    material = new THREE.MeshPhongMaterial({ color: 0xffc34d, side: THREE.DoubleSide });
                    object = new THREE.Mesh(geometry, material);
                    break;
                case '3A2':
                    geometry = new THREE.ParametricGeometry((u, v) => {
                        const x = 2 * u * u * u + T1;
                        const y = 3 * u * u + T2;
                        const z = v;
                        return new THREE.Vector3(x, y, z);
                    }, 50, 50);
                    material = new THREE.MeshPhongMaterial({ color: 0xffc34d, side: THREE.DoubleSide });
                    object = new THREE.Mesh(geometry, material);
                    break;
                case '3A3':
                    geometry = new THREE.ParametricGeometry((u, v) => {
                        const x = 3 * u * u * u * u + u * u * v;
                        const y = -4 * u * u * u - 2 * u * v + T1 * 2;
                        const z = v + T2 * 2;
                        return new THREE.Vector3(x, y, z);
                    }, 50, 50);
                    material = new THREE.MeshPhongMaterial({ color: 0xffc34d, side: THREE.DoubleSide });
                    object = new THREE.Mesh(geometry, material);
                    break;
                case '3B2p':
                    geometry = new THREE.ParametricGeometry((u, v) => {
                        const x = u + T1;
                        const y = v + T2;
                        const z = (u * u + T1) * (u * u + T1) + v * v + T2;
                        return new THREE.Vector3(x, y, z);
                    }, 50, 50);
                    material = new THREE.MeshPhongMaterial({ color: 0xffc34d, side: THREE.DoubleSide });
                    object = new THREE.Mesh(geometry, material);
                    break;
                case '3B2m':
                    geometry = new THREE.ParametricGeometry((u, v) => {
                        const x = u + T1;
                        const y = v + T2;
                        const z = -(u * u * u * u + 2 * T1 * u * u + v * v - T2);
                        return new THREE.Vector3(x, y, z);
                    }, 50, 50);
                    material = new THREE.MeshPhongMaterial({ color: 0xffc34d, side: THREE.DoubleSide });
                    object = new THREE.Mesh(geometry, material);
                    break;
                case '3B3':
                    geometry = new THREE.ParametricGeometry((u, v) => {
                        const x = u + T1;
                        const y = v + T2;
                        const z = u * u * u + T1 * u + v * v + T2;
                        return new THREE.Vector3(x, y, z);
                    }, 50, 50);
                    material = new THREE.MeshPhongMaterial({ color: 0xffc34d, side: THREE.DoubleSide });
                    object = new THREE.Mesh(geometry, material);
                    break;
                case '3C3p':
                    geometry = new THREE.ParametricGeometry((u, v) => {
                        const x = u + T1;
                        const y = v + T2;
                        const z = u * u * u - 3 * u * v * v + 2 * T1 * (u * u + v * v) + 4 * T2 * u;
                        return new THREE.Vector3(x, y, z);
                    }, 50, 50);
                    material = new THREE.MeshPhongMaterial({ color: 0xffc34d, side: THREE.DoubleSide });
                    object = new THREE.Mesh(geometry, material);
                    break;
                case '3C3m':
                    geometry = new THREE.ParametricGeometry((u, v) => {
                        const x = u + T1;
                        const y = v + T2;
                        const z = u * u * u + v * v * v + 4 * T1 * u + 4 * T2 * v;
                        return new THREE.Vector3(x, y, z);
                    }, 50, 50);
                    material = new THREE.MeshPhongMaterial({ color: 0xffc34d, side: THREE.DoubleSide });
                    object = new THREE.Mesh(geometry, material);
                    break;
            }

            scene.add(object);
        }

        document.getElementById('btn-2A2').classList.add('active');
        init();
    </script>
</body>
