            </div>
            <div>
                <label for="slider-dh1">\(\Delta H_1\)</label>
                <input type="range" id="slider-dh1" min="500" max="5000" step="50" value="1000">
                <input type="number" id="input-dh1" value="1000">
            </div>
            <div>
                <label for="slider-dh2">\(\Delta H_2\)</label>
                <input type="range" id="slider-dh2" min="500" max="5000" step="50" value="3000">
                <input type="number" id="input-dh2" value="3000">
            </div>
        </div>
    </div>
    <div id="plot-area">
        <canvas id="plot-canvas"></canvas>
