    <script>
        // Synchronize slider and number input
        const syncInputs = (sliderId, inputId) => {
            const slider = document.getElementById(sliderId);
            const input = document.getElementById(inputId);
            slider.addEventListener('input', () => {
                input.value = slider.value;
                updatePlot();
            });
            input.addEventListener('input', () => {
                slider.value = input.value;
                updatePlot();
            });
        };

        syncInputs('slider-feed-rate', 'input-feed-rate');
        syncInputs('slider-ua', 'input-ua');
        syncInputs('slider-dh1', 'input-dh1');
        syncInputs('slider-dh2', 'input-dh2');

        // Feed cycler logic
        let feedConfig = "feed A to B";
        document.getElementById('cycler-feed-label').textContent = feedConfig;

        document.getElementById('btn-feed-down').addEventListener('click', () => {
            feedConfig = feedConfig === "feed A to B" ? "feed B to A" : "feed A to B";
            document.getElementById('cycler-feed-label').textContent = feedConfig;
            updatePlot();
        });

        document.getElementById('btn-feed-up').addEventListener('click', () => {
            feedConfig = feedConfig === "feed A to B" ? "feed B to A" : "feed A to B";
            document.getElementById('cycler-feed-label').textContent = feedConfig;
            updatePlot();
        });

        // Plotting logic
        let plotType = "semibatch";
        document.getElementById('select-plot').addEventListener('change', () => {
            plotType = document.getElementById('select-plot').value;
            updatePlot();
        });

        const R = 8.314; // Gas Constant J/mol/K
        const V = 100; // Reactor Volume L
        const T_initial = 330; // Initial Temperature K
        const T_coolant = 330; // Coolant Temperature K
        const Cp_total = 150000; // Total Heat Capacity J/K
        const k1_0 = 3e4; // Rate constant L²/(mol²·h)
        const E1 = 30000; // Activation energy J/mol
        const k2_0 = 1e5; // Rate constant L/(mol·h)
        const E2 = 40000; // Activation energy J/mol

        const dt = 0.01; // Time step h
        const t_max = 2.0; // Maximum time h

        const rk4 = (f, Y0, t0, tf, dt) => {
            const n = Math.ceil((tf - t0) / dt);
            let Y = [...Y0];
            let t = t0;
            let results = [Y];
            for (let i = 0; i < n; i++) {
                const k1 = f(t, Y);
                const k2 = f(t + dt / 2, Y.map((y, j) => y + k1[j] * dt / 2));
                const k3 = f(t + dt / 2, Y.map((y, j) => y + k2[j] * dt / 2));
                const k4 = f(t + dt, Y.map((y, j) => y + k3[j] * dt));
                Y = Y.map((y, j) => y + (k1[j] + 2 * k2[j] + 2 * k3[j] + k4[j]) * dt / 6);
                t += dt;
                results.push([...Y]);
            }
            return results;
        };

        const odesBatch = (t, Y) => {
            const [N_a, N_b, N_d, N_u, T] = Y;
            const k1 = k1_0 * Math.exp(-E1 / (R * T));
            const k2 = k2_0 * Math.exp(-E2 / (R * T));
            const r1 = k1 * (N_a / V) * Math.pow(N_b / V, 2);
            const r2 = k2 * (N_a / V) * (N_b / V);
            const dN_a_dt = -(r1 + r2) * V;
            const dN_b_dt = -(r1 + r2) * V;
            const dN_d_dt = r1 * V;
            const dN_u_dt = r2 * V;
            const dT_dt = (document.getElementById('slider-ua').value * (T_coolant - T) + (-document.getElementById('slider-dh1').value * r1 - document.getElementById('slider-dh2').value * r2) * V) / Cp_total;
            return [dN_a_dt, dN_b_dt, dN_d_dt, dN_u_dt, dT_dt];
        };

        const odesSemibatch = (t, Y) => {
            const [N_a, N_b, N_d, N_u, T] = Y;
            const f_r = parseFloat(document.getElementById('slider-feed-rate').value);
            const t_f = 100 / f_r;
            const k1 = k1_0 * Math.exp(-E1 / (R * T));
            const k2 = k2_0 * Math.exp(-E2 / (R * T));
            const r1 = k1 * (N_a / V) * Math.pow(N_b / V, 2);
            const r2 = k2 * (N_a / V) * (N_b / V);
            let dN_a_dt, dN_b_dt;
            if (feedConfig === "feed A to B") {
                const F_A0 = (t < t_f) ? f_r : 0;
                dN_a_dt = F_A0 - (r1 + r2) * V;
                dN_b_dt = -(r1 + r2) * V;
            } else {
                const F_B0 = (t < t_f) ? f_r : 0;
                dN_a_dt = -(r1 + r2) * V;
                dN_b_dt = F_B0 - (r1 + r2) * V;
            }
            const dN_d_dt = r1 * V;
            const dN_u_dt = r2 * V;
            const dT_dt = (document.getElementById('slider-ua').value * (T_coolant - T) + (-document.getElementById('slider-dh1').value * r1 - document.getElementById('slider-dh2').value * r2) * V) / Cp_total;
            return [dN_a_dt, dN_b_dt, dN_d_dt, dN_u_dt, dT_dt];
        };

        let plotData = [];

        const updatePlot = () => {
            const f_r = parseFloat(document.getElementById('slider-feed-rate').value);
            const t_f = 100 / f_r;

            if (plotType === "batch") {
                const Y0 = [100, 100, 0, 0, T_initial];
                plotData = rk4(odesBatch, Y0, 0, t_max, dt);
            } else if (plotType === "semibatch") {
                const Y0 = feedConfig === "feed A to B" ? [0, 100, 0, 0, T_initial] : [100, 0, 0, 0, T_initial];
                plotData = rk4(odesSemibatch, Y0, 0, t_max, dt);
            } else if (plotType === "selectivity") {
                const Y0_batch = [100, 100, 0, 0, T_initial];
                const batchData = rk4(odesBatch, Y0_batch, 0, t_max, dt);
                const Y0_semi = feedConfig === "feed A to B" ? [0, 100, 0, 0, T_initial] : [100, 0, 0, 0, T_initial];
                const semiData = rk4(odesSemibatch, Y0_semi, 0, t_max, dt);

                const S_batch = batchData.map(([_, __, N_d, N_u]) => N_u > 0 ? N_d / N_u : 0);
                const S_semi = semiData.map(([_, __, N_d, N_u]) => N_u > 0 ? N_d / N_u : 0);
                plotData = S_semi.map((S, i) => S_batch[i] > 0 ? S / S_batch[i] : 0).map((S_ratio, i) => [i * dt, S_ratio]).filter(([t]) => t > 0);
            }

            drawPlot();
        };

        const drawPlot = () => {
            const plotCanvas = document.getElementById('plot-canvas');
            const ctx = plotCanvas.getContext('2d');
            const width = plotCanvas.width;
            const height = plotCanvas.height;

            // Clear canvas
            ctx.clearRect(0, 0, width, height);
            ctx.fillStyle = '#F0F0F0';
            ctx.fillRect(0, 0, width, height);

            // Axes and grid
            const margin = 50;
            const xAxisLength = width - 2 * margin;
            const yAxisLength = height - 2 * margin;

            ctx.strokeStyle = '#000';
            ctx.lineWidth = 1;

            // X-axis
            ctx.beginPath();
            ctx.moveTo(margin, height - margin);
            ctx.lineTo(width - margin, height - margin);
            ctx.stroke();

            // Y-axis
            ctx.beginPath();
            ctx.moveTo(margin, margin);
            ctx.lineTo(margin, height - margin);
            ctx.stroke();

            // X-axis ticks
            for (let i = 0; i <= 4; i++) {
                const x = margin + i * (xAxisLength / 4);
                ctx.beginPath();
                ctx.moveTo(x, height - margin);
                ctx.lineTo(x, height - margin + 5);
                ctx.stroke();
                ctx.fillText(i * 0.5, x - 5, height - margin + 20);
            }

            // Y-axis ticks
            if (plotType !== "selectivity") {
                for (let i = 0; i <= 5; i++) {
                    const y = margin + i * (yAxisLength / 5);
                    ctx.beginPath();
                    ctx.moveTo(margin, y);
                    ctx.lineTo(margin - 5, y);
                    ctx.stroke();
                    ctx.fillText(i * 20, margin - 30, y + 5);
                }
            } else {
                const minS_ratio = Math.min(...plotData.map(([_, S_ratio]) => S_ratio));
                const maxS_ratio = Math.max(...plotData.map(([_, S_ratio]) => S_ratio));
                const range = maxS_ratio - minS_ratio;
                const padding = range * 0.1;
                const yMin = minS_ratio - padding;
                const yMax = maxS_ratio + padding;

                for (let i = 0; i <= 5; i++) {
                    const y = margin + i * (yAxisLength / 5);
                    ctx.beginPath();
                    ctx.moveTo(margin, y);
                    ctx.lineTo(margin - 5, y);
                    ctx.stroke();
                    ctx.fillText(((yMax - yMin) * (i / 5) + yMin).toFixed(2), margin - 30, y + 5);
                }
            }

            // Grid lines
            ctx.strokeStyle = '#ccc';
            ctx.lineWidth = 1;

            // Vertical grid lines
            for (let i = 0; i <= 4; i++) {
                const x = margin + i * (xAxisLength / 4);
                ctx.beginPath();
                ctx.moveTo(x, margin);
                ctx.lineTo(x, height - margin);
                ctx.stroke();
            }

            // Horizontal grid lines
            if (plotType !== "selectivity") {
                for (let i = 0; i <= 5; i++) {
                    const y = margin + i * (yAxisLength / 5);
                    ctx.beginPath();
                    ctx.moveTo(margin, y);
                    ctx.lineTo(width - margin, y);
                    ctx.stroke();
                }
            } else {
                for (let i = 0; i <= 5; i++) {
                    const y = margin + i * (yAxisLength / 5);
                    ctx.beginPath();
                    ctx.moveTo(margin, y);
                    ctx.lineTo(width - margin, y);
                    ctx.stroke();
                }
            }

            // Data curves
            ctx.strokeStyle = '#2ca02c';
            ctx.lineWidth = 2;
            ctx.setLineDash([5, 5]);
            ctx.beginPath();
            plotData.forEach(([t, N_a], i) => {
                const x = margin + (t / t_max) * xAxisLength;
                const y = height - margin - ((N_a / 100) * yAxisLength);
                if (i === 0) ctx.moveTo(x, y);
                else ctx.lineTo(x, y);
            });
            ctx.stroke();
            ctx.setLineDash([]);

            ctx.strokeStyle = '#000000';
            ctx.beginPath();
            plotData.forEach(([t, _, N_b], i) => {
                const x = margin + (t / t_max) * xAxisLength;
                const y = height - margin - ((N_b / 100) * yAxisLength);
                if (i === 0) ctx.moveTo(x, y);
                else ctx.lineTo(x, y);
            });
            ctx.stroke();
            ctx.setLineDash([]);

            ctx.strokeStyle = '#1f77b4';
            ctx.lineWidth = 2;
            ctx.beginPath();
            plotData.forEach(([t, _, __, N_d], i) => {
                const x = margin + (t / t_max) * xAxisLength;
                const y = height - margin - ((N_d / 100) * yAxisLength);
                if (i === 0) ctx.moveTo(x, y);
                else ctx.lineTo(x, y);
            });
            ctx.stroke();

            ctx.strokeStyle = '#d62728';
            ctx.lineWidth = 2;
            ctx.beginPath();
            plotData.forEach(([t, _, __, N_u], i) => {
                const x = margin + (t / t_max) * xAxisLength;
                const y = height - margin - ((N_u / 100) * yAxisLength);
                if (i === 0) ctx.moveTo(x, y);
                else ctx.lineTo(x, y);
            });
            ctx.stroke();

            if (plotType === "selectivity") {
                ctx.strokeStyle = '#800080';
                ctx.lineWidth = 2;
                ctx.beginPath();
                plotData.forEach(([t, S_ratio], i) => {
                    const x = margin + (t / t_max) * xAxisLength;
                    const y = height - margin - ((S_ratio - yMin) / (yMax - yMin)) * yAxisLength;
                    if (i === 0) ctx.moveTo(x, y);
                    else ctx.lineTo(x, y);
                });
                ctx.stroke();
            }

            // Legend
            const legendLabel = document.getElementById('legend-label');
            legendLabel.innerHTML = '';
            if (plotType !== "selectivity") {
                legendLabel.innerHTML = `
                    <span style="color:#2ca02c">N<sub>a</sub></span> &nbsp;
                    <span style="color:#000000">N<sub>b</sub></span> &nbsp;
                    <span style="color:#1f77b4">N<sub>d</sub></span> &nbsp;
                    <span style="color:#d62728">N<sub>u</sub></span>
                `;
            } else {
                legendLabel.innerHTML = `
                    <span style="color:#800080">semibatch S<sub>d/u</sub> / batch S<sub>d/u</sub></span>
                `;
            }
        };

        // Initial plot
        updatePlot();
    </script>
</body>
</html>
