            display: block;
            margin-top: 5px;
        }
    </style>
    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.js"></script>
</head>
<body>
    <div id="main-container">
        <div id="control-panel">
            <h4>Tree type</h4>
            <div id="tree-type-buttons">
                <button id="btn-rt" class="active">Random Tree</button>
                <button id="btn-rrt">RRT</button>
                <button id="btn-rrt-star">RRT*</button>
            </div>
            <h4>obstacle type</h4>
            <select id="select-obstacle">
                <option value="narrow-passage" selected>narrow passage</option>
                <option value="empty">empty</option>
                <option value="random-rects">random rects</option>
            </select>
            <h4>number of nodes to add:</h4>
            <div id="add-nodes-buttons">
                <button id="btn-add-1">1</button>
                <button id="btn-add-10">10</button>
                <button id="btn-add-100">100</button>
                <button id="btn-add-200">200</button>
                <button id="btn-add-500">500</button>
            </div>
            <h4>exploration bias</h4>
            <input type="range" id="slider-bias" min="0" max="1" step="0.05" value="0">
            <span id="bias-value-label">0</span>
            <h4>goal radius</h4>
            <input type="range" id="slider-radius" min="1" max="10" step="0.1" value="1">
            <span id="radius-value-label">1</span>
        </div>
        <div id="canvas-container">
            <p id="status-text">1 node, goal not yet reached</p>
            <button id="btn-reset">+</button>
        </div>
    </div>

    <script>
        let treeType = 'Random Tree';
        let obstacleType = 'narrow-passage';
        let explorationBias = 0;
        let goalRadius = 1;
        let nodes = [];
        let startNode = { x: 50, y: 250 };
        let goalNode = { x: 450, y: 450 };
        let goalReached = false;
        let path = [];
        let pathLength = 0;

        function setup() {
            const canvas = createCanvas(500, 500);
            canvas.parent('canvas-container');
            nodes.push(startNode);
            drawObstacles();
            drawNodes();
            drawGoal();
        }

        function draw() {
            background(255);
            drawObstacles();
            drawNodes();
            drawGoal();
            if (goalReached) {
                stroke('green');
                strokeWeight(3);
                beginShape();
                for (let node of path) {
                    vertex(node.x, node.y);
                }
                endShape();
            }
            fill(0);
            noStroke();
            textSize(18);
            textAlign(CENTER);
            text(`${nodes.length} nodes, ${goalReached ? `path length ${pathLength.toFixed(2)}` : 'goal not yet reached'}`, width / 2, 20);
        }

        function drawObstacles() {
            stroke('blue');
            strokeWeight(8);
            noFill();
            rect(0, 0, width, height);
            if (obstacleType === 'narrow-passage') {
                rect(150, 0, 200, 100);
                rect(150, 400, 200, 100);
            } else if (obstacleType === 'random-rects') {
                for (let i = 0; i < 10; i++) {
                    rect(random(width), random(height), random(50, 100), random(50, 100));
                }
            }
        }

        function drawNodes() {
            for (let node of nodes) {
                fill('red');
                noStroke();
                circle(node.x, node.y, 5);
            }
            if (nodes.length > 0) {
                fill('green');
                circle(startNode.x, startNode.y, 5);
            }
        }

        function drawGoal() {
            fill(255, 255, 0, 100);
            noStroke();
            circle(goalNode.x, goalNode.y, goalRadius * 2);
            stroke(0);
            line(goalNode.x - goalRadius, goalNode.y, goalNode.x + goalRadius, goalNode.y);
            line(goalNode.x, goalNode.y - goalRadius, goalNode.x, goalNode.y + goalRadius);
        }

        function addNode() {
            let newNode;
            if (treeType === 'Random Tree') {
                newNode = randomNode();
            } else if (treeType === 'RRT') {
                newNode = rrtNode();
            } else if (treeType === 'RRT*') {
                newNode = rrtStarNode();
            }
            nodes.push(newNode);
            checkGoalReached(newNode);
        }

        function randomNode() {
            return { x: random(width), y: random(height) };
        }

        function rrtNode() {
            let randPoint = randomPoint();
            let nearestNode = findNearestNode(randPoint);
            let newNode = interpolate(nearestNode, randPoint);
            newNode.parent = nearestNode;
            return newNode;
        }

        function rrtStarNode() {
            let randPoint = randomPoint();
            let nearestNode = findNearestNode(randPoint);
            let newNode = interpolate(nearestNode, randPoint);
            newNode.parent = nearestNode;
            rewire(newNode);
            return newNode;
        }

        function randomPoint() {
            if (random() < explorationBias) {
                return goalNode;
            } else {
                return { x: random(width), y: random(height) };
            }
        }

        function findNearestNode(point) {
            let nearest = nodes[0];
            let minDist = dist(point.x, point.y, nearest.x, nearest.y);
            for (let node of nodes) {
                let d = dist(point.x, point.y, node.x, node.y);
                if (d < minDist) {
                    minDist = d;
                    nearest = node;
                }
            }
            return nearest;
        }

        function interpolate(from, to) {
            let direction = createVector(to.x - from.x, to.y - from.y);
            direction.setMag(min(dist(from.x, from.y, to.x, to.y), 10));
            return { x: from.x + direction.x, y: from.y + direction.y };
        }

        function checkGoalReached(node) {
            if (dist(node.x, node.y, goalNode.x, goalNode.y) <= goalRadius) {
                if (!goalReached) {
                    goalReached = true;
                    path = tracePath(node);
                    pathLength = calculatePathLength(path);
                } else if (treeType === 'RRT*') {
                    let newPath = tracePath(node);
                    let newPathLength = calculatePathLength(newPath);
                    if (newPathLength < pathLength) {
                        path = newPath;
                        pathLength = newPathLength;
                    }
                }
            }
        }

        function tracePath(node) {
            let path = [];
            while (node) {
                path.unshift(node);
                node = node.parent;
            }
            return path;
        }

        function calculatePathLength(path) {
            let length = 0;
            for (let i = 0; i < path.length - 1; i++) {
                length += dist(path[i].x, path[i].y, path[i + 1].x, path[i + 1].y);
            }
            return length;
        }

        function rewire(newNode) {
            let neighbors = nodes.filter(node => dist(newNode.x, newNode.y, node.x, node.y) <= 10);
            for (let neighbor of neighbors) {
                let costThroughNewNode = dist(newNode.x, newNode.y, neighbor.x, neighbor.y) + dist(startNode.x, startNode.y, newNode.x, newNode.y);
                if (costThroughNewNode < dist(startNode.x, startNode.y, neighbor.x, neighbor.y)) {
                    neighbor.parent = newNode;
                }
            }
        }

        function resetSimulation() {
            nodes = [startNode];
            goalReached = false;
            path = [];
            pathLength = 0;
            draw();
        }

        document.getElementById('btn-rt').addEventListener('click', () => {
            document.querySelectorAll('#tree-type-buttons button').forEach(btn => btn.classList.remove('active'));
            document.getElementById('btn-rt').classList.add('active');
            treeType = 'Random Tree';
            resetSimulation();
        });

        document.getElementById('btn-rrt').addEventListener('click', () => {
            document.querySelectorAll('#tree-type-buttons button').forEach(btn => btn.classList.remove('active'));
            document.getElementById('btn-rrt').classList.add('active');
            treeType = 'RRT';
            resetSimulation();
        });

        document.getElementById('btn-rrt-star').addEventListener('click', () => {
            document.querySelectorAll('#tree-type-buttons button').forEach(btn => btn.classList.remove('active'));
            document.getElementById('btn-rrt-star').classList.add('active');
            treeType = 'RRT*';
            resetSimulation();
        });

        document.getElementById('select-obstacle').addEventListener('change', () => {
            obstacleType = document.getElementById('select-obstacle').value;
            resetSimulation();
        });

        document.getElementById('btn-add-1').addEventListener('click', () => {
            for (let i = 0; i < 1; i++) {
                addNode();
            }
            draw();
        });

        document.getElementById('btn-add-10').addEventListener('click', () => {
            for (let i = 0; i < 10; i++) {
                addNode();
            }
            draw();
        });

        document.getElementById('btn-add-100').addEventListener('click', () => {
            for (let i = 0; i < 100; i++) {
                addNode();
            }
            draw();
        });

        document.getElementById('btn-add-200').addEventListener('click', () => {
            for (let i = 0; i < 200; i++) {
                addNode();
            }
            draw();
        });

        document.getElementById('btn-add-500').addEventListener('click', () => {
            for (let i = 0; i < 500; i++) {
                addNode();
            }
            draw();
        });

        document.getElementById('slider-bias').addEventListener('input', () => {
            explorationBias = parseFloat(document.getElementById('slider-bias').value);
            document.getElementById('bias-value-label').textContent = explorationBias.toFixed(2);
        });

        document.getElementById('slider-radius').addEventListener('input', () => {
            goalRadius = parseFloat(document.getElementById('slider-radius').value);
            document.getElementById('radius-value-label').textContent = goalRadius.toFixed(1);
            checkGoalReached(nodes[nodes.length - 1]);
            draw();
        });

        document.getElementById('btn-reset').addEventListener('click', () => {
            resetSimulation();
        });

        let draggingGoal = false;

        function mousePressed() {
            if (dist(mouseX, mouseY, goalNode.x, goalNode.y) <= goalRadius) {
                draggingGoal = true;
            }
        }

        function mouseDragged() {
            if (draggingGoal) {
                goalNode.x = constrain(mouseX, 0, width);
                goalNode.y = constrain(mouseY, 0, height);
                resetSimulation();
            }
        }

        function mouseReleased() {
            draggingGoal = false;
        }
    </script>
</body>
</html>
