    <script id="MathJax-script" async src="https://cdn.jsdelivr.net/npm/mathjax@3/es5/tex-mml-chtml.js"></script>
</head>
<body>
    <div id="control-panel">
        <div class="control-group">
            <label> \(t\) </label>
            <input type="range" id="slider-t" min="1" max="100" step="1" value="49">
            <input type="number" id="input-t" min="1" max="100" step="1" value="49">
        </div>
        <div class="control-group">
            <label> \(\rho_0\) </label>
            <input type="range" id="slider-rho0" min="0.5" max="3.0" step="0.01" value="1.9">
            <input type="number" id="input-rho0" min="0.5" max="3.0" step="0.01" value="1.9">
        </div>
        <div class="control-group">
            <label> \(\zeta_0\) </label>
            <input type="range" id="slider-zeta0" min="0" max="1.6" step="0.005" value="0.455">
            <input type="number" id="input-zeta0" min="0" max="1.6" step="0.005" value="0.455">
        </div>
        <div class="control-group">
            <label> \(e_0\) </label>
            <input type="range" id="slider-e0" min="2.01" max="5.0" step="0.01" value="2.93">
            <input type="number" id="input-e0" min="2.01" max="5.0" step="0.01" value="2.93">
        </div>
        <div class="control-group">
            <label> \(v\) </label>
            <input type="range" id="slider-v" min="0" max="2.0" step="0.01" value="0">
            <input type="number" id="input-v" min="0" max="2.0" step="0.01" value="0">
        </div>
        <div class="control-group">
            <label> \(\lambda\) </label>
            <input type="range" id="slider-lambda" min="0" max="2.0" step="0.01" value="1.1">
            <input type="number" id="input-lambda" min="0" max="2.0" step="0.01" value="1.1">
        </div>
    </div>
    <div id="visualization-container">
        <canvas id="three-canvas"></canvas>
    </div>
    <script>
        // Initialize Three.js scene
        const scene = new THREE.Scene();
        const camera = new THREE.PerspectiveCamera(60, window.innerWidth / window.innerHeight, 0.1, 1000);
        const renderer = new THREE.WebGLRenderer({ antialias: true });
        renderer.setSize(window.innerWidth, window.innerHeight);
        renderer.setClearColor(0xf0f0f0);
        renderer.setPixelRatio(window.devicePixelRatio);
        document.getElementById('three-canvas').appendChild(renderer.domElement);

        // Camera setup
        camera.position.set(4, 4, 4);
        camera.lookAt(0, 1, 0);

        // Orbit controls
        const controls = new THREE.OrbitControls(camera, renderer.domElement);
        controls.target.set(0, 1, 0);
        controls.update();

        // Lighting
        const ambientLight = new THREE.AmbientLight(0x666666);
        const directionalLight = new THREE.DirectionalLight(0xffffff, 1.0);
        directionalLight.position.set(5, 5, 5);
        scene.add(ambientLight);
        scene.add(directionalLight);

        // Axes helper
        const axesHelper = new THREE.AxesHelper(4);
        scene.add(axesHelper);

        // Potential surface
        const potentialSurfaceGeometry = new THREE.ParametricGeometry((u, v, target) => {
            const rho = 0.4 + u * 2.1;
            const theta = v * 2 * Math.PI;
            const x = rho * Math.cos(theta);
            const ySurf = rho * rho + 1 / (rho * rho);
            const zSurf = -rho * Math.sin(theta);
            target.set(x, zSurf, ySurf);
        }, 100, 100);
        const potentialSurfaceMaterial = new THREE.MeshStandardMaterial({
            color: 0xE69943,
            metalness: 0.4,
            roughness: 0.5,
            side: THREE.DoubleSide
        });
        const potentialSurface = new THREE.Mesh(potentialSurfaceGeometry, potentialSurfaceMaterial);
        scene.add(potentialSurface);

        // Central cylinder
        const cylinderGeometry = new THREE.CylinderGeometry(0.4, 0.4, 2.5, 32);
        const cylinderMaterial = new THREE.MeshStandardMaterial({
            color: 0xcccccc,
            metalness: 0.1,
            roughness: 0.9
        });
        const cylinder = new THREE.Mesh(cylinderGeometry, cylinderMaterial);
        cylinder.position.set(0, 1.25, 0);
        scene.add(cylinder);

        // Function to link sliders and number inputs
        function linkInputElements(sliderId, inputId) {
            const slider = document.getElementById(sliderId);
            const input = document.getElementById(inputId);

            slider.addEventListener('input', () => {
                input.value = slider.value;
                updateVisualization();
            });

            input.addEventListener('input', () => {
                slider.value = input.value;
                updateVisualization();
            });
        }

        // Link all sliders and number inputs
        linkInputElements('slider-t', 'input-t');
        linkInputElements('slider-rho0', 'input-rho0');
        linkInputElements('slider-zeta0', 'input-zeta0');
        linkInputElements('slider-e0', 'input-e0');
        linkInputElements('slider-v', 'input-v');
        linkInputElements('slider-lambda', 'input-lambda');

        // Function to update visualization
        function updateVisualization() {
            const t = parseFloat(document.getElementById('input-t').value);
            const rho0 = parseFloat(document.getElementById('input-rho0').value);
            const zeta0 = parseFloat(document.getElementById('input-zeta0').value);
            const e0 = parseFloat(document.getElementById('input-e0').value);
            const v = parseFloat(document.getElementById('input-v').value);
            const lambda = parseFloat(document.getElementById('input-lambda').value);

            // Remove existing path
            const oldPath = scene.getObjectByName('ion-path');
            if (oldPath) {
                scene.remove(oldPath);
                oldPath.geometry.dispose();
                oldPath.material.dispose();
            }

            // RK4 method to solve ODEs
            const ds = 0.05;
            const steps = Math.floor(t / ds);
            let Y = [rho0, 0, 0]; // Initial conditions [ρ, dρ/ds, φ]
            const points = [];

            for (let i = 0; i <= steps; i++) {
                const k1 = [
                    Y[1],
                    -(2 * Y[0] - 2 / (Y[0] ** 3)) - 4 * (lambda ** 2) * Y[0] + 4 * e0 / (Y[0] ** 3),
                    2 * (e0 / (Y[0] ** 2) - lambda)
                ];
                const k2 = [
                    Y[1] + ds * 0.5 * k1[0],
                    -(2 * (Y[0] + ds * 0.5 * k1[0]) - 2 / ((Y[0] + ds * 0.5 * k1[0]) ** 3)) - 4 * (lambda ** 2) * (Y[0] + ds * 0.5 * k1[0]) + 4 * e0 / ((Y[0] + ds * 0.5 * k1[0]) ** 3),
                    2 * (e0 / ((Y[0] + ds * 0.5 * k1[0]) ** 2) - lambda)
                ];
                const k3 = [
                    Y[1] + ds * 0.5 * k2[0],
                    -(2 * (Y[0] + ds * 0.5 * k2[0]) - 2 / ((Y[0] + ds * 0.5 * k2[0]) ** 3)) - 4 * (lambda ** 2) * (Y[0] + ds * 0.5 * k2[0]) + 4 * e0 / ((Y[0] + ds * 0.5 * k2[0]) ** 3),
                    2 * (e0 / ((Y[0] + ds * 0.5 * k2[0]) ** 2) - lambda)
                ];
                const k4 = [
                    Y[1] + ds * k3[0],
                    -(2 * (Y[0] + ds * k3[0]) - 2 / ((Y[0] + ds * k3[0]) ** 3)) - 4 * (lambda ** 2) * (Y[0] + ds * k3[0]) + 4 * e0 / ((Y[0] + ds * k3[0]) ** 3),
                    2 * (e0 / ((Y[0] + ds * k3[0]) ** 2) - lambda)
                ];

                Y[0] += ds * (k1[0] + 2 * k2[0] + 2 * k3[0] + k4[0]) / 6;
                Y[1] += ds * (k1[1] + 2 * k2[1] + 2 * k3[1] + k4[1]) / 6;
                Y[2] += ds * (k1[2] + 2 * k2[2] + 2 * k3[2] + k4[2]) / 6;

                const uRho = Y[0] ** 2 + 1 / (Y[0] ** 2);
                const sqrtArg = Math.max(0, e0 - uRho);
                const z = Math.sqrt(sqrtArg) * Math.sin(Math.sqrt(2) * v * i * ds + zeta0);
                const x = Y[0] * Math.cos(Y[2]);
                const y = Y[0] * Math.sin(Y[2]);

                points.push(new THREE.Vector3(x, z, -y));
            }

            // Create path
            const pathGeometry = new THREE.CatmullRomCurve3(points).getPoints(points.length);
            const tubeGeometry = new THREE.TubeGeometry(new THREE.CatmullRomCurve3(pathGeometry), 512, 0.04, 8, false);
            const tubeMaterial = new THREE.MeshBasicMaterial({ color: 0x61AEEF });
            const tubeMesh = new THREE.Mesh(tubeGeometry, tubeMaterial);
            tubeMesh.name = 'ion-path';
            scene.add(tubeMesh);
        }

        // Initial visualization update
        updateVisualization();

        // Animation loop
        function animate() {
            requestAnimationFrame(animate);
            controls.update();
            renderer.render(scene, camera);
        }
        animate();

        // Handle window resize
        window.addEventListener('resize', () => {
            camera.aspect = window.innerWidth / window.innerHeight;
            camera.updateProjectionMatrix();
            renderer.setSize(window.innerWidth, window.innerHeight);
        });

        // Render MathJax
        MathJax.typeset();
    </script>
</body>
</html>
