<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Hydrogen Molecular Orbital Visualization</title>
    <script src="https://cdn.plot.ly/plotly-latest.min.js"></script>
    <style>
        #app-container {
            display: flex;
            flex-direction: column;
            align-items: center;
            padding: 20px;
        }
        #control-panel {
            margin-bottom: 20px;
        }
        #density-plots-container {
            display: flex;
            justify-content: space-around;
            width: 100%;
        }
        #bonding-plot-container, #antibonding-plot-container {
            width: 45%;
        }
    </style>
</head>
<body>
    <div id="app-container">
        <!-- Control Panel Section -->
        <div id="control-panel">
            <label for="slider-r">R (Å)</label>
            <input type="range" id="slider-r" min="0.001" max="4.0" step="0.001" value="0.001">
            <output id="slider-r-value">0.001</output>
        </div>

        <!-- Potential Energy Plot Section -->
        <div id="potential-energy-plot-container"></div>

        <!-- Electron Density Plots Section -->
        <div id="density-plots-container">
            <div id="bonding-plot-container"></div>
            <div id="antibonding-plot-container"></div>
        </div>
    </div>

    <script>
        // Constants
        const bohrToAngstrom = 0.529177;

        // Function to calculate potential energies
        function calculatePotentialEnergies() {
            const R_values = [];
            const E_b_values = [];
            const E_a_values = [];
            for (let R = 0.2; R <= 15; R += 0.1) {
                const S_ab = (1 + R + Math.pow(R, 2) / 3) * Math.exp(-R);
                const J = (1 / R) - (1 + 1 / R) * Math.exp(-2 * R);
                const K = (1 + R) * Math.exp(-R);
                const E_b = (J + K) / (1 + S_ab);
                const E_a = (J - K) / (1 - S_ab);
                R_values.push(R * bohrToAngstrom);
                E_b_values.push(E_b);
                E_a_values.push(E_a);
            }
            return { R_values, E_b_values, E_a_values };
        }

        // Function to update electron density plots
        function updateDensityPlots(R_au) {
            const x = Array.from({ length: 60 }, (_, i) => -3 + (i * (6 / 59)));
            const y = Array.from({ length: 60 }, (_, i) => -3 + (i * (6 / 59)));
            const z_bonding_data = Array.from({ length: 60 }, () => Array(60).fill(0));
            const z_antibonding_data = Array.from({ length: 60 }, () => Array(60).fill(0));

            for (let i = 0; i < 60; i++) {
                for (let j = 0; j < 60; j++) {
                    const dist_A = Math.sqrt(Math.pow(x[i] + R_au / 2, 2) + Math.pow(y[j], 2));
                    const dist_B = Math.sqrt(Math.pow(x[i] - R_au / 2, 2) + Math.pow(y[j], 2));
                    const phi_A = Math.exp(-dist_A);
                    const phi_B = Math.exp(-dist_B);
                    const S_ab = (1 + R_au + Math.pow(R_au, 2) / 3) * Math.exp(-R_au);
                    const N_b = 1 / Math.sqrt(2 * (1 + S_ab));
                    const N_a = 1 / Math.sqrt(2 * (1 - S_ab));
                    const psi_b = N_b * (phi_A + phi_B);
                    const psi_a = N_a * (phi_A - phi_B);
                    z_bonding_data[i][j] = Math.pow(psi_b, 2);
                    z_antibonding_data[i][j] = Math.pow(psi_a, 2);
                }
            }

            const bondingData = [{
                z: z_bonding_data,
                x: x,
                y: y,
                type: 'surface',
                colorscale: 'YlOrBr'
            }];

            const antibondingData = [{
                z: z_antibonding_data,
                x: x,
                y: y,
                type: 'surface',
                colorscale: 'YlOrBr'
            }];

            const layout = {
                scene: {
                    xaxis: { range: [-3, 3], visible: false },
                    yaxis: { range: [-3, 3], visible: false },
                    zaxis: { range: [0, 0.5], visible: false },
                    aspectmode: 'cube'
                },
                showlegend: false,
                autosize: true
            };

            Plotly.newPlot('bonding-plot-container', bondingData, layout);
            Plotly.newPlot('antibonding-plot-container', antibondingData, layout);
        }

        // Initialize potential energy plot
        const { R_values, E_b_values, E_a_values } = calculatePotentialEnergies();
        const potentialEnergyData = [
            { x: R_values, y: E_b_values, mode: 'lines', name: 'bonding PE', line: { color: 'blue' } },
            { x: R_values, y: E_a_values, mode: 'lines', name: 'antibonding PE', line: { color: 'red' } }
        ];
        const potentialEnergyLayout = {
            title: 'potential energy',
            xaxis: { title: 'R', range: [0, 8] },
            yaxis: { range: [-0.6, 0.4] },
            showlegend: true
        };
        Plotly.newPlot('potential-energy-plot-container', potentialEnergyData, potentialEnergyLayout);

        // Slider interaction
        const sliderR = document.getElementById('slider-r');
        const sliderRValue = document.getElementById('slider-r-value');

        sliderR.addEventListener('input', function() {
            const R_angstroms = parseFloat(sliderR.value);
            sliderRValue.textContent = R_angstroms.toFixed(3);
            const R_au = R_angstroms / bohrToAngstrom;
            updateDensityPlots(R_au);
        });

        // Initial update of density plots
        updateDensityPlots(parseFloat(sliderR.value) / bohrToAngstrom);
    </script>
</body>
</html>
